    opacity:.5;
  }

  .switchmenu button:hover {
    transition:All 1.6116s ease;    
    transform: rotate(0deg) scale(1.05) skew(0deg) translate(0px);   
    background:url(http://i.imgur.com/rJmTxtU.png) !important;     
  }

  .switchselect { 
    opacity:0;       
    transform: translate(280px);    
    position:relative;     
    left:0;     
    top:115px;     
  } 
  
  /********************
  * MegaEvo input fix *
  ********************/
  
  .movemenu label, .movemenu label:hover {
    background: none;
    width: 150px;
    border: none;
    right: 0px;
    top: 60px;
  }
  
  .movemenu label::after {
    color: white;
    text-shadow: 1px 1px purple;
  }
  
  
  /***************************
  * Tooltip (popup info) CSS *
  ***************************/
  
  #tooltipwrapper {
    position: absolute;
    top: 400px;
    left: 100px;
    text-align: left;
    color: #311A44;
    text-shadow: 1px 1px 1px #DF4397;
  }

  #tooltipwrapper .tooltipinner {
    position: relative;
  }

  #tooltipwrapper .tooltip {
    position: absolute;
    bottom: 0;
    left: 0;
    width: 300px;
    border: 1px solid #161616;
    background: url("http://i.imgur.com/Tb4Q3eq.jpg?1") bottom right no-repeat !important;
    background-color: #55D4FF !important;
    border-radius: 2px;
    z-index: 9999;
  }

  #tooltipwrapper .tooltip h2 {
    padding: 2px 4px;
    margin: 0;
    border-bottom: 1px solid #161616;
    font-size: 10pt;
  }

  #tooltipwrapper .tooltip h2 small {
    font-weight: normal;
  }

  #tooltipwrapper .tooltip p {
    padding: 2px 4px;
    margin: 0;
    font-size: 8pt;
  }

  #tooltipwrapper .tooltip p.section {
    border-top: 1px solid #161616;
  }
  
  /*****************
  * Tournament CSS *
  *****************/
  
  .tournament-title {
    background: rgba(0, 0, 0, 0.5);
    color: white;
    text-shadow: 1px 1px purple;
  }
  
  .tournament-status, .tournament-toggle {
    background: none;
    border: none;
    color: white;
    text-shadow: 1px 1px purple;
  }
  
  .tournament-box {
    background: rgba(37, 22, 41, 0.8);
    border: none;
    box-shadow: 0px 0px 8px purple inset;
  }
  
  .tournament-bracket-tree-node > rect {
    fill: rgba(0, 0, 0, 0.7);
    stroke: black;
    color: white;
  }
  
  .tournament-bracket-tree-node-win > rect {
    fill: rgba(33, 128, 50, 0.7);
    stroke: #207830 !important;
    color: white;
  }
  
  .tournament-bracket-tree-node-draw > rect {
    fill: rgba(184, 124, 55, 0.5);
    stroke: #99672C !important;
    color: white;
  }
  
  .tournament-bracket-tree-node-loss > rect {
    fill: rgba(166, 70, 70, 0.7);
    stroke: #943D3D !important;
    color: black;
  }
  
  .tournament-bracket-tree-node > text > a {
    fill: #85C2D6;
  }
  
  .tournament-bracket-tree-node > text > a:hover {
    fill: #78B1C4;
  }
  
  .tournament-bracket-tree-node > text {
    fill: #999;
    text-shadow: 0px 0px 2px black;
  }
}
