css:
@-moz-document url-prefix('https://i2.paprika-worldwide.com') {
body{
 font-size: 12px;
}

#logo {
  display:none !important;
}
form#logon{
  margin-top: auto;
  margin-right: auto;
  float: initial;
  position: absolute;
  left: 50%;
  top: 40%;
  transform: translate(-50%, -50%);
}
body.logon div#Inner{
  background-image:	none !important;
}
div#Body div#Inner{
  width: 99% !important;
  min-width:99%;
  min-height: 99%;  
}
html.blue div#Body div#Inner{
 border:initial;
}
table.grid th, table.grid td  {
  padding:0px;
}
.DAY5 .container{
  width:8px;
}
.DAY6 .container{
  width:8px;
}
div#wTBp0202_Bottom{
  font-size: 12px;
  font-weight: bolder;
}
.JO_JOB_TITLE .container{
  width: 158px;
}
.ACTIVITY .container{
  width: 87px;
}
.NARRATIVE .container{
  width: 98%;
}
td.NARRATIVE.dataType_s {
  width: 98%;
}

table.tbody .container,
.todyBodyHeight{
    height: 29px;
}            
table.grid td .container{
  white-space: initial;
}

fieldset{
	margin:0px;
}
div#Body div#Inner div#Top{
    height: 128px;
}
div#Body div#Inner div#Bottom{
    top: 127px;
}

#Top > fieldset:nth-child(2) > fieldset:nth-child(3) {
  clear: initial !important;
}
}
