css:
/* i really want this to be global */

#discover_button, .pinned_sidebar_footer, .controls_section.controls_section_radar, .radar.radar_type_photo.is_media.post, .sidebar_link[href="/explore"], .save-the-internet-button, .recommended-unit-container.blog-card-compact, .sponsored_post, .explore-trending-badge-footer {
        display: none;
    }

    #tabs_outer_container .tab_notice, #tabs_outer_container .tab_notice_value {
        background-color: #bc5555!important;
        color: #fff!important;
    }

    .l-header-container--refresh {
        box-shadow: none;
        padding-top: 0px;
        padding-bottom: 5px;
    }
    
    #account_button {
        padding-right: 0;
    }
    
    li.remnant-unit-container {
        display: none;
    }
    
    .post_full:hover .post_body {
        overflow: hidden;
    }
    
    #dashboard_controls_open_blog {
        margin-top: 0;
    }
    
    .save-the-internet-button+.blog_menu .popover_blogs {
        top: 0!important;
}
    
    .post_full .post_info_fence, .post_full.is_reblog .post_info_fence>a, .post .post-source-footer a.post-source-link, .post_full .post_info {
        max-width: 100%!important;
    }
    
    .svg-logo {
        display: none;
    }
    
    .logo-anchor {
        background: url('https://i.imgur.com/pPayJbc.png') no-repeat;
        width: 200px;
        margin-left: 5px;
        margin-top: -3px;
    }
    
    .logo-anchor:active {
        opacity: .8;
    }
    
    .l-header-container {
        box-shadow: 1px!important;
    }
    
    .logged_in .l-header {
        max-width: 100%!important;
        padding: 0 20px 0 0!important;
    }
    
    .l-header-container--refresh .l-header .logo.logo-redesign {
        margin-left: 0!important;
    }
