css:
@-moz-document domain("vk.com") {
	a{
		color: #0000cc;
	}
	
	#pb_text {
		direction: rtl;
		font-family: tahoma;
		font-size: 13px;
		text-align: justify;
	}

	.wide_column{
		width:70%;
	}
	
	.wall_module .post_info {
		direction: rtl !important;
		margin-left: 0 !important;
		/*margin-right: 10px !important;*/
		text-align: right !important;
		width:90% !important;
	}
	
    .wall_module div.post_image {
		padding: 5px 0 !important;
		right: 0 !important;
	}

	.wide_wall_module .post_full_like_wrap, .post_fixed .post_full_like_wrap {
		float: left !important;
	}
	
	.post{
		padding: 0 0 16px !important;
	}
	
	.reply_field{
		width:95% !important;
	}
	
.info_footer.sm {
    direction: ltr !important;
    text-align: left !important;
	padding-top: 60px;
}
	 .sm{
		direction: ltr !important;
    text-align: right !important;
	}
	
.wide_wall_module .reply_info {
    margin-left: 0 !important;
    margin-right: 20px !important;

	padding: 0px !important;
}
.wall_module .post_actions, .wall_module .reply_actions {
    direction: ltr !important;
    float: left;
    left: 0 !important;
    right: auto !important;
}
.wall_module .post_delete_button, .wall_module .post_edit_button, .wall_module .reply_delete_button, .wall_module .reply_edit_button {
    float: left !important;
}
.wall_module .post_actions_wrap, .wall_module .reply_actions_wrap {
    float: left !important;
}
.page_fronly {
    margin: 2px 4px 0 0 !important;
}

.reply_text > div {
    float: right !important;

}
.reply_text .author {
    float: right !important;
		padding-left: 5px !important;
    padding-right: 5px !important;
    padding-top: 5px !important;
}
  
.reply_image {
    height: 40px !important;
    width: 40px !important;

}
	.wide_wall_module .reply {
    padding: 0px;
}
.published_by_wrap {
    direction: ltr !important;
    text-align: left !important;
}
.wide_wall_module .wall_reply_text {
    width: 517px !important;
}
.wall_module .reply_info {
    margin-left: 0 !important;
    margin-right: 45px !important;
    width: auto  !important;
}
	
	body{		
		font-family: Arial,sans-serif !important;
		color: black !important; 
		background-color: #aaa !important;
	}
			
	#page_layout{
		width:80% !important;
	}
	
	#page_body{
		width:80% !important;
		padding:0 !important;
	}
	
	.wall_text{
		width:100% !important;
	}
	
	.post_info{
		width:85% !important;
	}
	
	.wall_post_text{
		direction: rtl !important;
		text-align: justify !important;
	}
	 
	
	
	.wide_wall_module .wall_post_text, .wide_wall_module .wall_reply_text {
		line-height: 160% !important;
		text-align: justify !important;
		width: 100% !important;
	}
	
	#page_header > div{
		background: transparent !important;
		padding: 10px;
	}
	
	#main_feed > .tabs{
		background: #2f72c4 !important;
	}
	
	ul.t0 *{
		background:transparent !important;
		border: 0px !important;
		color: white;
		font-family: Arial,sans-serif;
    font-size: 15px;
	}
	
	ul.t0{
		width: unset;
	}
	
	.t_bar{
		padding: 0 10px 0 0 !important;
	}
	
	#ts_wrap {
		top: 10px;
		right: 300px;
		left: unset;
	}
	
	#side_bar{
		width:185px !important;
		padding: 0;
		background: #eee !important;
	}
		
	#side_bar ol li .left_row {
		padding: 2px 0px 2px 4px;
		font-size: 15px !important;
		background-color: transparent !important;		
	}
	#side_bar ol li > .left_row {  
		padding-left: 20px;
	}
	#myprofile_table{
		background-color: #6fac38;
		padding: 5px;
	}
	
	#myprofile_table a{
		color: white !important;  
	}
	
	#wrap2 {
		font-size: 13px !important;
		line-height: 15px !important;;
		padding: 2px !important;
		background-color: White !important;
	}
	
	#feed_bar {
		background: transparent !important;
		border-bottom: 1px solid #dae1e8;
		padding: 0px 10px;
	}

	.wide_wall_module {
		padding: 5px 15px 15px !important;;
	}
	
	.reply {
		padding-left: 5px !important;
		padding-right: 5px !important;
		padding-bottom: 3px;
	}
	
	#feed_news_bar > div{
		padding: 2px 5px;
	}
	
	.summary_wrap {
		padding: 0 10px;
	}
	
	.reply_box {
		background: transparent !important;
	}
	
	.reply_box_open .reply_form {
		margin-left: 0 !important;
	}
	
	.wall_module .reply_fakebox_wrap {
		background: transparent !important;
		padding: 6px;
	}
	
	.flat_button, .button_gray button, .button_blue button {
		background: #ededed;
		border: 1px solid #acacac;
		border-radius: 0;
		color: black;
	}
		
	.wide_wall_module .post_full_like_wrap, .post_fixed .post_full_like_wrap {
		width: 100%;
	}
	
	.replies{
		color: #333333 !important;
		font-size: 13px !important;
	}
			
	.post_full_like {
		left: auto !important;
		right: 160px !important;
	}
	
	.wall_module .no_shares {
		visibility: visible;
	}
	
	.reply_link_wrap.sm > * {
		float: right !important;
		direction: ltr !important;
		padding-left: 5px;
		margin-left: 6px; 
		border-left: 1px solid gray;
	}
	
	.reply_link_wrap.sm .divide {
		visibility: hidden;
		padding: 0 !important;
	}
	
	#page_header > .right{
		background: url("http://ge.tt/api/1/files/1xZWePE2/0/blob") no-repeat !important;
		width: 227px !important;
		
	}
	
	#page_header{
		padding: 5px 20px 15px!important;;
	}
	
	#page_header  #top_nav{
		direction: rtl;
		float: right;
		padding-right: 330px;
	}
}
