css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("joyreactor.cc") {

  #pageinner > DIV[id]:not([id="content"]):not([id="sidebar"]):not([id="taglist"]) {
    display: none !important;
  }

  DIV[id="content"][class] {
    display: none !important;
  }

  #content {
    margin-bottom: 0px !important;
  }

  body {
    padding: 0px !important;
    margin: 0px !important;
    font-size: 10pt !important;
    line-height: 16px !important;
    font-family: Tahoma,Arial,Verdana,sans-serif !important;
    background-color: #FAB728 !important;
    color: #586263;
  }

  a {
    color: #586263;
  }

  a:hover,
  .postContainer a:hover,
  .article .ufoot span a:hover {
    color: #841700;
  }

  #background {
    background: url("http://css.joyreactor.cc/images/bk_new.png") repeat-x scroll left 20px #FAB728 !important;
/*    background: url(http://neotemple.ru/work/bg/bg1.gif) repeat !important; */
  }

  #container {
    background: url("http://css.joyreactor.cc/images/joyreactor_bk.jpg") no-repeat scroll center 20px transparent !important;
/*    background: none !important;*/
    margin-top: 0px !important;
  }


  /* Topbar */

  .topbar_wr {
    height: 36px;
    background: none repeat scroll 0% 0% rgba(57, 47, 30, 1.0); /* #392F1E; */
  }

  .topbar_wr, .topbar_wr a {
    font-weight: bold;
    color: #8B857B;
    line-height: 36px;
  }

  .topbar_wr a:hover {
    color: inherit !important;
    text-decoration: underline !important;
  }

  .top_logo {
    font-size: 10pt;
  }

  .swf_switcher {
    font-size: 9pt;
  }

  .sswither {
    margin: 7px 10px;
    height: 24px;
  }

  .sswither span {
    width: 28px;
    height: 16px;
    top: 2px;
  }

  .lang_select {
    position: relative;
    top: -1px;
  }

  .login_wr a,
  .login_wr a:hover {
    color: #FFF !important;
    font-size: 10pt;
  }

  .icon_mail {
    margin: 9px 0px 0px 13px;
  }

  #header + span {
    display: none !important;
  }

  #header {
    height: 75px !important;
    top: 0px !important;
  }

  #header .logo {
    padding: 12px 0px 0px !important;
  }

  #header .logo {
    margin-top: 0px !important;
  }

  #header .logo a,
  #header .logo a:hover {
    width: 207px;
    height: 54px;
    text-decoration: none !important;
    display: block !important;
    background: url("http://css.joyreactor.cc/images/joyreactor.png") no-repeat scroll 0px 0px transparent !important;
  }

  #header .description {
    margin: -41px 0px 0px -21px !important;
    color: #FAF68C !important;
    text-shadow: 0px 1px 1px #000 !important;
    font-size: 11px !important;
  }

  /* Nav block */

  #navcontainer {
    height: 41px !important;
    padding: 0px 16px 0px !important;
    position: relative;
    border-radius: 5px 5px 0px 0px !important;
  }

  #navcontainer::after {
    opacity: 1 !important;
    background: none repeat scroll 0% 0% rgba(41, 29, 2, 0.8 );
  }

  #navcontainer ul {
    height: 38px !important;
    line-height: normal !important;
  }

  #navcontainer ul li span {
    text-decoration: inherit !important;
  }

  #navcontainer ul li a {
    font-family: inherit !important;
    font-size: 10pt !important;
    text-transform: none !important;
    color: #FDB201 !important;
    text-decoration: inherit !important;
    line-height: normal !important;
  }

  #navcontainer ul li a:hover {
    text-decoration: underline !important;
  }


  #navcontainer ul li {
    height: 22px !important;
    padding: 12px 12px !important;
  }

  #navcontainer ul li.current_page_item span,
  #navcontainer ul li.current_page_ancestor span,
  #navcontainer ul li#konkurs span {
    height: 22px;
    margin-top: -2px;
    border-radius: 6px;
    background: url("http://css.joyreactor.cc/images/mainmenu_active_bg1.png") repeat-x scroll 0px 0px #d3811e;
    border-width: 1px 1px 0px;
    border-style: solid;
    border-color: #D3811E #D3811E #6C1403;
    border-image: none;
    box-shadow: 0px 1px 1px #6C1403;
} 

  #navcontainer ul li.current_page_item a,
  #navcontainer ul li.current_page_ancestor a,
  #navcontainer ul li#konkurs.current_page_ancestor a {
    opacity: 1 !important;
    color: #952B06 !important;
    text-decoration: none !important;
    border-radius: 0px !important;
    background: url("http://css.joyreactor.cc/images/mainmenu_active_a.png") no-repeat scroll 50% 100% transparent !important;;
    padding: 1px 12px 7px !important;
  }

  #navcontainer ul li.current_page_item a::after,
  #navcontainer ul li.current_page_ancestor a::after,
  #navcontainer ul li#konkurs.current_page_ancestor a::after {
    display: none !important;
  }

  .post_random a {
    opacity: 1 !important;
    text-transform: none !important;
    background: none !important;
    color: #FDB201 !important;
  }

  .post_random a:hover {
    text-decoration: underline !important;
  }

  /*
   * Search bar?!
   */

  #searchBar {
    font-size: 80% !important;
    background: #E0E0E0 !important;
    background: linear-gradient(to top, #D0D0D0, #EFEFEF) !important;
    height: 33px !important;
    padding: 0px 16px !important;
    border-radius: 0px 0px 5px 5px !important;
    border-bottom: 1px solid rgba(0, 0, 0, 0.3) !important;
    border-left: 1px solid rgba(0, 0, 0, 0.3) !important;
    border-right: 1px solid rgba(0, 0, 0, 0.3) !important;
    box-shadow: inset 0px 0px 2px 1px rgba(255, 255, 255, 0.5), inset 0px 0px 0px 1px rgba(0, 0, 0, 0.2) !important;
  }

  #submenu div.tagname {
    color: inherit !important;
    line-height: normal !important;
    padding: 0px 14px !important;
  }

  #submenu a, #submenu b {
    color: inherit !important;
    font-size: 100% !important;
    text-decoration: inherit !important;
  }

  #submenu a:hover {
    text-decoration: underline !important;
  }
/*
  #submenu div.submenuitem {
    position: relative !important;
    top: -0px !important;
  }

  #submenu div.active {
    position: relative !important;
    top: 0px !important;
  }
*/

  #submenu div.submenuitem.active {
    position: relative !important;
    top: 0px !important;
    height: 34px !important;
    background-color: #FDB201 !important;
    border-radius: 0px 0px 3px 3px !important;
  }


  #submenu div.submenuitem.active a,
  #submenu div.submenuitem.active a:hover {
    line-height: normal !important;
    padding: 10px 20px 8px 20px !important;
  	opacity: 1 !important;
    color: #000 !important;
    text-decoration: none !important;
  }

/* Content Side */

  #page {
    margin-bottom: 0px !important;
  }

  #pageinner {
    padding: 0px !important;
    margin-top: 4px !important;
    background-color: transparent !important;
  }

  #content {
    width: 920px !important;
    margin-bottom: 20px !important;
  }

  #contentinner {
    padding: 10px 9px !important;
    border: 1px solid #B56B14 !important;
    border-radius: 5px 0px 5px 5px !important;
    background-color: #F8F8F8 !important;
  }

  #contentInnerHeader {
    margin: 0px 27px;
  }

  #add_post div.add_post_tags {
    padding: 0px 5px;
  }

  #add_post > div > div.add_post_footer {
    padding: 0px 16px !important;
    line-height: 26px !important;
    margin: 0px 0px 16px;
    background-color: #ECECEC !important;
    border-radius: 5px !important;
  }

  #add_post span.super-tag {
    color: #444 !important;
    padding: 4px 12px !important;
    line-height: normal !important;
    font-size: 8pt !important;
    background-color: #E8E8E8;
    border-width: 1px 0px;
    border-style: solid !important;
    border-color: #D0D0D0;
    border-radius: 5px;
  }

  #add_post span.super-tag:hover {
    background-color: #EEE;
    text-decoration: underline;
  }

  #add_post span.super-tag.active {
    background-color: #E4E4E4;
    box-shadow: 0px 0px 1px 1px #fdb201;
    cursor: pointer;
  }

  #picture_url {
    margin: -1px 16px 0px !important;
  }

  #add_post .addpost_submit_wr {
    margin-bottom: 0px !important;
  }

  #content div.mainheader {
    font-size: 10pt !important;
    margin-bottom: 8px !important;
  }

/* Пост */


  .post_description {
    font-size: 8pt;
    color: #888;
    line-height: normal !important;
    font-weight: normal !important;
  }

  .article {
    padding: 5px;
    margin: 0px 0px 6px;
    display: block;
    position: relative;
    background: none repeat scroll 0% 0% #FFF;
    border: 1px solid #DDD;
  }

  /* область информации об авторе поста */

  .article .uhead .avatar {
    max-width: 35px !important;
    max-height: 35px !important;
  }

  .article .uhead {
    height: 36px !important;
    margin-bottom: 6px !important;
  }

  .article .uhead a {
    font-size: 10pt !important;
    color: #972D06 !important;
  }


  /* область тэгов */

  .taglist {
    color: #666 !important;
    font-weight: normal !important;
    font-size: 8pt !important;
    margin: 0px 0px 5px !important;
    line-height: normal !important;
  }

  .taglist a {
    font-weight: bold !important;
    line-height: normal !important;
    color: #666;
    padding-bottom: 5px !important;
    margin-right: 3px !important;
  }


  /* Область поста */

  .article .post_content {
    margin: 0px !important;
    font-size: 11pt;
    line-height: 164%;
  }

  /* "Развернуть" */
  .article .post_content_expand,
  #tagArticle .post_content_expand {
    margin: 0px !important;
    opacity: 1 !important;
    background-color: #ECECEC !important;
    height: 36px !important;
    background: linear-gradient(to top, #D0D0D0, #ECECEC) !important;
 }

  .article .post_content_expand:hover,
  #tagArticle .post_content_expand:hover {
    background-color: #E4E4E4 !important;
    background: linear-gradient(to top, #CCCCCC, #E4E4E4) !important;
  }

  .article .post_content_expand span,
  #tagArticle .post_content_expand span {
    line-height: 36px !important;
    font-size: 8pt !important;
    color: #777 !important;
  }

  /* Нижняя часть поста */

  .article .ufoot {
    padding-top: 6px !important;
  }

  .article .ufoot .ufoot_first {
    padding-left: 0px !important;
    font-size: 8pt !important;
  }

  .article .ufoot span.comments {
    position: relative !important;
    margin-right: 10px !important;
    background-color: #EEE !important;
    color: #444 !important;
    padding: 5px 16px 4px 16px !important;
    line-height: normal !important;
    font-size: 8pt !important;
    border-width: 1px !important;
    border-style: solid !important;
    border-color: #DDDDDD #F0F0F0 !important;
    border-radius: 5px !important;
  }

  .article .ufoot span,
  .article .ufoot span a {
    line-height: normal !important;
  }

  .article .ufoot span.comments a {
    background: url("http://css.joyreactor.cc/images/comments.png") no-repeat scroll left 1px transparent !important;
    padding-left: 20px !important;
    overflow: hidden;
  }

  .article .ufoot span.comments a::before {
    display: none !important;
  }

  .commentnum {
    vertical-align: top !important;
    font-weight: bold !important;
    line-height: normal !important;
    text-transform: none !important;
    text-decoration: underline !important;
    padding: 0px !important;
    border: none !important;
    border-radius: 0px !important;
    background-color: transparent !important;
  }

  .commentnumDelta {
    font-size: 8pt !important;
    height: auto !important;
/*
    color: #FFF !important;
    background-color: #FAB728 !important;
    padding: 2px 3px !important;
    top: -1px !important;
    left: 4px !important;
*/
    padding: 0px 0px 0px 6px !important;
    background: transparent !important;
    color: #FAB728 !important; /* #FAB728 */
    font-weight: bold !important;
    text-shadow: 1px 1px 0px rgba(255,255,255, 1.0) !important;
    line-height: normal !important;
    position: static !important;
    border: none !important;
    border-radius: 3px !important;
  }


/*
  .article .ufoot span.comments a::before {
    background: url("http://css.joyreactor.cc/images/comments.png") no-repeat scroll 0px 3px transparent !important;
    width: 18px !important;
    height: 18px !important;
  }
*/


  .article .ufoot_first > span.date {
    font-size: 8pt !important;
    margin-right: 15px !important;
  }

  .article .ufoot_first > span.date .time {
    font-weight: bold !important;
  }

  .article .ufoot span.hidden_link a,
  .article .ufoot span.link_wr a {
    font-weight: normal !important;
    margin-right: 10px !important;
  }

/* Голосовалки */

  .post_rating {
    font-size: 11pt;
    float: right !important;
    margin-top: 5px !important;
  }

  div.vote-plus,
  div.vote-plus:hover,
  div.vote-minus,
  div.vote-minus:hover {
    width: 13px !important;
    height: 18px !important;
    cursor: pointer !important;
    display: inline-block !important;
    vertical-align: middle !important;
    margin-top: 3px !important;
  }

  div.vote-plus,
  div.vote-plus:hover {
    background: url("http://css.joyreactor.cc/images/vote_plus.png") no-repeat scroll 0% 0% transparent !important;
    float: left;
    margin-right: 8px !important;
  }

  div.vote-minus,
  div.vote-minus:hover {
    background: url("http://css.joyreactor.cc/images/vote_minus.png") no-repeat scroll 0% 0% transparent !important;
    float: right;
    margin-left: 8px !important;
  }


  /* Для блока комментариев */
  .comment_rating div.vote-plus,
  .comment_rating div.vote-plus:hover,
  .comment_rating div.vote-minus,
  .comment_rating div.vote-minus:hover {
    margin-top: 0px !important;
  }

/* Блок комментариев */

  .comment {
    margin-bottom: 6px !important;
    padding: 4px 10px 4px 24px !important;
    border: 1px solid #E0E0E0;
    border-radius: 5px !important;
    background-image: url("http://css.joyreactor.cc/images/comments.png") !important;
    background-repeat: no-repeat !important;
    background-position: 4px 4px !important;
    background-color: #F7F7F7;
  }


  DIV.comment[style*="background"] {
/*    background-image: url("http://css.joyreactor.cc/images/comments.png") no-repeat 4px 4px #F7F7F7; */
    border-color: #E0E0E0 !important;
  }
/*
  .comment:hover {
    background-color: #EBEBEB !important;
  }
*/
  .comment .txt div:first-child {
    font-size: 9.6pt !important;
    line-height: 152% !important;
  }

  .comment .txt font {
    font-size: 7pt !important;
    color: #777 !important;
  }

  .comment .txt b {
    font-weight: normal !important;
  }

  .comment .comment_show {
    color: #AAA !important;
  }


  .comments_bottom {
    font-size: 8pt !important;
    margin-top: 4px !important;
    
  }

  .comments_bottom .avatar {
    display: none !important;
  }

  .article {
    
  }

  .article .ufoot div.comment .reply-link a.comment_username {
    margin-left: 0px !important;
  }

  .comments_bottom,
  .article .ufoot .txt span,
  .article .ufoot span,
  .article .ufoot span a {
    line-height: normal;
  }

  .article .ufoot .post_comment_form span,
  .article .ufoot .post_comment_form span a {
    line-height: 42px !important;
  }

  .article .ufoot span a.response {
    font-size: 10pt !important;
    font-weight: normal !important;
    border-radius: 0 !important;
    padding: 0px !important;
    background: none !important;
  }

  .comment_username,
  .comment_username:hover {
    font-weight: normal !important;
  }

  .article .ufoot .txt span.comment_rating {
    font-size: 8pt !important;
  }

  .article .ufoot div.comment .reply-link a,
  .article .ufoot div.comment .comment_rating,
  #post_list div.comment .reply-link a,
  #post_list div.comment .comment_rating {
    margin-left: 8px;
  }

  /* Написать комментарий */

  .addcomment {
    margin-right: 5px;
  }

  .post_comment_form {
    font-size: 10pt;
  }

  /* Управление постом */

  .manage_post_wr {
    padding: 0px 8px;
    margin: 0px;
    background-color: rgba(253, 218, 151, 0.4);
    line-height: 24px;
    border-radius: 5px;
    box-shadow: inset 0px 0px 0px 1px rgba(0, 0, 0, 0.1);
  }

  .manage_post_wr span a {
    margin: 0px 10px;
  }


/*
 * Боковые блоки
 */

  #sidebar {
    border-radius: 0px 5px 5px 0px !important;
    padding-top: 6px !important;
    width: 340px !important;
    background: linear-gradient(to right, #AAA, #EEE 8px, #EEE) !important;
    border: 1px solid #B56B14 !important;
    box-shadow: inset -1px 0px 1px 1px rgba(255, 255, 255, 0.2), inset -1px 0px 0px 1px rgba(0, 0, 0, 0.2) !important;
  }

  #sidebar .blogs.super li a {
    color: #FFF !important;
  }

  #sidebar ul li,
  #sidebar > div.sidebar_block {
    margin-bottom: 14px;
    line-height: 152% !important;
  }

  #sidebar ul li div.sideheader.taginfo,
  #sidebar .sideheader.taginfo {
    background-color: transparent !important;
    text-shadow: none !important;
    background-color: #FFF !important;
    font-size: 9pt;
    line-height: 172% !important;
  }

  #sidebar ul li div.sideheader,
  #sidebar .sideheader {
    border-radius: 0px !important;
    text-shadow: 0px 1px 1px rgba(0, 0, 0, 0.3), -1px -1px 0px rgba(255, 255, 255, 0.1);
    color: #f8c010;
    font-size: 10pt;
    text-transform: none !important;
    border-top: 2px solid rgba(255, 255, 255, 0.1) !important;
    border-bottom: 2px solid rgba(0, 0, 0, 0.1) !important;
    font-weight: bold !important;
    line-height: normal !important;
    background-color: #9b3a10 !important;
    padding: 6px 20px 6px 20px;
  }

  .delimiter {
    font-size: 7pt;
    color: #9b3a10;
  }

  .fn {
    color: #9b3a10;
  }

  .sidebarContent {
    margin-top: 10px !important;
    margin-left: 20px !important;
    margin-right: 20px !important;
    font-size: 10pt;
  }

  .user.week_top {
    background: none !important;
  }

  .user.week_top a {
    background-color: transparent !important;
  }

  .user.week_top .userposition {
    background-color: transparent !important;
  }

  #sidebar .sidebar_block {
  }

  #tagcloud {
    line-height: 200%;
  }

  /* удаляем рекламу (за флэшовое говно надо убить) */
  #sidebar > DIV:not([class*="sidebar_block"]) {
    display: none !important;
  }
  

/* Прочее */

  input#tag, .post_share_view input,
  .sfSignin input[type="text"],
  .sfSignin input[type="password"],
  .sf_apply input[type="text"],
  .sf_apply input[type="password"],
  #demotivator_form input[type="text"],
  .addcomment input[type="text"],
  #add_post input[type="text"],
  #add_poll input[type="text"],
  input[type="text"] {
    border-radius: 3px !important;
    border: 1px solid #CECECE;
    margin: 4px 2px;
    padding: 4px 6px !important;
    height: 27px;
    line-height: normal;
  }

  .addcomment textarea,
  #add_post textarea {
    border-radius: 3px !important;
    border: 1px solid #CECECE;
    padding: 6px !important;
    margin: 4px 2px;
  }

  input:focus,
  button:focus,
  textarea:focus {
    outline: none !important;
    border: 1px solid #FDB201 !important;
    box-shadow: 0px 0px 2px 0px rgba(253, 178, 1, 1);
  }

  #searchmenu {
    width: 300px;
    height: 30px;
    padding: 1px 8px 1px 4px;
    border: 1px solid #857456;
    border-radius: 5px;
    top: 5px;
  }

  #s {
    border: none !important;
    height: 26px;
    box-shadow: none !important;
    font-size: 10pt;
    width: 268px;
    padding: 0px 6px !important;
  }

  #searchsubmit {
    margin-top: -4px;
  }

  input[type="submit"],
  input[type="button"],
  button,
  big_button {
    padding: 8px 24px;
    line-height: normal !important;
    font-family: inherit !important;
    font-size: 100% !important;
    min-height: 0px !important;
    border-radius: 5px !important;
    font-size: 10pt;
  }

  input[type="submit"]:focus {
    border: none !important;
    box-shadow: none !important;
  }


/*
 * Профиль
 */

  .stars > div:nth-child(3) {
    border-color: #888 !important;
    background-color: #EAEAEA !important;
    border-radius: 4px;
    box-shadow: inset 0px 0px 2px 2px rgba(0, 0, 0, 0.1);
  }

  .poll_res_bg_active {
    border-radius: 3px;
    background: linear-gradient(to top, #eda91b, #FAB728 8px, #fdbd34);
    border-right: 1px solid rgba(0,0,0,0.1);
  }

}
