css:
@-moz-document domain('forum.blockland.us') {
* {
  background: none !important;
  border: none !important;
}
body,
td,
th,
.post {
  color: #263238 !important;
  font-size: 11pt !important;
  font-family: 'Roboto', verdana, sans-serif !important;
}
body,
td:not(*[style]),
th,
.post {
  font-weight: 400 !important;
}
td[style*="padding"] {
  padding: 0 !important;
}
.smalltext {
  font-size: 9pt !important;
}
table td {
  padding: 3px 5px;
}
a,
a * {
  color: #1976d2 !important;
  text-decoration: none !important;
}
a:hover,
a *:hover,
a:hover *,
a *:hover * {
  color: #092949 !important;
  text-decoration: none !important;
}
b {
  font-weight: 700 !important;
}
body {
  background: #e0e0e0 !important;
}
table {
  margin: auto !important;
}
.post img {
  max-width: 100%;
}
.bordercolor {
  border-spacing: 2px 5px !important;
}
.tborder {
  margin-top: 5px !important;
}
body > table:not(:last-of-type),
body > table {
  max-width: 90%;
}
body > table:first-of-type {
  margin-top: 20px !important;
}
body > table:first-of-type td {
  background-color: white !important;
  padding: 0px !important;
  -webkit-box-shadow: 0px 1px 2px #a0a0a0 ;
  -moz-box-shadow: 0px 1px 2px #a0a0a0 ;
  box-shadow: 0px 1px 2px #a0a0a0 ;
  display: block;
}
body > table:first-of-type td:first-child {
  height: 100px;
  background: url(http://i.imgur.com/7rOvoqF.png) !important;
  background-position: 50% 50% !important;
  background-repeat: no-repeat !important;
  background-size: 100% auto !important;
}
body > table:first-of-type td:first-child img {
  -webkit-transition: 0.3s;
  -moz-transition: 0.3s;
  -o-transition: 0.3s;
  transition: 0.3s;
  background-image: url(http://i.imgur.com/Q4AdXQB.png) !important;
  background-size: auto 60px !important;
  background-repeat: no-repeat !important;
  background-position: 20px 20px !important;
  width: 0;
  height: 0;
  padding-right: 360px;
  padding-top: 90px;
}
body > table:first-of-type td:last-child {
  padding: 10px 15px !important;
  text-align: right;
  max-width: 92%;
  margin: -10px auto 10px;
}
center:first-of-type {
  display: block;
  width: 82.8%;
  margin: -53px auto 30px !important;
  text-align: left;
}
center:first-of-type br {
  display: none;
}
hr,
.hrcolor {
  border-bottom: 1px solid #b3b3b3 !important;
}
.windowbg,
.windowbg2,
.windowbg > td,
.windowbg2 > td,
.catbg,
.titlebg > td {
  background-color: white !important;
  padding: 7px 12px !important;
  -webkit-box-shadow: 0px 1px 2px #a0a0a0 ;
  -moz-box-shadow: 0px 1px 2px #a0a0a0 ;
  box-shadow: 0px 1px 2px #a0a0a0 ;
}
.titlebg > td {
  margin-bottom: 5px !important;
}
.catbg td {
  padding-bottom: 0 !important;
}
span.nav {
  display: block;
}
span.nav img,
span.nav br {
  display: none;
}
span.nav b:not(:first-of-type)::before {
  content: '> ';
  margin: 0 3px;
  color: #263238 !important;
}
span.nav a,
span.nav a:hover {
  font-weight: 400;
}
.quote,
.code {
  color: #263238 !important;
  padding: 6px 8px !important;
  font-size: 9pt !important;
  margin-bottom: 10px !important;
}
.quote {
  background-color: rgba(0, 0, 0, 0.05) !important;
}
.code {
  background-color: rgba(0, 0, 0, 0.05) !important;
}
.quoteheader {
  margin: 5px 0px 2px 3px !important;
  font-size: 9pt !important;
}
.avatar {
  margin-top: 7px !important;
}
textarea {
  -webkit-box-shadow: 0px 0.5px 3px #b5b5b5 inset;
  -moz-box-shadow: 0px 0.5px 3px #b5b5b5 inset;
  box-shadow: 0px 0.5px 3px #b5b5b5 inset;
  color: #263238 !important;
  border: 1px solid #d9d9d9 !important;
  padding: 3px 6px !important;
  margin: 10px 0 !important;
}
::selection {
  background-color: #64a9ed !important;
  color: white !important;
}
img[src*="/images/ip.gif"] + a {
  font-size: 9pt !important;
}
input:not([type]),
input[type='text'],
input[type='password'],
select {
  -webkit-box-shadow: 0px 0.5px 3px #b5b5b5 inset;
  -moz-box-shadow: 0px 0.5px 3px #b5b5b5 inset;
  box-shadow: 0px 0.5px 3px #b5b5b5 inset;
  color: #263238 !important;
  border: 1px solid #d9d9d9 !important;
  padding: 3px 6px !important;
}
input[type='submit'],
input[type='button'] {
  -webkit-border-radius: 2px;
  -moz-border-radius: 2px;
  border-radius: 2px;
  -webkit-box-shadow: 0px 1px 3px #a0a0a0 ;
  -moz-box-shadow: 0px 1px 3px #a0a0a0 ;
  box-shadow: 0px 1px 3px #a0a0a0 ;
  background-color: #1976d2 !important;
  color: white !important;
  padding: 7px 13px !important;
  margin-left: 10px !important;
  font-size: 10pt !important;
  font-weight: 400 !important;
  border: none !important;
  display: inline-block;
  cursor: pointer;
}
input[type='submit']:hover,
input[type='button']:hover {
  background-color: #2e8ae6 !important;
}
img[src*='/images/english/login.gif'],
img[src*='/images/english/register.gif'],
img[src*='/images/english/home.gif'],
img[src*='/images/english/help.gif'],
img[src*='/images/english/search.gif'],
img[src*='/images/english/profile.gif'],
img[src*='/images/english/logout.gif'],
img[src*='/images/english/markread.gif'],
img[src*='/images/english/notify.gif'],
img[src*='/images/english/new_topic.gif'],
img[src*='/images/english/new_poll.gif'],
img[src*='/images/english/addpoll.gif'],
img[src*='/images/english/reply.gif'],
img[src*='/images/english/admin_lock.gif'] {
  content: '';
}
img[src*='/images/english/login.gif']::after,
img[src*='/images/english/register.gif']::after,
img[src*='/images/english/home.gif']::after,
img[src*='/images/english/help.gif']::after,
img[src*='/images/english/search.gif']::after,
img[src*='/images/english/profile.gif']::after,
img[src*='/images/english/logout.gif']::after,
img[src*='/images/english/markread.gif']::after,
img[src*='/images/english/notify.gif']::after,
img[src*='/images/english/new_topic.gif']::after,
img[src*='/images/english/new_poll.gif']::after,
img[src*='/images/english/addpoll.gif']::after,
img[src*='/images/english/reply.gif']::after,
img[src*='/images/english/admin_lock.gif']::after {
  -webkit-border-radius: 2px;
  -moz-border-radius: 2px;
  border-radius: 2px;
  -webkit-box-shadow: 0px 1px 3px #a0a0a0 ;
  -moz-box-shadow: 0px 1px 3px #a0a0a0 ;
  box-shadow: 0px 1px 3px #a0a0a0 ;
  background-color: #1976d2 !important;
  color: white !important;
  padding: 7px 13px !important;
  margin-left: 10px !important;
  font-size: 10pt !important;
  font-weight: 400 !important;
  border: none !important;
  display: inline-block;
  cursor: pointer;
}
img[src*='/images/english/login.gif']::after:hover,
img[src*='/images/english/register.gif']::after:hover,
img[src*='/images/english/home.gif']::after:hover,
img[src*='/images/english/help.gif']::after:hover,
img[src*='/images/english/search.gif']::after:hover,
img[src*='/images/english/profile.gif']::after:hover,
img[src*='/images/english/logout.gif']::after:hover,
img[src*='/images/english/markread.gif']::after:hover,
img[src*='/images/english/notify.gif']::after:hover,
img[src*='/images/english/new_topic.gif']::after:hover,
img[src*='/images/english/new_poll.gif']::after:hover,
img[src*='/images/english/addpoll.gif']::after:hover,
img[src*='/images/english/reply.gif']::after:hover,
img[src*='/images/english/admin_lock.gif']::after:hover {
  background-color: #2e8ae6 !important;
}
img[src*='/images/english/login.gif']:hover::after,
img[src*='/images/english/register.gif']:hover::after,
img[src*='/images/english/home.gif']:hover::after,
img[src*='/images/english/help.gif']:hover::after,
img[src*='/images/english/search.gif']:hover::after,
img[src*='/images/english/profile.gif']:hover::after,
img[src*='/images/english/logout.gif']:hover::after,
img[src*='/images/english/markread.gif']:hover::after,
img[src*='/images/english/notify.gif']:hover::after,
img[src*='/images/english/new_topic.gif']:hover::after,
img[src*='/images/english/new_poll.gif']:hover::after,
img[src*='/images/english/addpoll.gif']:hover::after,
img[src*='/images/english/reply.gif']:hover::after,
img[src*='/images/english/admin_lock.gif']:hover::after {
  background-color: #2e8ae6 !important;
}
img[src*='/images/english/quote.gif'],
img[src*='/images/english/modify.gif'],
img[src*='/images/english/reply_sm.gif'],
img[src*='/images/english/notify_sm.gif'] {
  content: '';
}
img[src*='/images/english/quote.gif']::after,
img[src*='/images/english/modify.gif']::after,
img[src*='/images/english/reply_sm.gif']::after,
img[src*='/images/english/notify_sm.gif']::after {
  font-size: 10pt !important;
  color: #1976d2 !important;
  margin: 0px 14px;
}
img[src*='/images/english/quote.gif']:hover::after,
img[src*='/images/english/modify.gif']:hover::after,
img[src*='/images/english/reply_sm.gif']:hover::after,
img[src*='/images/english/notify_sm.gif']:hover::after {
  color: #092949 !important;
}
img[src*='/images/english/login.gif']::after {
  content: 'Login';
}
img[src*='/images/english/register.gif']::after {
  content: 'Register';
}
img[src*='/images/english/home.gif']::after {
  content: 'Home';
}
img[src*='/images/english/help.gif']::after {
  content: 'Help';
}
img[src*='/images/english/search.gif']::after {
  content: 'Search';
}
img[src*='/images/english/profile.gif']::after {
  content: 'Profile';
}
img[src*='/images/english/logout.gif']::after {
  content: 'Logout';
}
img[src*='/images/english/markread.gif']::after {
  content: 'Mark Read';
}
img[src*='/images/english/notify.gif']::after {
  content: 'Notify';
}
img[src*='/images/english/new_topic.gif']::after {
  content: 'New Topic';
}
img[src*='/images/english/new_poll.gif']::after {
  content: 'New Poll';
}
img[src*='/images/english/addpoll.gif']::after {
  content: 'Add Poll';
}
img[src*='/images/english/reply.gif']::after {
  content: 'Reply';
}
img[src*='/images/english/quote.gif']::after {
  content: 'Quote';
}
img[src*='/images/english/modify.gif']::after {
  content: 'Modify';
}
img[src*='/images/english/reply.gif']::after {
  content: 'Reply';
}
img[src*='/images/english/notify_sm.gif']::after {
  content: 'Notify';
}
img[src*='/images/english/admin_lock.gif']::after {
  content: 'Lock / Unlock';
}
img[src*='/images/on.gif'],
img[src*='/images/off.gif'] {
  content: '';
}
img[src*='/images/on.gif']::after,
img[src*='/images/off.gif']::after {
  content: '';
  display: block;
  width: 20px;
  height: 20px;
  padding: 0;
  font-size: 0 !important;
  -webkit-transform: rotate(45deg);
  -moz-transform: rotate(45deg);
  -ms-transform: rotate(45deg);
  -o-transform: rotate(45deg);
  -webkit-box-shadow: 0.5px 0.5px 2px #a0a0a0 ;
  -moz-box-shadow: 0.5px 0.5px 2px #a0a0a0 ;
  box-shadow: 0.5px 0.5px 2px #a0a0a0 ;
}
img[src*='/images/on.gif']::after {
  background-color: #378fe7 !important;
}
img[src*='/images/off.gif']::after {
  background-color: #7d8c93 !important;
}

}
