.headerNavigation.top24 .menu li a {color: #CFCFCF !important;}
.headerNavigation.top24 .menu li.current a {color: #CFCFCF !important;}
.box {border-right-color: #302F31 !important;border-bottom-color: #302F31 !important;border-left-color: #302F31 !important;border-top-color: #302F31 !important;background-color: #887E73 !important;}
.box.sargaHeader .header {background-color: #B8944E !important;}
.box.sargaHeader .header .close {background-image: url("data:image/png;base64,") !important;}
.box.narancsHeader .header {background-color: #694A4B !important;}
.box.narancsHeader .header h2 {color: #CFCFCF !important;}
.box.narancsHeader .header a {color: #CFCFCF !important;}
