.box.kekHeader .header .close {background-image: url("data:image/png;base64,") !important;}
.box.kekHeader .header .more {background-image: url("data:image/png;base64,") !important;}
.box.pirosHeader .header h2 {color: #CFCFCF !important;}
.box.pirosHeader .header a {color: #CFCFCF !important;}
.box .content .important a, .newsProfileRed {color: #000000 !important;}
.box .footer a {color: #4D2429 !important;}
.footerInContent a {color: #4D2429 !important;}
.weather .dayColumn .temperature {color: #13369B !important;}
.tv .channel a {color: #000000 !important;}
