.column-wide .box {border-top-color: #653537 !important;}
.column-wide .box .source, .column-wide .box .source a {color: #5F3A3A !important;}
.searchTitle {color: #653537 !important;}
#main .searchMore a {color: #CFCFCF !important;}
.horoscopeTitle {color: #653537 !important;}
#popup input[type="submit"] {color: #CFCFCF !important;}
