ul.pureCssMenu li:hover > a {background-color: #52504D !important;}
ul.pureCssMenu li a:hover {background-color: #52504D !important;}
#nav a {background-color: #52504D !important;}
#nav a:hover {background-color: #52504D !important;}
.facebook-like {color: #4D2429 !important;}
