css:
@-moz-document domain(socialneko.com) {

*{
font-family:Lucida Sans, sans-serif;
}
html{
overflow-x:hidden
}

body{
width:100%;
max-width:340px;
overflow:hidden;
font-size:11px;
margin:0;
color:#000;
background:#ffdfb1
}

#header{
width:100%;
height:85px;
overflow:hidden;
background:#fff url(http://www.mediafire.com/convkey/7cbf/02wba4b4pipf9a6zg.jpg) no-repeat;
background-size:100% 100%;
transition:background .75s ease-in-out
}

#iheader,.logo{
background:#fff url(http://www.mediafire.com/convkey/7cbf/02wba4b4pipf9a6zg.jpg);
width:100%;
height:85px;
overflow:hidden
}

#welcome{
text-shadow:1px 0px #000
}

/* profile stuff */
.cover{
width:100%;
height:100px
}
.mailuser{
position:absolute;
top:2px;
left:1px
}
.onlineoroffline{
position:absolute;
top:1px;
left:15px;
font-size:10px
}
.usernamecontainer{
position:absolute;
top:85px;
left:69px;
background:#FFF;
font-size:12px;
border:1px solid gray;
border-left:none;
border-bottom:none;
border-radius:7px;
border-top-left-radius:0;
border-bottom-left-radius:0;
border-bottom-right-radius:0;
width:105px;
height:15px;
overflow:hidden;
text-align:center;
color:gray
}
.profilepiccontainer{
position:absolute;
top:33px;
left:-1px;
border:solid 1px silver;
border-radius:2px
}
#progressContainer{
background: #fcfcfc;
background: -moz-linear-gradient(top,  #fcfcfc 0%, #f7f7f7 20%, #e2e2e2 100%);
background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,#fcfcfc), color-stop(20%,#f7f7f7), color-stop(100%,#e2e2e2));
background: -webkit-linear-gradient(top,  #fcfcfc 0%,#f7f7f7 20%,#e2e2e2 100%);
background: -o-linear-gradient(top,  #fcfcfc 0%,#f7f7f7 20%,#e2e2e2 100%);
background: -ms-linear-gradient(top,  #fcfcfc 0%,#f7f7f7 20%,#e2e2e2 100%);
background: linear-gradient(to bottom,  #fcfcfc 0%,#f7f7f7 20%,#e2e2e2 100%);
width:90%;
margin:auto;
margin-top:4px;
padding:6px 8px 6px 8px;
border:1px solid #888;
border-radius:4px;
}
#rankpContainer{
background:#f7f7f7;
border:1px solid #888;
border-radius:5px;
}
#rankProgress{
color:#FFF;
background: #0084ea;
background: -moz-linear-gradient(left,  #0084ea 0%, #008cf7 49%, #0090ff 100%);
background: -webkit-gradient(linear, left top, right top, color-stop(0%,#0084ea), color-stop(49%,#008cf7), color-stop(100%,#0090ff));
background: -webkit-linear-gradient(left,  #0084ea 0%,#008cf7 49%,#0090ff 100%);
background: -o-linear-gradient(left,  #0084ea 0%,#008cf7 49%,#0090ff 100%);
background: -ms-linear-gradient(left,  #0084ea 0%,#008cf7 49%,#0090ff 100%);
background: linear-gradient(to right,  #0084ea 0%,#008cf7 49%,#0090ff 100%);

height:18px;
box-shadow:inset 0 1px 0 0 #42B0FF;
border-radius:3px;
border-top-right-radius:0;
border-bottom-right-radius:0;
}
#rankpts{
margin-top:8px;
font-weight:bold;
float:left;
}
#nextRank{
margin-top:8px;
font-weight:bold;
float:right;
}
#points{
width:90%;
margin:auto;
}

a,.headermenu,.mainmenubutton{
color:#0048a8;
text-decoration:none
}

a:hover,.mainmenubutton:hover{
color:#9EC100
}

H1{
color:#FFF;
background:#0090ff url(http://www.mediafire.com/convkey/b0e1/4e8ba856c22285wzg.jpg) repeat-x;
border:1px solid #0062AD;
border-radius:15px;
font:bold 18px Lucida Sans;
margin:15px auto 5px auto;
padding:0;
text-align:center;
width:88%;
text-shadow:-1px -1px 0 #000,1px -1px 0 #000,-1px 1px 0 #000,1px 1px 0 #000
}

H2{
color:#FFF;
background:#0090ff url(http://www.mediafire.com/convkey/b0e1/4e8ba856c22285wzg.jpg) repeat-x;
border:1px solid #0062AD;
border-radius:10px;
padding:0;
margin:0;
font-size:12px;
font-weight:bold;
text-shadow:1px 1px 0 #000
}

#box{
background:#A8DFF9;
border:2px solid #438A9A;
border-left:2px solid #438A9A;
border-right:2px solid #438A9A;
border-radius:0px;
padding:2px;
text-align:center;
word-wrap:break-word;
overflow:hidden
}

#box2{
background:#A8DFF9;
border:2px solid #438A9A;
border-radius:10px;
padding:2px;
margin:auto;
margin-bottom:6px;
text-align:center;
width:95%;
word-wrap:break-word;
overflow:hidden
}

.mainmenubutton{
border:0;
background:transparent;
margin:0;
padding-left:5px;
padding-right:5px;
outline:none
}

.xuonlinenumbah{
color:#007cdb;
}

form{
margin:0;
padding:0
}

select{
font-size:10px;
height:21px;
border:1px solid gray
}

.emailicon{
background:url(http://socialneko.com/image/email.png) no-repeat !important;
padding-left:20px !important
}

input[type=text],input[type=password]{
background:#f8f8f8;
border-radius:3px;
border:1px solid #555;
padding:2px;
font-size:11px;
width:51.5%;
height:19px;
outline:0;
box-shadow:1px 1px 3px #888
}

input[type=password]:focus,textarea:focus,input[type=text]:focus{
background:#fff;
box-shadow:0 0 4px #007cdb;
border:1px solid #007cdb
}

.form_button,.button,#submitmsg{
color:#FFF;
background:#0090ff url(http://socialneko.com/themes/1.button.png) repeat-x;
background:-moz-linear-gradient(top,#0090ff 0%,#0078d4 100%);
background:-webkit-gradient(linear,left top,left bottom,color-stop(0%,#0090ff),color-stop(100%,#0078d4));
background:-webkit-linear-gradient(top,#0090ff 0%,#0078d4 100%);
background:-o-linear-gradient(top,#0090ff 0%,#0078d4 100%);
background:-ms-linear-gradient(top,#0090ff 0%,#0078d4 100%);
background:linear-gradient(to bottom,#0090ff 0%,#0078d4 100%);

border:1px solid #003372;
border-radius:3px;
font-family:"Arial",Helvetica,Times New Roman;
font-size:11px;
letter-spacing: 0.6pt;
text-align:center;
text-shadow:-1px -1px 0 #003856, 1px -1px 0 #003856, -1px 1px 0 #003856, 1px 1px 0 #003856;
box-shadow:inset 0 1px 0 0 #42B0FF;
margin:2px;
padding:5px 12px;
outline:none
}

.form_button:hover ,.button:hover,#submitmsg:hover{
background:#0090ff
}

.form_button:active,.button:active,#submitmsg:active{
background:#006cbf;
background:-moz-linear-gradient(top,#006cbf 0%,#0090ff 100%);
background:-webkit-gradient(linear,left top,left bottom,color-stop(0%,#006cbf),color-stop(100%,#0090ff));
background:-webkit-linear-gradient(top,#006cbf 0%,#0090ff 100%);
background:-o-linear-gradient(top,#006cbf 0%,#0090ff 100%);
background:-ms-linear-gradient(top,#006cbf 0%,#0090ff 100%);
background:linear-gradient(to bottom,#006cbf 0%,#0090ff 100%);
border:1px solid #3B0084
}

/* chat stuff */
#chatbox.chatbox{
background:#fff;
border:5px solid lightblue
}

#touchscreen{
background:lightblue;
border-bottom:1px solid #363636
}

#touchscreen a:hover{
color:#007cdb
}

textarea{
background:#f8f8f8;
border:1px solid #555;
border-radius:6px;
font-size:10px;
outline:0;
margin:auto;
padding:5px;
height:85px;
width:95%;
box-shadow:1px 1px 3px #888
}

textarea:focus{
background:#fff
}

.table{
width:100%;
float:left
}

table,tr,td,th{
padding:0;
margin:0;
font-size:12px
}

td{
background:#fff;
border-bottom: 1px solid #888
}

td a{
color:#0048a8
}

.inner{
background-image:url(http://www.mediafire.com/convkey/8f95/ojhje9mlad04oj1zg.jpg);
padding:4px;
margin:2px;
border:0px solid #AAA
}

#compose,#reply{
display: none
}

div.backbtn{
background:url(http://socialneko.com/themes/1.backbtn.png) repeat-x;
background:-moz-linear-gradient(top,#FFF 0%,#d3d3d3 100%);
background:-webkit-gradient(linear,left top, left bottom,color-stop(0%,#FFF),color-stop(100%,#d3d3d3));
background:-webkit-linear-gradient(top,#FFF 0%,#d3d3d3 100%);
background:-o-linear-gradient(top,#FFF 0%,#d3d3d3 100%);
background:-ms-linear-gradient(top,#FFF 0%,#d3d3d3 100%);
background:linear-gradient(to bottom,#FFF 0%,#d3d3d3 100%);
border:1px solid #AAA;
border-radius:5px;
margin:auto;
text-align:center;
font-size:12px;
font-family:cursive;
width:95%;
transition:all .5s ease-in-out
}

div.backbtn:hover{
border: 1px solid #0063C6
}

.tinyavatar{
border:1px inset #888;
float:left;
height:30px;
width:30px;
margin:2px
}

.commenttd{
background:#ddd; border-top:1px solid #888
}
.comment{
width:97.2%;
overflow:hidden;
word-wrap:break-word;
text-align:left;
background:#eee;
border:1px solid #888;
clear:both;
padding:3px
}

#status{
width:93%;
background:#A6B9FE;
background:-moz-linear-gradient(top,#00E56A 0%,#ededed 100%);
background:-webkit-gradient(linear,left top,left bottom,color-stop(0%,#00E56A),color-stop(100%,#00E56A));
background:-webkit-linear-gradient(top,#00E56A 0%,#00E56A 100%);
background:-o-linear-gradient(top,#00E56A 0%,#00E56A 100%);
background:-ms-linear-gradient(top,#00E56A 0%,#00E56A 100%);
background:linear-gradient(to bottom,#00E56A 50%,#00E56A 50%);
border:1px solid #666;
border-radius:5px;
margin:auto;
overflow:hidden
}

#status a{
color: #0048a8;
font-weight:bold
}

#status a:hover{
color:#9EC100
}

#status_text{
margin:2px;
   background: #00E56A;
word-wrap:break-word
}

.ava{
border:1px solid #007cdb;
width:60px;
   box-shadow:0 0 5px #000;
height:60px;
overflow:hidden;
padding:0;
margin:0;
transition:border .5s ease-in-out
}
.ava:hover{
border:1px solid #9EC100
}

#userinfo{
width:100%;
   box-shadow:0 0 5px #000;
overflow:hidden;
background-image:url(http://www.mediafire.com/convkey/bb87/as1uv1mmksesbguzg.jpg);
border:1px solid #777;
text-align:center;
font-weight:bold
}
#userinfo a{
color:#007cdb;
font-family:pixel;;
font-size:1.2em;
text-shadow:1px 1px 0 #000;
transition:color .5s
}
#userinfo a:hover{
color:#9EC100
}

hr{
border:0;
height:0;
border-top: 1px solid #888;
border-bottom: 1px solid #FFF
}

input[type=checkbox]{
border:1px solid silver;
border-radius:2px;
box-shadow:inset 0 2px 1px -1px rgba(0,0,0,.2)
}

#topicname{
color:#fff;
font-weight: bold;
background:url(http://socialneko.com/themes/1.h2.png) repeat-x;
border:1px solid #0062AD;
border-top-right-radius:3px;
border-top-left-radius:3px;
width:99.4%
}

#forums{
background:#FFF;
border:1px solid grey;
width:95%;
border-radius:5px;
margin:auto;
text-align:center;
word-wrap:break-word
}

.forum1 ,.askme_a{ /* brighter color */
background: #eee;
border-bottom: 1px solid #888;
border-top: 1px solid #fff;
}
.forum2 ,.askme_q{ /* darker color */
background:#ddd;
border-top:1px solid #fff;
border-bottom:1px solid #555
}

.leaderboardavatar{
border:1px solid #007cdb
}

.leaderboardavatar:hover{
border:1px solid #9EC100
}

.spacing{
margin-top:5px
}

.yolo{
text-align:center
}

/* stuff that share the same gradient */
H1,H2,#topicname{
background:-moz-linear-gradient(top,#0090ff 0%,#0066ba 100%);
background:-webkit-gradient(linear,left top,left bottom,color-stop(0%,#0090ff),color-stop(100%,#0066ba));
background:-webkit-linear-gradient(top,#0090ff 0%,#0066ba 100%);
background:-o-linear-gradient(top,#0090ff 0%,#0066ba 100%);
background:-ms-linear-gradient(top,#0090ff 0%,#0066ba 100%);
background:linear-gradient(to bottom,#0090ff 0%,#0066ba 100%);
}

/* Validation Messages */
.successmsg,.errormsg,.infomsg,.warnmsg{
width:95%;
text-align:center;
margin:-8px auto 8px auto;
padding:6px 0 6px 0;
border-radius:3px
}
.closemsg {
float:right;
margin-right:2px;
margin-top:-4px
}
.successmsg{
color:#478900;
background-color:#DFF2BF;
border:1px solid #8AD845
}
.warnmsg{
color:#9F6000;
background-color:#FEEFB3;
border:1px solid #CEA100
}
.errormsg{
color:#D8000C;
background-color:#FFBABA;
border:1px solid #FF0000
}
.infomsg{
color:#00529B;
background:#BDE5F8 url(http://socialneko.com/image/info.png) no-repeat left center;
background-position:3px 2px;
border:1px solid #006EC9
}

hr{
height:1px;
border:none;
background:#ccc;
margin:2px auto
}

.emoji{
height:16px;
width:20px;
display:inline-block;
vertical-align:top;
background-image:url(http://socialneko.com/image/emoji.png);
background-repeat:no-repeat
}
.emoji_1{background-position:0 0}
.emoji_2{background-position:-21px 0}
.emoji_3{background-position:-42px 0}
.emoji_4{background-position:-63px 0}
.emoji_5{background-position:-84px 0}
.emoji_6{background-position:-105px 0}
.emoji_7{background-position:0 -17px}
.emoji_8{background-position:-21px -17px}
.emoji_9{background-position:-42px -17px}
.emoji_10{background-position:-63px -17px}
.emoji_11{background-position:-84px -17px}
.emoji_12{background-position:-105px -17px}
.emoji_13{background-position:0 -34px}
.emoji_14{background-position:-21px -34px}
.emoji_15{background-position:-42px -34px}
.emoji_16{background-position:-63px -34px}
.emoji_17{background-position:-84px -34px}
.emoji_18{background-position:-105px -34px}
.emoji_19{background-position:0 -51px}
.emoji_20{background-position:-21px -51px}
.emoji_21{background-position:-42px -51px}
.emoji_22{background-position:-63px -51px}
.emoji_23{background-position:-84px -51px}
.emoji_24{background-position:-105px -51px}

/* --------------------- spritesheet --------------------- */
.icon{background:url(http://www.mediafire.com/convkey/d3cf/m3yckq3ijlqq8lgzg.jpg) no-repeat;display:inline-block}

.wh9{width:16px;height:16px}
.wh8{width:16px;height:16px}
.wh12{width:16px;height:16px}
.w16h13{width:16px;height:16px}
.wh16{width:16px;height:16px}
.w11h12{width:16px;height:16px}
.w12h11{width:16px;height:16px}

.mail{background-position:0 0}
.coin{background-position: 17px 0px}
.notif{background-position:-32px 0}
.offline{background-position:-48px 0}
.online{background-position:-64px 0}
.delete{background-position:-80px 0}
.edit{background-position:-96px 0}
.lock{background-position:-112px 0}

.arrow-left{background-position:-16px -16px}
.arrow-right{background-position:-16px -16px}
.arrow-left-disabled{background-position:-0 -32px}
.arrow-right-disabled{background-position:-0px -32px}

.refresh{background-position:-32px -16px}
.switch{background-position:-48px -16px;height:16px}

.sticky{background-position:-64px -16px}
.refresh2{background-position:-32px -32px}

.triangle-left{background-position:-48px -32px}
.triangle-right{background-position:-64px -32px}

.g_comment{background-position:-80px -32px;height:8px;width:8px}
.g_favorite{background-position:-96px -32px}
.g_star{background-position:-112px -32px}
.g_artist{background-position:-66px -65px;width:13px;height:15px}
.g_star2{background-position:-80px -64px}

.thumbsup{background-position:-16px -64px}
.thumbsdown{background-position:-32px -64px}

.alert{background-position:-48px -64px}
.scroll-top{background-position:0 -64px;width:16px}

/* EXTERNAL LINKS SPRITESHEET */
.external-link{background:url(http://socialneko.com/image/external-links) no-repeat;width:148px;height:20px;display:inline-block;margin-bottom:2px}
.facebook{background-position:0 0}
.tumblr{background-position:0 -20}
.twitter{background-position:0 -40}
}
