css:
@-moz-document url-prefix("http://reddit.com/r/DayZ"), url-prefix("https://reddit.com/r/DayZ"), url-prefix("http://www.reddit.com/r/DayZ"), url-prefix("https://www.reddit.com/r/DayZ") {
.arrow {
    visibility: visible;
}

.likes.score {
    font-weight: bold;
}

.newcomments .usertext-body {
    background: #ffc;
}

.newcomments p, .newcomments .tagline a {
    color: black;
}

.side p+p+p+table+table {
    font-size: 0.9em;
}

#header .tabmenu {
    top: 53px;
}

#header-bottom-left {
    height: 60px ;
}

#header-img-a {
    top: -26px;
}

.Post__commentContainer {
    color: transparent;
}

.Post__commentContainer .Post__time {
    color: #a5a4a4;
}

.RES-keyNav-activeElement .md-container {
    background-color: rgba(255,255,255,0.04);
}

.comment .md {
    color: #ccc;
}

.comment .md blockquote {
    color: #aaa;
}

.comment .md p>a:visited {
    color: #6D95CA;
}

.content .comment {
    border-color: #444;
}

.content .comment .comment {
    background: #333 ;
}

.content .comment .comment .comment {
    background: #393939 ;
}

.content .comment .comment .comment .comment {
    background: #3D3D3D ;
}

.content .comment .comment .comment .comment .comment {
    background: #424242 ;
}

.content .comment .comment .comment .comment .comment .comment {
    background: #474747 ;
}

.content .comment .comment .comment .comment .comment .comment .comment {
    background: #4B4B4B ;
}

.content .comment .comment .comment .comment .comment .comment .comment .comment {
    background: #4e4e4e ;
}

.content .comment .comment .comment .comment .comment .comment .comment .comment .comment {
    background: #555 ;
}

.content .comment .comment .comment .comment .comment .comment .comment .comment .comment .comment {
    background: #595959 ;
}

.entry.res-selected, .entry.res-selected .md-container {
    background-color: transparent;
}

.flair-rainbow {
    animation: none;
}

.md pre {
    background: none;
}

.new-comment .usertext-body {
    background: #181818;
    border: 1px solid #333;
}

.pagename a {
    top: 16px;
    left: 0;
}

.score.unvoted, .score.likes, .score.dislikes, .userkarma, .karma-breakdown, span.karma, .linkinfo {
    display: none ;
}

.tagline a {
    text-shadow: 1px 1px 1px rgba(0,0,0,0.4);
}

.thing a.title {
    color: white;
}

.thing a.title:visited {
    color: #b9b9b9;
}

.thing, .content .comment, .usertext.border .usertext-body {
    background: #292929 ;
}

.titlebox blockquote:first-child {
    top: 47px;
}

.titlebox blockquote:nth-of-type(1) ul:nth-of-type(3) {
    background: none;
}

.usertext-edit textarea {
    background: #333;
    color: #ccc;
}

a #header-img {
    display: none;
}

a.author {
    color: #93b7e2;
    font-size: 1.2em;
    font-weight: normal;
}

div.titlebox .md h4 {
    max-width: 100%;
    text-indent: 10px;
    top: 84px;
}

span.score {
    color: #1dd;
}
}
