@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("startpage.com"), domain("ixquick.com") {

/*Hidden*/

.sponsored, .head2, .midline_right, #settings, .enhanced_by_td, .cloud
  {
    display: none;
  }
  
/*** Home ***/

body {
  background: none !important;
}

