/* Results */
  
.desc {
  font-size: 14px !important;
}
  
#results .result {
  max-width: 650px !important;
  padding: 8px 10px 10px 12px !important;
}
 
#results .result p.ads_desc_stuff_normal {
  margin: 1px 0px 0px;
}
  
#bottom-result-container {
  margin-left: 165px;
}
  
.side_list_menu li a, #head_left a, .slide-wrapper ul li a, #results .result h3 a, .slide-wrapper, .classified .relatedlist_wrap p a, #bookmark_tip, p.sitelinks a {
  color: #2654A7 !important;
}
  
p.sitelinks a:visited, .sellerrating a:visited, div.result h3 a:visited span {
  color: #76407B !important;
}
  
