}
  
div#bottom-result-container div#pagenavigation {
  max-width: 650px !important;
}
  
/* Side */
.navbar-static-side{
  right:0px;
}
  
div#side_bar2 {
  margin-top: -45px;
  text-align: right;
  float: right !important;
  margin-right: -15px !important;
}
  
div#side_bar2 ul.side_list_menu,
div#side_bar2 ul.location_filter {
  margin-left: 0px !important;
}
  
  
/*** Images ***/
  
#pics-multimedia-div .wrapper-dropdown, #video-multimedia-div .wrapper-dropdown {
  margin-top: -37px;
}
  
.wrapper-dropdown {
  border: 1px solid #bbb !important;
} 
  
.wrapper-dropdown::after {
  border-color: #888 transparent !important;
}
 
.wrapper-dropdown .dropdown {
  border-radius: 0px !important;
  border: 1px solid #ddd !important;
}
  
.wrapper-dropdown, .wrapper-dropdown .dropdown {
  background: none repeat scroll 0 0 #eaebed !important;
}

.wrapper-dropdown .any-span {
  color: #777 !important;
}

.wrapper-dropdown .dropdown li a {
  color: #777 !important;
}
  
  
/*** Videos ***/
  

  
/*** Advanced Search ***/
  
div.button-container input#search-btn {
  border-radius: 0px !important;
}
  
div.wrapper-dropdown {
  border-radius: 0px !important;
}
  
/*** Old site ***/
  
body {
  background: none !important;
  background-image: none !important;
  background-color: #fff !important;
}

#enhanced, a#detail, div#tip, div#sponsored_container, p.head2 {
  display: none !important;
}
  
input#query_top, input#submit1, input#submit, input#query, input#query_bottom {
  border-radius: 0px !important;
  box-shadow: none !important;
}
  
div#content a#main_link p#caption {
