  width: 0px !important;
  height: 110px !important;
  background: url(https://new.startpage.com/graphics/no_tm_startpage_retina_logo.png) no-repeat !important;
  background-size: 100% !important;
  padding-left: 355px !important;
}
  
img[src*="/startpage_logo_res.gif"] {
  width: 0px;
  background: url(https://new.startpage.com/graphics/no_tm_startpage_retina_results_page_logo.png) no-repeat !important;
  height: 110px !important;
  padding-left: 200px;
  background-size: 80% !important;
}
  
}

@-moz-document domain("ixquick-proxy.com") {

.navbar-default{
  background-color: #EAEBED;
  background-image: none !important;
}
 
.search-field, .small-go-btn {
  border-radius: 0px;
}
  
.wrapper-english
#results_header
.proxy-bar
.proxy-note {
  opacity: 0.4 !important;
}
  
.proxy-lm {
  display: none !important;
}
  
.small-go-btn {
  min-width: 60px;
  width: 80px;
  border-radius: 0px;
}
  
.text-fields {
  padding-right: 105px !important;
}
}

