css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("forum.greenheartgames.com") {


img#site-logo.logo-big{
  width: 148px !important;
  height: 40px !important;
  background: url("data:image/png;base64,")!important;
}

.d-header .icons .icon{
  color: white
}

.d-dropdown{
  background-color: lightgray !important;
}

div#main-outlet{
  background-color: black !important;
  color: white !important; 
}

.d-header{
  background-color: black !important;
}

header#ember1131{
  background-color: black !important;
}

.topic-list th {
  color: white !important;
  font-weight: bold !important;
}

.topic-list{
  background-color: gray !important;
}

div.row{
  background-color: black !important;
  color: white !important;
}

table#ember1299{
  background-color: gray !important;
}

.topic-list>tbody>tr:nth-child(odd){
	background-color:black !important;
  color: #fff 
}

.topic-list>tbody>tr:nth-child(even){
	background-color:gray !important;
}

.topic-list a.title {
    color: white !important;
    font-weight: bold !important;
}

div.topic-excerpt {
  color: #DFDFDF !important;
}

button#create-topic {
  background-color: white !important;
  color: black !important;
  font-weight: bold !important;
}

/*Topic Buttons (New, Unread, etc)*/
.list-controls .category-breadcrumb li.bar > .badge-category{
  background-color: white !important;
  color: black !important;
  font-weight: bold !important;
}

.nav-pills > li > a{
  color: white !important;
  font-weight: bold !important;
}

.nav-pills>li>a:hover {
    color: white;
    background-color: gray;
}
  
.nav-pills > li.active > a, .nav-pills > li > a.active{
  background-color: white !important;
  color: black !important;
  font-weight: bold !important;
}

.badge-notification.new-topic{
  font-weight: bold !important;
}

/*Assorted Stats (Category, Views, Replies, etc)*/
th.default{
  color: white !important;
  font-weight: bold !important;
}

th.category.sortable {
  color: white !important;
  font-weight: bold !important;
}

th.posters{
  color: white !important;
  font-weight: bold !important;
}

th.posts.sortable.num{
  color: white !important;
  font-weight: bold !important;
}

th.views.sortable.num{
  color: white !important;
  font-weight: bold !important;
}

th.activity.sortable.num{
  color: white !important;
  font-weight: bold !important;
}

.topic-list .avatar.latest:not(.single){
  box-shadow: 0px 0px 6px 1px #4EABDA !important;
}

.heatmap-med a{
  color: lightblue !important;
}

.heatmap-low a {
  color: khaki !important;
}

.topic-list td{
  color: white !important;
}

.topic-list .fa-thumb-tack{
  color: white !important;
}

body .heatmap-high{
  color: orangered !important;
  font-weight: bold !important;
}
/*Badges*/

.badge-wrapper.bar span.badge-category{
  color: white !important;
}

.topic-list.categories .category h3 a[href]{
  color: white !important;
}

/*Opened Topic*/

.extra-info-wrapper .topic-link {
  color: white !important;
}

.extra-info-wrapper .topic-statuses i {
  color: white !important;
}

.extra-info-wrapper .badge-wrapper.bar span.badge-category {
  color: white !important;
}

#topic-title h1 a{
  color: white !important;
}

.names span a{
  color: white !important;
}

.names .fa{
  color: white !important;
}

.names .new_user a, .names .user-title, .names .user-title a{
  color: white !important;
  font-weight: bold !important;
}

.moderator .topic-body {
  background-color: darkgrey !important;
}

aside.quote .title{
  background-color: darkgray !important;
}

blockquote {
  color: whitesmoke !important;
  background-color: darkgray !important;
}

.modal .modal-body p {
  background-color: black !important;
  color: white !important;
}

.modal-inner-container{
  background-color: gray !important;
  color: white !important;
}

.modal.history-modal #revision-details{
  background-color: lightgrey !important;
  color: white !important;
  font-weight: bold !important;
}

/*Profile*/

.nav-stacked .active > a{
  background-color: white !important; 
  color: black !important;
  font-weight: bold !important;
}

.btn-danger {
  color: black !important;
  background-color: white !important;
  font-weight: bold !important;
}

.user-main .about .secondary dt{
  color: black !important;
  font-weight: bold !important;
}
 
.badge-notification.clicks{
  font-weight: bolder !important;
  color: black !important;
}
 
}
