@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("mail.live.com") {


html,
.leftnav, .LeftNav, .SortFilterItems,
.c-MessageListHeader, .SortFilterContainer, .t_mbgc,
.uxp_ftr_control td,
.SearchBox .c_search_mc ul,
.fromAddrMenu ul, .selCount, .clearAllCk,
.ComposeHeader, .t_lnksi, #selection,

.t_sbgc .t_s_hov:hover, .t_s_hov:hover, .t_s_hov:hover .t_s_hovh, .t_s_sel, .t_s_h,

.t_blci, .ecxt_blci, .t_mlli .t_sel, .t_mlli .t_sel .t_s_hovh
{
background:#111111 !important;
}

.c-SizerBar.sizerBarY.masterSizerBar,
.c_search_mc, .c_search_psb_f,
.cp_inputContainer div, .cp_inputContainer textarea,
.SubjectArea .fSubject, #c_h_theme_m,
.filtersContainer .c_m, #c_copt,
.MenuTop, .SortFilterItems ul
{
background:#333333 !important;
}

.selected
{/* Red highlight */
background:#E20000 !important;
}

.indentedleftnavlist li .leftnavitem:hover,
.mailListPane li:hover,
.SearchBox .c_search_mc ul li a:hover,
.fromAddrMenu ul li a:hover,
.cp_Contact a, .ButtonList ul,
.filtersContainer .c_m li .menuLinkItem:hover,
#c_copt li a:hover, .MenuTop li a:hover,
.SortFilterItems li .c_m a:hover,

/* Options Start */
#OptionsSignature .ButtonList 
{/* Blue highlight */
background:#2072B9 !important;
}
.filtersContainer .c_m li .menuLinkItem .g_bul,
#c_copt li .is_img
{/* Light grey background */
background:#8E8E8E !important;
}
.mailListPane li:hover .ia_iw
{
background:#2072B9 !important;
}
.mailListPane li:hover .ia_iw img
{
background-color:#FFFFFF !important;
}
#RightRailContainer
{
display:none !important;
}

#messageListControl80fmessageListContainer
{
width:116% !important;
}

#inboxControl0fv-ReadMessageContainer
{
width:114% !important;
}

.PageNavigation 
{/* bottom navigation */
position:relative !important;
right:700px !important;
}

.SearchBox .c_search_mc ul,
#ComposeRteEditor,
.fromAddrMenu ul, .SubjectArea .fSubject,
.ContactHeader .cp_inputArea,

.filtersContainer .c_m, #c_copt,
.MenuTop, .SortFilterItems ul

{ /* Border light grey */
border:1px #8E8E8E solid !important;
}

.ReadMsgFrom, #ComposeRteToolbar
{
border-left:1px #FFFFFF solid!important;
border-bottom:1px #FFFFFF solid!important;
}

.mailListPane li
{
border-bottom:1px #8E8E8E dotted !important;
}
.c-MessageListHeader .mlRule
{
border:none !important;
}

::-moz-selection 
{/* highlight selection */
background: #E20000 !important; 
}
.MessageListBottomTextInner a
{
color:#FFFFFF !important;
text-decoration:underline !important;
}
input,
.leftnavitem, .lnav_item, .lnav_topItemLabel,
.Lt span, .c-MessageRow span a, .Dt,
.MsgPropertiesWrapper span, .MessageHeaderItem a,
.PageNavigationMsgRange, 
.uxp_ftr_left li span, .uxp_ftr_left li a,
.SearchBox .c_search_mc .c_search_contact,
.SearchBox .c_search_mc strong, .toBtn,
.SearchBox .c_search_mc .c_m_lastchild a,
.SearchBox .c_search_mc .c_search_txt, .DraftArea p,
.SearchBox .c_search_mc .c_search_sub_heading,
.fromAddrMenu ul li .Name, .fromAddrMenu ul li .Address,
.FromContainer .Name, .FromContainer .Address,
.cp_Contact .cp_name, .cp_inputArea textarea,
.CL_Display_Name, #selection, .clearAllCk a,
.RichText, .PlainText, .HTML, /* <<< Editor default text color */
.filtersContainer .c_m li .menuLinkItem,
#c_copt li a, #c_copt li, .rp, .t_atc,
.UserContent, .MenuTop span, .MenuTop a,
.SortFilterItems li a, .rmSubject,

