.MainOptionsControl h3,
.BreadCrumb .ToolBarItem span,
.MainOptionsTitle, .PrimaryTextColor,
.MainOptionsList li a, .cOptionsRadio label,
.MainOptionsControl h3 .cOptionsBody form h3,
#OptionsSignature .SubsectionPadding,
#OptionsSignature .SectionPadding
{
color:#FFFFFF !important;
text-decoration: none !important;
}
/* Options start */
.MainOptionsControl a:hover
{
color:#FFFFFF !important;
text-decoration: underline !important;
}
#notificationContainer .OuterContainer
{/* Notifications */
background:transparent !important; 
}
#notificationContainer .OuterContainer .InnerContainer
{/* Notifications */
color:#FFFFFF !important;
background-color:#164972 !important; 
border:1px #FFFFFF solid !important;
}
.RichText, .PlainText, .HTML,
#ComposeRteEditor
{/* Create email styling */
padding:5px !important;
background-color:#000000 !important; 
}

.ExternalClass,
.ExternalClass a,
.ExternalClass td,
.ExternalClass span
{/* Recieved email styling */
color:#FFFFFF !important;
background-color:#444444 !important; 
text-decoration:none!important;
padding:5px !important;
}

}
