css:
@-moz-document domain("mangafox.la") {
body, .ProfileCanopy-header, #page-container, #page, .manga, #advertisment, #header, #footer, #tip
  {background: black !important;}

ul#menu li a, .warning {
    background: black !important;
    border: 1px #444 solid !important;}

#corner a, #bookmark ul .title a, #updates .title a:hover, #updates .title a, #updates .title a:hover, #popular h2, span.updatedch, #title a:hover, #footer a, #title h1, #discussion h6, #chapters h2, #discussion a.forum, .chlist h3 a.tips.highlight, #related li a, .tips p a, span.releases a, span.releases a:hover, #content h1, ul#menu li a.selected, #related h3 a, a#comments:hover, a#bookmark:hover, #tool #series *, #warning_extra a, .chlist .highlight, .tplist .highlight
  {color: #d2d2d2 !important;}

ul#menu li a:hover {
  color: #d2d2d2 !important;
  background: #222 !important;}

#popular a.top, #popular a:hover, .chlist, .highlight, .chlist h3 a, #title h2, #title a, #footer, .chlist span.title.nowrap.highlight, .tips h2, .tips p, .tips h3, .releases, .page #top_center_bar form, #nav li a, .warning, .page select, .chlist a, .tplist .topic .title a
  {color:gray !important;}

.page a.btn span, #sidebar {display:none !important;}

.page a.btn {
  -moz-appearance: none !important;
  background: linear-gradient(#333, #000) !important;
  border: 1px #444 solid !important;
  color: #999 !important;}

#tip {border:none !important;}

#footer {display: none !important;}
}

@-moz-document domain("mangapanda.com") {
#bodyalt, #chapterlist, #readmangasum, h2, #popularlist, #sec_navigation, .top, body, #wrapper_body, .ProfileCanopy-header, #page-container, #page, .manga, #advertisment, #header, .note, #wrapper_footer
  {background: black !important;}

#recom_info, #recom_input
  {background: #494949 !important;
  border: none !important;
  color: black !important;}

tr.c5 {border: none !important;}

.note{
  border: none;
  color: gray}

.latesthotimages {
  background: black !important;
  border: 1px white solid !important;
  color: gray !important;
  box-shadow: none !important;}

#top_navigation li {
    background: black !important;
    border: 1px #444 solid !important;}

#top_navigation a:hover {background: #222 !important;}

table {border-collapse: unset !important;}

.prev a, .next a {background: #444 !important;}

#bodyalt, .prev a, .next a {
  box-shadow: none !important;
  border-top-color: #444}

#sec_navigation a {text-decoration-color:black}

.chapters, .updates a.chaptersrec, .updates a, #latestchapters h2, #readmangasum p, .chapternav, .chapternav_next, #mangainfo_bas a:link, #mangainfo_bas a:visited, #mangainfo_son a, #mangainfo_son a:link, #mangainfo_son a:visited, #mangainfo_bas a, body, #related, #related_inner a, h1, span.c1, #mangainfo a, #listing, .latesthotimages a
  {color: gray}

span.c7 a:hover, .updates a.chaptersrec:hover
  {text-decoration: underline !important;}

#sec_navigation a:hover
  {border-bottom: 2px solid #d2d2d2 !important;}

#latestchapters h3, .popularitemcaption, #popularcaption h2, #readmangasum h2, #listing th, #listing a, strong, #emailinput, .latesthotimages a:hover, span.c7 a
  {color: #d2d2d2}

#wrapper_footer, #bodyust, #popularmangas, #latestchapters a img
  {display: none !important;}
}
