.scroll_fix,#page_body{
  		  background-color: #cac !important;
    	background-image:url("http://cs623428.vk.me/v623428030/224a9/CUr-HDR-ECo.jpg");
  		/* put your url for background */
}
#feed_wall .wall_module ,.summary_wrap ,.summary_wrap .fl_r,.feed_info_bar,#feed_new_posts,#profile ,.no_lists,#content,#photos_container{
  background-color: #ccf !important;
  /*wall color*/
}
.wall_text
{
  background-color: #ac9 !important;
		direction: rtl;
		font-family: "Times New Roman", Times, serif;
		font-size: 16px;
		text-align: justify;
  /*posts color*/
}
.reply_fakebox_wrap,.reply_box{background-color: #ac9 !important;}
.reply ,.reply_dived ,.clear , .reply_replieable,.reply_field,.reply_fakebox
{
  background-color: #aaf !important;
		font-family: "Times New Roman", Times, serif;
		font-size: 14px;
		text-align: justify;
  /*comments color*/
}
.wall_reply_text,.reply_field,.feedback_reply_field 
{
		direction: rtl;
		font-family: "Times New Roman", Times, serif;
		font-size: 14px;
		text-align: justify;
}
.feedback_row_answer
{
  background-color: #afa !important;
		font-family: "Times New Roman", Times, serif;
		font-size: 14px;
		text-align: justify;
}
 .wall_module .online{
  color:#ffa;
  background-color:#aaf;
}
.mob_onl,.page_fronly {
  background-color:#ffa;
}
.post_like {  background-color: #0ff !important;color:#f00;/*like color*/}
.my_like{  background-color: #ff0 !important;color:#00f;/*liked color*/}
.post_share {  background-color: #f44 !important;color:#0bb;/*shared color*/}
.wall_module .no_shares {  visibility: visible;
  background-color: #fbb !important;color:#0ff;/*share color*/}
span .reply_link{  background-color: #9f9 !important;color:#707;/*comment color*/}
._reply_lnk{  background-color: #7d7 !important;color:#929;/*reply color*/}
}
