css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("inoreader.com") {

[id^=article_stripe] {
    position: absolute;
       left: 3px;
       top: 7px;
    height: 13px;
    width: 13px;
    background-color: transparent !important;
    border: solid 1px #000 !important;
    border-radius: 500%;
}

[id^=article_stripe]:after {
    content: '';
    position: absolute;
       top: 2px;
       left: 2px;
    display: block;
    height: 9px;
    width: 9px;
    background-color: #000;
    border-radius: 500%;
}

[id^=article_stripe].article_stripe_read:after {
    display: none;
}

#sb_rp_upgrade_button,
div#consent_bbb.alert_state.green_state.top_notification {
    display: none !important;
}
}
