css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("badoo.com") {
  
.page-section {
    background-color: #2E3444 !important;
}
    
div.page-section--spotlight {
    background-color: #15213B !important;
}

    
.footer-import {
    background-color: #15213B;
}
    
.messenger-footer {
    border-top: none !important;
}
    
.hd {    
    background-color: #2E3444;
}

    
    
.sideblock--stroke, .pageblock--stroke, .user-card__attr, div.sidebar {
    background-color: #434B5D !important;
    border-color:  #806F69 !important;    
    color: #806F69 !important;
}

.user-header__username {
    color: #ccc !important;
    
}

.user-card__section {
    background-color: #434B5D !important;
    border-color:  #806F69 !important;    
    color: #806F69 !important;
    border-top: none !important;
    border-right: 1px solid #806F69 !important;
    border-left: 1px solid #806F69 !important;
}
    
.user-card__section a,.p-link, a.p-link, .btn {
color: #ccc !important;    
}
    
div.user-card__attr, div.user-card__verify, div.user-card__distance {
  border-width: 0px !important;
}
    
.intr:not(active) {
color: #ccc !important;    
}
    
.intr.active .intr-txt {
color: #eee !important;
font-weight: bold !important;
}
    
.intr, .btn--grey {
    background-image: none !important;
    background-color: #575E70 !important;
    border-color:  #806F69 !important;
    text-shadow: none !important;
}

.page_search .hml {    
    background-image: none !important;
    background-color: #677385 !important;    
    border-color:   #344052 !important;
    border: none !important;
    color: #ccc !important; 
}
    
.page_meets .hme {    
    background-image: none !important;
    background-color: #677385 !important;    
    border-color:   #344052 !important;
    border: none !important;
    color: #ccc !important; 
    
}
    

.messenger-header, div.type-wrap, div.s_c_wr {
    background-color: #677385 !important;
}

    
.user-header--messenger {
    border-bottom: none !important;
}
    
.user-photo img {
    border-radius: 0 !important;
}

.user-photo img, img.userpic_image {
    border: 1px solid #ccc !important;    
}
    
.messages-list {
    top: 0px !important;
}
    
#correspondence {
    background-color: #2E3444 !important;
    color: #ccc !important;;
    
}

.userlist_item:not(current)  {
    background-color: #434B5D !important;    
}


.userlist_item:not(current) .username  {
    color: #ccc !important;          
}

 
.userlist_item.current {
    background-color: #A7997D !important;
    box-shadow: none !important;
    
}
    
  
.userlist_item.current .username {
    color: #fff !important;
}    
    
    
.userlist_item {
    border-bottom-style: solid !important;;
    border-bottom-color: #806F69 !important;;
}
    
.userlist {
    bottom: 0px !important;
    border-right: none !important;
}

div.s_c_wr {
    border-bottom: none !important;
    background-color: #434B5D !important;
    height: 100% !important;
}
    

.ready_to_chat {
    height: 0px !important;
    display: none !important;
}
    
span.folder-name  {
    color: #999 !important;;
}
    
body  {
    color: #000 !important;
    background-color: #2E3444 !important;
}

div.user-card.aside-promo, div.aside-promo.js-aside-promo, div.messenger-banner.js-im-banner {
  display: none !important;
 }
    
div.messenger-banner.js-im-banner {
/*    display: none !important;*/
/*    width: 0% !important;*/
    height: 0% !important;
/*    padding: 0px 0px 0px 0px !important;    */
}

/*
    div.messages-list {
        height: 100% !important;
        
    }
*/  
 

.profile-section h2 {
    color: #fff !important;
}    

    
.section-fields__value {
    color: #806F69 !important;
}
    
.encounters__wrap {
    border-bottom: none !important;
}


.photo-gallery {
    background: #434B5D !important;
    position: static !important;
}

.common-info__count-cnt {
    background: none !important;
}

    
.user-card__attr .dstc {
    color: #C30 !important;
}

.user-card__user-photo {
    /*border-radius: initial !important;*/
    background: none !important;
    border-left: 1px solid #806F69 !important;
    border-right: 1px solid #806F69 !important;
    border-top: 1px solid #806F69 !important;
    width: 178px !important;
}

    .user-card__user-photo .photo {
        width: 178px !important;
    }
 

body.page_meets.geolocation .ovl-frame.ovl-frame--spotlight,body.page_meets.geolocation .ovl-fade {
    display: none !important;
}

body.page_meets.geolocation .ovl {
    z-index: initial !important;
    display: none !important;
}

body.page_meets.geolocation .sl_hl-add {
    border-radius: 0 !important;
    background-color: initial !important;
    
}
    
div.page-section.page-section--headray {
    background: none !important;
}
    

div#sb_spp_button {
    display: none !important;    
}

div.messenger-banner.js-messenger-banner {
    display: none !important;
}

}
