css:
@-moz-document regexp("^https?://([A-Za-z]+\\.)?reddit\\.com/r/thebutton.*$") {
/* Always show flair contents without hovering */
.content .flair {
  max-width: 500px;
  padding: 0 5px;
  color: #fff;
  -webkit-transition-delay: 0s;
  transition-delay: 0s;
}

/* Highlight dirty cheaters */
.flair-cheater:before {
  content:"CHEATER:";
}

}
