css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix(http://www.google),  
               url-prefix(https://www.google)
{
    /*.................background image...................*/

    img#hplogo 
    {
        box-sizing: border-box;
        background: url("http://i.imgur.com/oEhkVNb.png") center no-repeat !important;
        width: 1000px;
        height: 560px;
    }

    div#hplogo
    {
        box-sizing: border-box;
        background: url("http://i.imgur.com/oEhkVNb.png") center no-repeat !important;
        padding-left: 1000px;
        height: 560px;
        padding-bottom: 545px;
        margin-top: -124px;
    }      
  
    /*.................'remove' logo text...................*/

     /*#hplogo {text-indent: -9000px !important; white-space: nowrap;}*/
       #hplogo div {display: none;}  
    
	/*.................special text position...................*/
	
	div#prm-pt
	{
        position: absolute;
        bottom: 20%;
        left: 42.5%;
    }
	
    /*.................remove special elements...................*/
    
    ._P7b, ._aGb, ._bGb {display: none;}
    
    /*.................basic font...................*/
    
    .hp body, .hp td, .hp a, .hp p {font-family: "Comic Sans MS", cursive, sans-serif !important;}    
    .hp ._Gs {color: #ff8c00 !important;}
    
    /*.................background color...................*/
    
   .hp div[id=viewport] {background: linear-gradient(to bottom, #ffffff, #FFE8D1)!important;}
    
    /*...............search bar.................*/
  
   .hp .sbibod
    {
        box-shadow: 0px 0px 16px #FF9933 !important;
        opacity: .8 !important;; 
        border: 0px solid !important;
        margin-top: 56px;
    }
    
    /*...............bar shadow (bottom).................*/
    
    .hp #fbar 
    {
        box-shadow: 0px 0px 16px #FF6633 !important;
        background: #fff6ef !important;
    }
    
    /*.................search buttons...................*/

    input[type="submit"][name="btnI"], input[type="submit"][name="btnK"]
    {
        box-shadow: 0px 0px 16px #FF9933!important;  
        background: linear-gradient(to bottom, #FFD0A7, #FFA7D0)!important; 
        font-family: "Comic Sans MS", cursive, sans-serif !important;
        color: #EE0022 !important;
    }
    
    /*.................search buttons hover...................*/

    input[type="submit"][name="btnI"]:hover, input[type="submit"][name="btnK"]:hover
    {
        box-shadow: 0px 0px 16px #fff !important;  
        background: linear-gradient(to bottom, #FFD0A7, #FFA7D0)!important;
        color: white !important;
    }
    
    /*.................image search button...................*/
    
    .hp .sfsbc
    { 
        opacity: .95; 
        margin-top: 55px;  
    }
    
    /*.................language message...................*/
    
    .hp div[id=_eEe]
    {
        position: absolute;
        bottom: 10%;
        left: 45%;
    }  
}
