css:
@-moz-document domain(www.youtube.com) {

.html5-play-progress, .ytp-play-progress {
   background: url("data:image/png;base64,") repeat-x !important; 
}

.html5-load-progress, .ytp-load-progress {
   background: url("data:image/png;base64,") !important;
}

.html5-scrubber-button, .ytp-scrubber-button {
   background: url("http://i.imgur.com/rS5jg1A.gif") !important;
   width: 50px !important;
   height: 50px !important;
   border: none !important;
   margin-left: -26px !important;
   margin-top: -21px !important;
}
.html5-progress-bar-container, .ytp-progress-bar-container {
   height: 12px !important;
}
.html5-progress-bar, .ytp-progress-bar {
   margin-top: 12px !important;
}
.html5-progress-list, .ytp-progress-list, .video-ads .html5-progress-list.html5-ad-progress-list, .video-ads .ytp-progress-list.ytp-ad-progress-list {
   height: 12px !important;
}
.ytp-volume-slider-foreground:before {
   background: #BFD8F0 !important;
   background: -moz-linear-gradient(left, #1b1b1b 0%, #BFD8F0 100%) !important;
   background: -webkit-gradient(linear, left top, right top, color-stop(0%,#1b1b1b), color-stop(100%,#BFD8F0)) !important;
   background: -webkit-linear-gradient(left, #1b1b1b 0%,#BFD8F0 100%) !important;
   background: -o-linear-gradient(left, #1b1b1b 0%,#BFD8F0 100%) !important;
   background: -ms-linear-gradient(left, #1b1b1b 0%,#BFD8F0 100%) !important;
   background: linear-gradient(to right, #1b1b1b 0%,#BFD8F0 100%) !important;
}
}
