css:
@document domain(mightytext.net) {

/* 등록 오류 때문에 주석처리했으니 살려서 쓰세쇼. 
	@import url('https://cdn.materialdesignicons.com/1.1.34/css/materialdesignicons.min.css'); */

  #share-button-main	{ display: none!important; visibility:hidden; }  
  #heart-mt-button, #alert-bottom-right-corner, #goProWrapper, .shareMTTool 	{ display: none!important; visibility:hidden; }

  #pinnedContent	{ min-width:0!important; width:751px!important; margin:auto; }

  #messageViewToggle	{ position: absolute; left: 0; margin:0; }

  #navBarTabs > .active > a, #navBarTabs > .active > a:hover,
  #singleMessageContainer .contentPanelHeader	{ background-color:#303F9F!important; }
  #singleMessageContainer .contentPanelHeaderText, 
  #singleMessageContainer .contentPanelHeaderNum	{ background-color:#303F9F!important; margin-left: 1em; }

  #singleMessageContainer .messageThread	{ background-color:#E8EAF6!important; }

  .threadItem	{ margin: 1em 20px 1em!important; box-shadow: 0 2px 5px rgba(0,0,0,.25)!important; border:none!important; border-radius:3px!important; padding-top:1em!important; }
  .threadItem:before	{ display:none; }
  .threadItem:after	{ border-width:.5em!important; top:10px!important; }
  .sentText	{ background:#3F51B5!important; color:#fff!important; }
  .sentText:after	{ border-left-color:#3F51B5!important; border-top-color:#3F51B5!important; left:calc(100% - 2px)!important; }
  .receivedText	{ background:#fff!important; color:#212121!important;}
  .receivedText:after	{ border-right-color:#fff!important; border-top-color:#fff!important; right:calc(100% - 2px)!important; }

  .threadItem .timestamp-msg	{ color:rgba(0,0,0,.5); }
  .sentText .timestamp-msg,
  .sentFromIcon:before	{ color:rgba(255,255,255,.5); }

  .sentText + .sentText	{  }
  .sentText + .sentText:not(first-child):after	{  }

  .fa-laptop:before		{ font: 16px 'MaterialDesignIcons'; content:'\f376'!important; }
  .fa-refresh:before		{ font: 16px 'MaterialDesignIcons'; content:'\f45e'!important; }

  .messageAction			{ background:none!important; font:16px 'MaterialDesignIcons'; width:inherit!important; margin: 0 -1em 0 0!important; opacity: 0; color:rgba(0,0,0,.5)!important; transition:.25s; }
  .messageAction:hover	{ text-decoration:none; color:#3F51B5!important; }
.threadItem:hover	.messageAction	{ margin:1em!important; opacity: 1; margin:0 .25em 0 0!important; }
.timestamp-msg	{float:right;}
  .sentText .messageAction	{ color:rgba(255,255,255,.5)!important; }
  .sentText .messageAction:hover { color:#fff!important; }
  .forwardMessageIcon:before	{ content:'\f45c'; }
  .deleteOneMessageIcon:before	{ content:'\f25a'; }
  .unstarred:before	{ content:'\f4b7'; } 	.starred:before	{ content:'\f4b4'; color:gold; }
  .downloadMMS:before		{ content:'\f272'; } .downloadMMS > i:before	{ content:""; }

  .thread-header-tool			{ background:none!important; color:#fff; position:relative; margin:0!important;}
  .thread-header-tool:before	{ position:absolute; top:5px; left:5px; font:24px 'MaterialDesignIcons'; color:#fff; }
  .callContactTool:before		{ content:'\f0d5'; }
  .refreshThreadTool:before	{ content:'\f45e'; }
  .deleteThreadTool:before	{ content:'\f25a'; }

/* reply textarea */
#singleMessageContainer .textResponse	{position: absolute; left:initial; overflow:visible; border:0; box-shadow:0 2px 5px rgba(0,0,0,.25)!important; background:#3F51B5; color:#fff; padding:1em; right: 6em; float:right; width:initial; height:initial; min-width:5em; max-width:75%;} 
#singleMessageContainer .textResponse:after	{ position:absolute; top:0; border: .5em solid rgba(0,0,0,0); content:" "; border-left-color:#3F51B5!important; border-top-color:#3F51B5!important; left:calc(100% - 2px)!important; }

  /* bottom */
  button, .btn, .mightyButton	{ background-color:rgba(0,0,0,0)!important; color:#3F51B5; text-shadow: none; transition:.5s;}
 button:hover, .btn:hover, .mightyButton:hover	{ background-color:#3F51B5!important; color:#fff; }
  .dcattachicon, .new-emoji-selector, .emoticonSelector, .scheduleSelector, .sendButton > div 	{background-image:none;}
  .dcattachicon:before { font: 24px 'MaterialDesignIcons'; content: "\f14d";  position: relative; top: 0.1em; }
  .new-emoji-selector:before { font: 24px 'MaterialDesignIcons'; content: "\f286";  position: relative; top: 0em; }
  .emoticonSelector:before { font: 24px 'MaterialDesignIcons'; content: "\f287";  position: absolute; padding:.1em; }
  .scheduleSelector:before { font: 24px 'MaterialDesignIcons'; content: "\f118";  position: absolute; padding:.1em; }

#singleMessageContainer .responseButtonsHolder	{position:relative; top:-3em; width:initial;}
#singleMessageContainer .sendButton	{border-radius:3em; background-color:#3F51B5!important; padding:1em .9em 1em 1.1em; height: initial; width: initial;}
#singleMessageContainer .sendButton:hover	{background-color:#283593!important;}
#singleMessageContainer .sendButton:before {  font: 24px 'MaterialDesignIcons'; content: "\f488";  position: relative; top: .1em; color:#fff; }
#singleMessageContainer .charsLeft	{font-size:.5em; top:2em; right:-2em;}

.save-draft-button-holder, .templateDropUpIcon	{display:none;}

  /* nav */
  #newSms	{ background-image:none; }
  #newSms:before	{ font: 24px 'MaterialDesignIcons'; content:'\f3a8'; top: 0.3em; position: relative; padding-right: 0.2em; }
  #newSms > img { display:none; }

  .sideBarIcon	{ background:none!important; }
	.sideBarIcon, .iconLabel	{vertical-align: bottom;}
  .sideBarIcon:before	{ font: 30px 'MaterialDesignIcons'; }
  #classicViewIcon:before	{ content:'\f1db'; }
  #photosIcon:before	{ content:'\f1c9'; }
  #eventsIcon:before	{ content:'\f1bb'; }
  #phoneIcon:before	{ content:'\f2aa'; }
  #contactsIcon:before	{ content:'\f103'; }
  #settingsIcon:before	{ content:'\f491'; }
}
