css:
@-moz-document domain("rutracker.org"), domain("rutracker.cr"), domain("rutracker.net"), domain("maintracker.org") {
/*Скрытие рекламы*/
div[style="padding: 5px 0 0;"], .sb2-bg2, #idx-sidebar2 > div:nth-child(4), #bn-bot-wrap, 
img[width="88"][height="31"], div[class="bn-idx"], #adriver-240x120, #bn-top-block [class*="brand-link"] {display:none!important;}

/*Скрытие лого*/
#logo, a.site-logo-link {display:none!important;}
#page_header > div.topmenu {padding-top: 20px!important;}

/*Выравнивание положения меню "Опции показа" */
div#only-new-options.menu-sub {right: 0px!important; left:auto!important; top:0px!important;}

/*Скрытие правой боковой колонки*/
td[style="width: 240px; vertical-align: top; padding-left: 2px;"] {display:none!important}

/*Устранение брэндинга в шапке*/
#page_container {background-image: none!important;}

/*Устранение "последствий" адаптации под брэндинг*/
td.tCenter.pad_2 > div, td.tRight.w50 > div 
{background-color: white!important; border: none!important; font-weight: 500!important; color: #006699!important;}
a.med, a.small, a.gensmall, .branding .topmenu td:last-child div a, .branding .topmenu td:last-child div .a-like {color: #006699!important;}
.branding .topmenu td:last-child div a:hover, .branding .topmenu td:last-child div .a-like:hover {color: #DD6900!important;}
}

@-moz-document domain("rutracker.net") {
/*Обратное отзеркаливание*/
body {transform: scaleX(1)!important;}
}
