css:
@-moz-document domain("rambler.ru") {
.b_promo, .pgSearch, div[class^="b_2012__b"], td.bdr.end.pgBanWorm, .listProjects2, #yandex_ad, div[class^="b_yandex_direct"] {display: none!important;}
}

@-moz-document domain("autorambler.ru") {
div.grid__column.grid__column--aside, div.section.js-offers-mixin, .header__search, 
div[data-specials="js-special-offers-top"], hr.section-devider.gap-top-large {display: none!important;}
.grid__column--main {width: auto!important;}
.sticky~.header__logo-container {position: absolute!important; -webkit-transform: none!important; top: 0!important;}
.b-rambler-topline~.sticky-wrapper .sticky~.header__logo-container {top: 0!important;}
.sticky-wrapper:before {height: 0!important;}
.header__logo {padding: 0!important;}
.article__img {max-width: 860px!important; width:100%!important;}
}

@-moz-document domain("news.rambler.ru") {
/*Удаление мусора*/
ul.cluster-aside__list.j-cluster-aside__promo, .cluster-aside__item.active[data-partner*="begun"], .cluster-aside__item.active[data-adfox], 
.aside-banners.no-tablet, .social.wide, .article__adfox.fix-height, .j-metrics__views-out-ext.related-resources, .article__bottom > .article__adfox, 
.article__bottom > .partners, .j-social.social-top
{display: none!important;}

/*Скрытие социальных кнопок под статьями*/
.social-bottom.wide[data-prefix] > .social-bottom__item, .article__paragraph + .j-social.social-bottom 
{display: none!important;}

/*Общие установки полосы статей*/
.article__inner.wide {margin-right: inherit!important; max-width: 640px!important;}

/*Установка размера окна видео*/
.article__inner.wide > .article__video > iframe, 
.article__object > iframe, .article__object > .eagle-player {width: 640px!important; height: 360px!important;}

/*Установка размера картинок*/
.article__image-wrapper > .article__image, .article__object img {width: 100%!important; max-width: inherit!important}

/*Установка ширины текста*/
div.article.j-metrics__views-out.j-article__bounding.j-statistics__cluster > .article__head  {max-width: 640px!important;}
.article__head > h1.article__title {margin-right: inherit!important;}

/*Изменение высоты навигационной панели*/
.header > .nav[data-path="logo"] {padding: 0!important;}
}

@-moz-document domain("mail.rambler.ru") {
div.appControls.appControlsTop.appSearchBar, div.appWindow.appWindowWithBanner {right: 0!important;}
div.asideBannerFrame.asideBannerFrame-join {display: none!important;}
}
