@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);

/*_    _________________________    _
 | \  /				\  / |
 |  \/	CHROME TAB STYLE	 \/  |
 |__/_____________________________\__|*/
 
/* offset tabbar */
window[sizemode="normal"] #TabsToolbar {
	margin-left: 5px !important;
}
#tabbrowser-tabs {
	min-height: 26px!important;
}
.tabbrowser-tab[pinned] {
	width:90px!important;	
}
.tabbrowser-tab:not([selected]){
	color:#333!important;
}
.tab-content {
	-moz-padding-end: 8px!important;
	-moz-padding-start: 8px!important;
}
.tab-background {
	/* overlap the tabs */
	-moz-margin-end: -8px !important;
	-moz-margin-start: -8px !important;
}
/* favicon margin fix*/
.tab-throbber,.tab-icon-image {
	margin-top: 0!important;
	margin-right: 5px!important;
}
/* buttons icon width fix */
.close-icon > .toolbarbutton-icon,
.tabs-newtab-button,
.tabs-newtab-button > .toolbarbutton-icon { width: unset!important; }

/*** ACTIVE TABS ***/
.tab-background-start[selected]::after,
.tab-background-start[selected]::before,
.tab-background-start,
.tab-background-end,
.tab-background-end[selected]::after,
.tab-background-end[selected]::before {
	min-height: 26px!important;
	width: 15px!important;
}
.tab-background-start[selected]:-moz-locale-dir(ltr):not(:-moz-lwtheme)::before {
	background-image: url(data:image/png;base64,)!important;
}
.tab-background-end[selected]:-moz-locale-dir(ltr):not(:-moz-lwtheme)::before {
	background-image: url(data:image/png;base64,)!important;
}
.tab-background-start[selected]:-moz-locale-dir(ltr)::after,
.tab-background-end[selected=true]:-moz-locale-dir(ltr)::after {
	background-image: none!important;
}
.tab-background-middle[selected] {
	background-image: url(data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAQAAAAaCAYAAACQLf2VAAAACXBIWXMAAA51AAAOdQG5Y1EyAAAAP0lEQVQY08WPMQ4AMAgCIfFx/f+DpKviYLcyXi4KBHBQQkmqIGD5BbQbvTmCDl5uuKE0gzQjx5cNUNu4scWLXfr9HZDCnqQvAAAAAElFTkSuQmCC)!important;
	background-color: transparent!important;
}
/* Hide Background tab separators (3px wide) */
#tabbrowser-tabs[movingtab] > .tabbrowser-tab[beforeselected]:not([last-visible-tab])::after,
.tabbrowser-tab:not([selected]):not([afterselected-visible]):not([afterhovered]):not([first-visible-tab]):not(:hover)::before,
#tabbrowser-tabs:not([overflow]) > .tabbrowser-tab[last-visible-tab]:not([selected]):not([beforehovered]):not(:hover)::after
	{ display: none; }

/*** INACTIVE TABS ** */
.tabbrowser-tab > .tab-stack > .tab-background:not([selected]) {
	background-image: url(data:image/png;base64,),
	url(data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAQAAAAaCAYAAACQLf2VAAAACXBIWXMAAA51AAAOdQG5Y1EyAAAAPklEQVQY02NkYGDQZ0ACjHcfvjiBLMDy9fsPBhQBBjQwKkADgSOH9pv///+fgZGRkeH///8MjD9//vyPrAIA++wTRMnR6iAAAAAASUVORK5CYII=),
	url(data:image/png;base64,);
	background-position: left bottom, 15px bottom, right bottom!important;
	background-repeat: no-repeat!important;
	background-size: 15px 26px, calc(100% - (2 * 15px)) 100%, 15px 26px!important;
	background-color: transparent!important;
}
/*** HOVERED TAB ***/
.tabbrowser-tab:hover > .tab-stack > .tab-background:not([selected]) {
	background-image: url(data:image/png;base64,),
	url(data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAQAAAAaCAYAAACQLf2VAAAACXBIWXMAAA51AAAOdQG5Y1EyAAAAXklEQVQY05WRsQqAMAxELyX/5OT/O6tfIQiOtWLr2twNxWx5vEsCMQAzurLjvJYeeHleBAAqNwFEHEQ8cUSGSkTXJplhfy8Vo1UxGhnj0zlCPby2kbFv6xT+ku8cnA/9yxe4Jj3xCwAAAABJRU5ErkJggg==),
	url(data:image/png;base64,)!important;
	background-position: left bottom, 15px bottom, right bottom!important;
	background-repeat: no-repeat!important;
	background-size: 15px 26px, calc(100% - (2 * 15px)) 100%, 15px 26px!important;
	background-color: transparent!important;
}

/*** CLOSE TAB BUTTON (reuse from ff26)***/
.tab-close-button {
	list-style-image: url("data:image/png;base64,")!important;
	-moz-image-region: rect(0, 12px, 12px, 0)!important;
	-moz-margin-end:-1px !important;
}
/*skin/browser.csss line:2703*/
#TabsToolbar[brighttext] .tab-close-button:not(:hover):not([selected]) {
	-moz-image-region: rect(0, 12px, 12px, 0)!important;
}
.tab-close-button:hover,
.tab-close-button:hover[selected] {
	-moz-image-region: rect(0, 24px, 12px, 12px)!important;
}
.tab-close-button:hover:active,
.tab-close-button:hover:active[selected] {
	-moz-image-region: rect(0, 36px, 12px, 24px)!important;
}

/*** NEWTAB BUTTON ***/
.tabs-newtab-button {
	list-style-image: url(data:image/png;base64,)!important;
	-moz-image-region: rect(0px, 34px, 18px, 0px)!important;
	-moz-margin-start: 3px!important;
}
.tabs-newtab-button:hover {
	-moz-image-region: rect(0px, 68px, 18px, 34px)!important;
	background-image:none!important;
}
.tabs-newtab-button:hover:active {
	-moz-image-region: rect(0px, 102px, 18px, 68px)!important;
	background-image:none!important;
}

/*[[button]]*/

/** OTHER FIXES **/
/** Bookmark bar margin **/
#personal-bookmarks {margin-bottom: 1px!important;}

style_setting_options.value:
/*______________________________
 |				|
 |	CHROME BUTTONS STYLE	|
 |______________________________|*/

/** Offset navbar **/
#nav-bar {padding-left: 2px!important;}

/** Toolbar Button (normal) **/
#nav-bar .toolbarbutton-1:not([type="menu-button"]),
#nav-bar .toolbarbutton-1 > .toolbarbutton-menubutton-button,
#nav-bar .toolbarbutton-1 > .toolbarbutton-menubutton-dropmarker {  padding-top: 7px; padding-bottom:7px; }
#nav-bar .toolbarbutton-1 .toolbarbutton-icon,
#PersonalToolbar toolbarbutton { border-radius: 2.5px; }

/** Toolbar Button (hover) **/
#nav-bar .toolbarbutton-1:not([disabled]):not([id="back-button"]):not([open]):not(:active):hover .toolbarbutton-icon,
#nav-bar .toolbarbutton-1:not([disabled])[open]:-moz-any(:not(:hover),:hover) > toolbarbutton:not([open]) >.toolbarbutton-icon, /*split menu button*/
#nav-bar .toolbarbutton-1:not([disabled]):not([open]):hover .dropmarker-icon,
#nav-bar .toolbarbutton-1:not([disabled]):-moz-any(:not([open]),:hover) > toolbarbutton[open] + dropmarker > .dropmarker-icon /*split menu dropmarket*/ {
    border-color: rgb(224,224,224) rgb(188,188,188) rgb(145,145,145) rgb(197,197,197)!important;
    box-shadow: 0 1px 0px rgb(255,255,255) inset, 0 1px 0px rgba(10,10,10,.05) !important;
    background:none!important;
}
/** Toolbar Button (active) **/
#nav-bar .toolbarbutton-1:not([disabled])[checked] .toolbarbutton-icon,
#nav-bar .toolbarbutton-1:not([disabled]):not([id="back-button"]):hover:active .toolbarbutton-icon,
#nav-bar .toolbarbutton-1:not([disabled]):not([id="back-button"])[open] .toolbarbutton-icon,
#nav-bar .toolbarbutton-1:not([disabled]) > .toolbarbutton-menubutton-button[open]:-moz-any(:not(:hover),:hover) > .toolbarbutton-icon, /*split menu button*/
#nav-bar .toolbarbutton-1:not([disabled])[open] .dropmarker-icon {
    border-color: rgb(125,125,125) rgb(165,165,165) rgb(178,178,178) rgb(165,165,165)!important;
    box-shadow: 0 0 1px rgba(0,0,0,.10) inset  !important;
    background-color: rgba(206,206,206,.75) !important;
    background-image:none!important;
}
