/*******************BOOKMARK BAR BACKGROUND*******************/

#main-window #navigator-toolbox #PersonalToolbar
{
  -moz-appearance: none !important;
  background: url(data:image/png;base64,) top !important }



/******************STATUS BAR BACKGROUND*********************/

statusbarpanel
{
  -moz-appearance: none !important;
  background: url(data:image/png;base64,) bottom !important }



/*****************TAB BAR BACKGROUND***************/

.tabbrowser-tabs, .tabbrowser-tab[selected="true"], .tabs-bottom {
  -moz-appearance: none !important;}




/*****************SELECTED TAB BACKGROUND***************/

.tabbrowser-tab[selected="true"],
.tabs-bottom {
  -moz-appearance: none !important;
  background: url(data:image/png;base64,) bottom !important }

/**************R-side icons**************/
#urlbar>#urlbar-icons
	{margin-right:-3px !important;}

#feed-button:not(:hover), #safebrowsing-urlbar-icon:not(:hover),
#star-button:not(:hover), #go-button:not(:hover) {
	opacity: 0.5 !important;}

#go-button{
	list-style-image: none !important;
	background: url("data:image/png;base64,") center center no-repeat;
	width: 16px !important; 
	/*width: 12px !important; height: 12px !important;*/}

  /*#go-button:hover + #urlbar-icons{
	background-color: rgba(72, 215, 251, 0.4) !important;
	background-image: url(chrome://browser/skin/tabbrowser/tab-active-bkgnd.png);}*/

#urlbar > .autocomplete-history-dropmarker > .dropmarker-icon{
	margin-left: 3px !important;} /*prevents overly large blue bknd*/

#urlbar > .autocomplete-history-dropmarker {
	/*margin-left:-5px !important; pull other elements closer
  	margin-top:-1px !important;
   	margin-bottom:-1px !important;*/
	-moz-border-radius-topright: 8pt !important;
	-moz-border-radius-bottomright: 8pt !important;
	-moz-background-clip: padding !important;
	border:none !important;}

#urlbar > .autocomplete-history-dropmarker:hover{
	background-color: rgba(72, 215, 251, 0.4) !important;}

/**************searchbox fixes**************/
.searchbar-engine-button {
	/*background: none !important;*/
	padding-left: 1px !important;
	margin-right: 0px !important;
	border: none !important;
	background: transparent url(chrome://browser/skin/tabbrowser/tab-active-bkgnd.png) !important;
	-moz-border-radius-topleft: 8px !important;
	-moz-border-radius-bottomleft: 8px !important;}

.searchbar-engine-image { margin-left: 6px !important; }

.searchbar-engine-button[addengines="true"] > .button-box {
	background-position: -0px 1px !important;}

.searchbar-dropmarker-image
	{ display:none !important; }

.searchbar-textbox:not(:hover):not([focused="true"])>.searchbar-engine-button
	{ opacity:.8!important;}

.searchbar-engine-button:hover{
	background-color: rgba(72, 215, 251, 0.4) !important;}


.searchbar-textbox>.autocomplete-textbox-container>.textbox-input-box
  >.autocomplete-textbox.textbox-input{
	border-left: solid 1px!important;
	border-left-color: rgba(0,0,0,.1)!important;
	padding-left: 1px !important;
	padding-right: 2px !important;}



/**************search-go-button**************/

.search-go-button {
	list-style-image: none !important;
	background: url("data:image/png;base64,") center center no-repeat;
	width: 14px !important; height: 15px !important;
	margin-right: 2px !important;}

.search-go-button:not(:hover)
	{ opacity:.5!important;}



/*****************************MENUBAR*******************************/

#toolbar-menubar{
	margin: 0px 0px 24px 0px !important;}



/***********TAB TEXT************/

.tabbrowser-tab .tab-text,
#PersonalToolbar toolbarbutton > label {
  color: White !important }



























/*******************************************************/

















@-moz-document url("chrome://browser/content/browser.xul")
{.tabbrowser-strip
{
position:fixed !important;

/* change top:29px to top: 37px for large icons */
top:29px !important;
left:0px !important;
width:100% !important;
z-index:0 !important;
height: 23px !important;
overflow: hidden !important;
}

.tabbrowser-tabs {
border-top: 1px !important;
height: 26px !important;
}
}


/**********TAB SIZE************/

#content tab[selected="true"] { min-width: 250px !important; }
#content tab { min-width: 160px !important; }




/**********TRANSPARENT TABS************/

*.tabbrowser-tabs
{
padding: 1px 0 0 0 !important;
background-image: none !important;
background-color: transparent !important;
}
*.tabbrowser-tabs > *.tabbrowser-tab
{
max-height: none !important;
padding: 0 !important;
border-left: 2px solid !important;
border-top: 2px solid !important;
border-right: 1px solid !important;
-moz-border-left-colors: #a0a0a0 rgba(255, 255, 255, 0.7) !important;
-moz-border-top-colors: #a0a0a0 rgba(255, 255, 255, 0.7) !important;
-moz-border-right-colors: #a0a0a0 rgba(255, 255, 255, 0.7) !important;
-moz-border-radius-topleft: 10px !important;
-moz-border-radius-topright: 10px !important;

-moz-box-sizing: border-box !important;
}

*.tabbrowser-tabs > *.tabbrowser-tab
{
height: 26px !important;
margin-top: 2px !important;
}
*.tabbrowser-tabs > *.tabbrowser-tab:hover
{
height: 27px !important;
margin-top: 1px !important;
}

*.tabbrowser-tab
{  background: url(data:image/png;base64,) bottom !important }


.tab-drop-indicator-bar {
position:fixed!important;
top:20px;
left:0px;
width:100%!important;
z-index:1;
}

*.tabbrowser-tabs > *.tabbrowser-tab[selected="true"]
{
height: 26px !important;
margin-top: 0 !important;
}


	.tabbrowser-tabs tab
	{
		-moz-opacity:0.99 !important;
	}
	.tabbrowser-tabs tab[selected="true"] label
	{
		font-weight: bolder !important;
	}



/*This makes unread tabs appear in italics*/

#content tab:not([selected]) {
font-style: italic !important;
} 

