css:
@-moz-document url-prefix("http://novosti-kosmonavtiki.ru/forum/") {
body {
	color: #000;
}
a { color: #4682B4; }
a:visited {color: #74a9d6;}
a:hover, a:active {color: #2889da;}
#login a,
.forum-header-box a,
.forum-column-footer a {
	color: #fff !important;
}


.forum-header-box,
.forum-column-footer {
	background: #CB6501 !important;
	color: #fff;
}

.forum-menu-box,
.forum-navigation-box,
.forum-users-online {
	margin: 20px 0 !important;
	padding: 0 !important;
	line-height: 1em !important;
}

#center {
	background: #f0f0f0;
	width: 980px;
	left: auto;
	margin: 0 auto;
}

table.forum-quote {
	border-color: #808080 !important;
	margin: 10px 0 !important;
}
table.forum-quote th {
	background: #808080 !important;
	color: #fff;
}
table.forum-quote td {
	background: #ddd !important; 
	color: #000;
}
td.forum-column-icon {
	border-left: none !important;
	border-bottom: 1px solid #808080;
}
.forum-lastpost-box {
	padding: 0 !important;
	text-align: center;
}
.forum-header-title {
	font-size: 1.4em !important; 
}
table.forum-forum-list thead,
table.forum-topic-list thead {
	background: #da7f25 !important;
	color: #fff;
}
table th.forum-column-title {
	border: none !important;
}
table th.forum-column-lastpost {
	text-align: center !important;
	border: none !important;
}
table.forum-forum-list tr td,
table.forum-topic-list tr td {
	padding-top: 10px !important; 
	padding-bottom: 10px !important;
	border: none;
	border-bottom: 1px solid #808080;
}
.forum-user-signature,
.forum-post-date,
.forum-user-additional,
.forum-user-avatar,
.forum-user-status,
.forum-post-rating,
.rating-vote-result,
.forum-contact-links {
	color: #808080 !important;
	font-size: 10px !important;
}
.forum-user-avatar {
	max-width: 100px !important;
}
.forum-user-avatar img {
	max-width: 100px;
	height: auto;
}
.forum-user-signature {display: none;}
.forum-post-text,
.forum-item-name {
	font-size: 16px !important;
	line-height: 1.5em;
}

*[style*="color"] { 
	color: black !important; 
	font-weight: bold; 
}
#footer1,
#footer2,
#footer0 {
	display: none !important;
}
.forum-footer-inner a {color: #fff !important;}

select.forum-selector-single,
.forum-search-block-container {
	font-size: 1.2em;
}
}
