css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document url("http://www.neowin.net/"), url("http://neowin.net/"),  url-prefix("http://www.neowin.net/index"), url-prefix("http://www.neowin.net/news"){

/*resize*/
.page,body>div{width:1100px!important}
body>div>table[width="942"]{display:none!important}

/*PADDING!*/
#content-main p, #content-main h4, #content-main-archives, #content-main-top {padding-left:20px !important}
#content-main p, #content-main h4, #content-main-top, #content-main-archives {padding-right:20px!important}
#content-main-archives h4 {padding-left:0px !important}
#content-main p {text-align: justify !important}
#content-main-square, #rev-square {margin:10px!important}
#content-main-featured>a>img {padding:10px!important}
#content-sidebar-main {padding:20px!important}
#content-sidebar-main {padding-top:10px!important}
#content-main-square, #rev-square {float:right!important}
#content-main-square {width:auto!important}
#content-main img {padding-left:10px!important}
#content-main img {margin-right:-10px!important}
#content-main-first {padding:30px!important}
#content-main-first {padding-top:20px!important}
#content-main-first {padding-bottom:10px!important}
#content-main {margin-top:5px!important}
.main-posted-by {padding-left:20px!important}
.first-news-post .main-posted-by {margin-left:-20px!important}
#sidebar-skyscraper {padding-left:20px!important}
.story {padding:20px!important}
.story {padding-left:35px!important}
.story {padding-right:35px!important}
.content-story-t-l {padding-left:35px!important}
.content-story-t-l {padding-right:35px!important}
.content-story-themed-tbar-l {padding-right:25px!important}
.content-story-themed-tbar-l {padding-left:25px!important}
#nav-toggle {display:none!important}

/*HEADING!*/
h4>a, h3>a {font-size:1.1em!important}
h4>a, h3>a {color:#204872!important}
}

/*FLUFF!*/
.content-themed-bar-l {display:none!important}
.content-themed-bar-r {display:none!important}
