@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("reverso.net") {

  /******************************/
  /*   Reverso.net - Clean up   */
  /******************************/
  /*          By loxaxs         */
  /******************************/
  
  /* Hiding crap part */
  .third-column, .tradpromenu, .bottominfo.clearfix,
  .tradpromenu.tradpro, .clearfix.comun-box, .left-side,
  .headerDescription.clearfix, .third-column.result, #advsection,
  .toprightdiv, .bottom-banner, .advertise-block, .banner,
  .ctxbox, .localizediv, .utilizer.clearfix, .top-banner, #ads_rev,
  .welcome-message, .frase, .softidiv, .box3, .nouveau, .nouveau210,
  #latestentries, #copy, #copywriteen, .tradcopy, 
  #form1 > #content > #rightcom, #suggest_but1, #ZeroClipboardMovie_1,
  #locdbanner, #ads, .ad, #adLeaveDiv, .fr-grammar, img[alt=gif],
  #facebookDiv, #fbDiv, #fblike, .fbutton, #flashContent, .cmInfo {
    display: none !important;
  }
  
  /* Adaptive part */
  /* If this style breaks down the page too much, you may try to remove this asterisk -> */
  #form1 > #content {
    display: block !important;
    width: -moz-available !important;
    padding: 50px 0 0 0 !important;
    margin: 40px auto 0 auto !important;
  }
  
  .h-holder.home, .center_content, .center-content, .wrapperW {
    margin: 0 auto !important;
    float: none !important;
    display: block !important;
  }
  
  #leftColumn, #rightColumn {
    margin: -60px auto 60px auto !important;
    float: none !important;
    display: block !important;
  }
  .wrapper-form {
    background-image: none !important;    
  }
  .wrapper-form-bg1, .wrapper-form-bg-2, .wrapper-form-bg-21, .wrapper-form-bg-3, #content, .content-inner {
    width: 100% !important;
    background-image: none !important;
  }
  .content-conj {
    margin-top: 70px;
    position: relative;
    top: -40px;
  }
  .Section1a {
    width: auto !important;
  }
  .homeContent {
    height: auto !important;
  }
  #headerHome {
    width: 825px !important;
    margin: 0 auto !important;
  }
  #mBar {
    width: 780px !important;
  }
  #footer {
    margin: -20px auto 20px auto !important;
  }
  
  .search-block {
    margin: 0 !important;
    padding: 0px calc(50% - 400px)!important;
  }
  
  #content.clearfix > div {
    float: none!important;
  }
  #spellSection {
    margin: 30px 5px 0 calc(50% - 350px ) !important;
    display: inline-block; 
  }  
  #searchsection {
    margin-top: 30px;
    display: inline-block; 
  }
  
  .h10 {
    height: 0 !important;
  }/**/
  /**/
}
