css:
@-moz-document domain("banner.acu.edu") {
td .taboff{
  display: inline-block;
  height: 38px;
  padding: 0 30px;
  color: #555;
  text-align: center;
  font-size: 11px;
  font-weight: 600;
  line-height: 38px;
  letter-spacing: .1rem;
  text-transform: uppercase;
  text-decoration: none;
  white-space: nowrap;
  background-color: transparent;
  border-radius: 4px;
  border: 1px solid #bbb;
  cursor: pointer;
  box-sizing: border-box;
  margin-right: 10px;
  background-image:none;
}

td .tabon{
  display: inline-block;
  height: 38px;
  padding: 0 30px;
  color: #555;
  text-align: center;
  font-size: 11px;
  font-weight: 600;
  line-height: 38px;
  letter-spacing: .1rem;
  text-transform: uppercase;
  text-decoration: none;
  white-space: nowrap;
  background-color: #4d3766;
  border-radius: 4px;
  border: 1px solid #4d3766;
  cursor: pointer;
  box-sizing: border-box;
  margin-right: 10px;
  background-image:none;
  
}
td .taboff:hover{   
  color: #333;
  border-color: #000;
  outline: 0; 
}


.infotextdiv{
    display:none;
}

.pageheaderlinks{
    display:none;
}

.headerlinksdiv2{
    display: none;
}

.banner_copyright{
    display: none;
}

.releasetext{
    display: none;
}

.bg3{
    display:none;
}
.bgtabon{
    display:none;
}

.bgtaboff{
    display:none;
}

h2{
    margin-bottom:25px;
}

h1{
    margin-bottom:20px;
}

input[type="reset"],
input[type="button"] {
  display: inline-block;
  height: 38px;
  padding: 0 30px;
  color: #555;
  text-align: center;
  font-size: 11px;
  font-weight: 600;
  line-height: 38px;
  letter-spacing: .1rem;
  text-transform: uppercase;
  text-decoration: none;
  white-space: nowrap;
  background-color: transparent;
  border-radius: 4px;
  border: 1px solid #bbb;
  cursor: pointer;
  box-sizing: border-box;
  font-family: sans-serif;
  margin-right:10px;
}

button:hover,
input[type="reset"]:hover,
input[type="button"]:hover,
.button:focus,
button:focus,
input[type="reset"]:focus,
input[type="button"]:focus {
  color: #333;
  border-color: #888;
  outline: 0; }


.dddefault{
    padding-top: 4px !important;
