@-moz-document domain("scratch.mit.edu"), domain("scratchblocks.github.io") {
.sb2 .boolean {
  position: relative;
  border-radius: 0;
  border-width: 2px 0;
  margin: 0 10px;
  z-index: 1;
  text-align: center;
  min-width: 10px;
  padding: 3px 1px; }
  .sb2 .boolean::before, .sb2 .boolean::after {
    background-color: #d42828;
    border-color: #da4646 #971c1c #971c1c #da4646; }
  .sb2 .boolean.motion::before, .sb2 .boolean.motion::after {
    background-color: #4a6cd4;
    border-color: #6380da #344d97 #344d97 #6380da; }
  .sb2 .boolean.looks::before, .sb2 .boolean.looks::after {
    background-color: #8a55d7;
    border-color: #9a6ddc #623c99 #623c99 #9a6ddc; }
  .sb2 .boolean.sound::before, .sb2 .boolean.sound::after {
    background-color: #bb42c3;
    border-color: #c45ccb #852f8b #852f8b #c45ccb; }
  .sb2 .boolean.pen::before, .sb2 .boolean.pen::after {
    background-color: #0e9a6c;
    border-color: #30a880 #0a6e4d #0a6e4d #30a880; }
  .sb2 .boolean.variables::before, .sb2 .boolean.variables::after {
    background-color: #ee7d16;
    border-color: #f08d33 #c36612 #c36612 #f08d33; }
  .sb2 .boolean.events::before, .sb2 .boolean.events::after {
    background-color: #c88330;
    border-color: #cf944d #8e5d22 #8e5d22 #cf944d; }
  .sb2 .boolean.control::before, .sb2 .boolean.control::after {
    background-color: #e1a91a;
    border-color: #e5b53a #a07812 #a07812 #e5b53a; }
  .sb2 .boolean.sensing::before, .sb2 .boolean.sensing::after {
    background-color: #2ca5e2;
    border-color: #49b1e6 #1f75a1 #1f75a1 #49b1e6; }
  .sb2 .boolean.operators::before, .sb2 .boolean.operators::after {
    background-color: #5cb712;
    border-color: #a7ce87 #488f0e #488f0e #a7ce87; }
  .sb2 .boolean.custom::before, .sb2 .boolean.custom::after {
    background-color: #632d99;
    border-color: #794aa7 #46206d #46206d #794aa7; }
  .sb2 .boolean::before, .sb2 .boolean::after {
    content: '';
    position: absolute;
    z-index: -1;
    width: 14px;
    height: 14px;
    top: 1px;
    border-style: solid;
    transform: rotate(45deg); }
  .sb2 .boolean::before {
    left: -7px;
    border-width: 0px 0px 2px 2px; }
  .sb2 .boolean::after {
    right: -7px;
    border-width: 2px 2px 0px 0px; }
}
