css:
/* Dark IUCN Red List by siliconworm */
@-moz-document domain("www.iucnredlist.org") {

body {
background: linear-gradient(to bottom,#202020,#353942 310px)!important;
color: #E5E5E5;
}

#container_home {
background: transparent;
color: #E0E0E0;
}
#container {
background: linear-gradient(to bottom,#202020,#353942 310px)!important;
}
#tabs li a {
background: #2F303C;
}
#tabs li a.current {
background: #3D5C6B;
}
#search input.text {
color: #ADFF61;
}
.tab2 {
background: #586D86;
}
.tab2.active {
background: #632020;
}
#leftnav div.scrollContainer div.dhtmlxTree {
background-color: #2A2D36;
}
#leftnav,#leftnav .rc-leftnav_top  {
background: #55555D;
}
.menu a {
background: #363242;
}
.menu a:hover, .menu a.currentPage {
background: #547683;
transition: background .2s;
}
#topnav_menu li:hover ul.sub li a:hover {
background: #614948;
transition: background .2s;
color: #000;
}
#topnav_menu li:hover ul.sub {
background: #373942;
}
#content_results {
border-left: 186px transparent solid;
border-right: 300px solid transparent;
}
#content_thin {
border-left: 186px transparent solid;
border-right: 235px solid transparent;
}
#data_factsheet {
border-right: 176px solid transparent;
}
#detailsFooter {
border-right: 176px solid transparent;
}
#content_factsheet {
border-left: 186px transparent solid;
}
#header {
background: transparent;
}
#news-home .rc-news, .rc-head {
background: transparent;
}
#scale {
background: linear-gradient(to bottom,rgba(71, 83, 105, 0),#8A281B 310px)!important;
}
element.style {
background: rgba(61, 64, 68, 0.69);
border: 0px solid #d52b1e;
}

ul#species li.gallery div.feature {
background: rgba(114, 114, 114, 0.36);
}

div#gal_bord_inner {
background: transparent;
}

textarea {
background: #293134!important;
color: #C0C0C0!important;
}
input, button, select {
background: -webkit-gradient(linear,0% 0,0% 100%,from(#0E1313),to(#485A64));
color: #67D5FF;
text-shadow: #584848 0px 0px 2px;
}
#topnav_menu li a.top_link:hover, #topnav_menu li a.top_link:hover span, #topnav_menu li:hover>a.top_link, #topnav_menu li:hover>a.top_link span {
color: #49A34D;
transition: color .2s;
}

/* defaults for all links */
a:link, .link, a font[color], .mn-hd-txt, 
    a.fl:link, .fl a, .flt, a.flt, .gl a:link, a.mblink, .mblink b, ._Hg ._qA, ._Hg ._Pn, ._Hg ._Uq, #gt-feedback, #gb a  { 
        color: #7C9EDC !important; 
        transition: color .4s, text-shadow .4s !important;
    }
                    
a:visited { 
    color: #F92672 !important; 
}

/* hovers */
.menuitem:hover, a:hover, a span:hover, .actbar-btn:hover, #gt-feedback:hover, #gb a:hover {
color: #E5E5E5 !important; text-shadow: #ffffff 0px 0px 2px !important;
transition: color .4s, text-shadow .4s !important;
}

/* big link visited */
#res .g a:visited { opacity: 0.5 !important; }

/* URL text */
.a, cite, .cite, .cite:link, cite b, #resultStats { color: #999 !important; }

/* dashes between Cached and Similar links */
.gl { color: #999 !important; }

/* disable dashed outline on focused elements */
:focus { outline: none; }

/* image thumbnails */
#res .g img, #ImgContent td img {
  border: none !important;
box-shadow: 4px 4px 10px rgba(0,0,0,0.8) !important;
}

/* Main text color */
div, td, font[color="#000000"], .qbtbha .qbtbtxt.qbclr, .gt-rw-span, .gt-syn-span, .gt-card-ttl-txt, #contribute-target, #source {
color: #CFCFCF !important;
}

/* fix for google suggest */
.aAutoComplete .cAutoComplete, .google-ac-a .google-ac-c, .gac_a > .gac_c { color: #CCCCCC !important; }
.aAutoComplete .dAutoComplete, .google-ac-a .google-ac-d, .gac_a > .gac_d { color: white !important; }
.google-ac-e td, .gac_e { color: #380000 !important; }

/* make sure web forms (and google suggest) don't get their colors changed */
input *, textarea *, .aAutoComplete {
    background: -moz-Field !important;
    color: #999999 !important;
}

}
/* Dark IUCN Red List by siliconworm */
