css:
@namespace url(http://www.w3.org/1999/xhtml);
/* Can't figure out how to fix the  bloody code edit cursor :| */

@-moz-document domain("hackerrank.com") {
    #wrapper, #hr_v2, #row, #challengeAccordion, .page_footer, body, .feed-wrapper,
    .outer-feed-wrapper, .user-education-popup.text-center { 
        background-color: #3A3039;
        color: white;
    }
    .chapter-item, #challengeAccordion, #breadcrumb .content-header, #contest-challenges-problem,
    .boundA.curved.mlB.mmR.pdA.track-box, .boundA.curved.mlB.mmL.pdA.track-box, .unstyled.psT.psB,
    .light-wrap, .table-wrap, .comments-container, .has-string, .CodeMirror-empty, .CodeMirror,
    #comment-preview, pre, .clearfix, .row.padding-small, .table-wrap > header, #select2-drop,
    .select2-results-dept-0, a.select2-choice.select2-default, .select2-result-label, .calloutBody,
    .editorial-header-box, .editorial-code-box, p.small.bold, #pref-pane, #profileTabs,
    .select2-no-results, .all-notif-settings li, .all-notif-settings ul, .dropdown-menu, .tab-head,
    .table-body, .span5, .tourbus-leg, .submission-model-panel, .CodeMirror-linenumber,
    .hr-dialog-header, .hr-dialog-body, .hr-dialog-footer, .hide-in-private-contest a, #languages,
    .plA, .nav-tabs.ungroup > li a,
    .table-striped tbody tr:nth-child(2n+1) th, .table-striped tbody tr:nth-child(2n+1) td,
    .feed
    {
        background-color: #3C2D3B !important;
        color: teal !important;
    }
    #contest-challenges-problem .btn.btn.backbone.start.pull-right,
    .block-center.blackbone.expand-subdomains.cursor.color-alt-grey, .txt-navy,
    label, #tab-spaces, input[type="text"], .static-input, a.title-link.inverse {
        color: #0A8A8A;
    }
    div.discussion-root-node-knob, code, html {
        background: #3C2D3B;
        background-color: #3C2D3B
    }
    a.backbone {
        color: #0D9D9D !important;
    }
    .view-more.text-center.small.bold, .btn.btn.backbone.start.pull-right, .btn, .nav-tabs.nav,
    .btn.btn, .pagination a, .pagination .active a, .select2-search input,
    .select2-container, .select2-choice a, .select2-container .select2-choice,
    .select2-container .select2-choice div, .dropdown-toggle .select, .dropdown-toggle .indent,
    .sidebar_list-heading.active, .cat-item {
        background-color: #3C2D3B !important;
        color: teal;
        background-image: linear-gradient(to bottom, #3C2D3B, #4D3B46) !important;
    }
    .nav-tabs:not(.ungroup) > li.active a, .all-notif-settings, .msB, .hr_secondary-btn {
        background-color: #3C2D3B;
        color: lightgrey !important;
    }
    #breadcrumb .bold.small {
        color: teal;
    }
    #navigation .page-header {
        background-color: #3B273C;
    }
    .btn.difficulty.active, #HackerRank-homepage .toggle {
        background-image : linear-gradient(to bottom, #5C4D5B, #6D5B66) !important;
    }
    /* Code editor */
    .cm-variable {
        color: #577012 !important;
    }
    .cm-keyword {
        color: #8C5D8B !important;
    }
}
