css:
@-moz-document domain(corazondemelon.es) {
/****                     BUBBLE GUM                        ****/
/*                        **********                           */
/* 

Descripción: Tema para CdM creado por Liz (@ eldarya-fairy.tumblr.com)
Reglas:
    * No quites el credito ^
    * Iconos del menú: Font Awesome.
    * Puedes editarlo tanto como quieras.
    * La mayoría de las imágenes pertenecen a Beemoov/Chinomiko, solamente la del menú fue hecha por mí.
    * Cualquier duda/problema, aquí: http://eldarya-fairy.tumblr.com/ask
*/

/**** GENERAL ****/

body {
   font:90% Calibri;
   color:#777777;
}

a {
   font-family:Calibri;
   text-transform:uppercase;
   letter-spacing:1px;
   text-decoration:none;
}

span {
   color:#f4bbff;
}

span a {
   color:#ff55a3;
}

h1, h2, h3, h4, h5 {
   font-family:Calibri!important;
   text-transform:uppercase;
   letter-spacing:1px;
   color:#ff8ea2!important;
}

h2 .firstword, .integrated-title2 .firstword {
   font:11px Calibri;
   color:#ffc1cc;
   font-style:normal;
   text-transform:uppercase;
   letter-spacing:1px;
}

.integrated-title1, .integrated-title2, .integrated-title3, #current-episode h2 {
   font-family:Calibri!important;
   text-transform:uppercase;
   letter-spacing:1px;
   font-style:oblique;
}

.integrated-title1 {
   color:#ff758d!important;
}

.integrated-title2 {
   color:#ff758d!important;
}

.integrated-title3 {
   color:#f4c1ff!important;
}

#avatarGirl {
   padding-left:44px!important;
}

#right-informations h3 {
   font:14px Calibri!important;
   text-transform:uppercase;
   letter-spacing:1px;
   color:#f4c1ff!important;
   padding:10px 18px 10px 4px!important;
   text-align:center;
}

#right-informations, #counter, .number, .firstword {
   font:16px Calibri!important;
   text-transform:uppercase;
   letter-spacing:1px;
   color:#ffc1cc!important;
}

.firstword {
   margin-right:16px; 
}

#container{
   background-image:url(http://40.media.tumblr.com/c888c45f6b832c846a62ef6393093ea1/tumblr_n9ig3r78me1tpf4xeo3_250.png)!important;
   background-repeat:repeat;
}

#container.connected h2 {
   font-family:Calibri;
   margin:12px 6px;
   border-bottom:1px dashed #cfcfcf;
   padding-bottom:4px;
}

#news-list .news .date {
   float:left;
   width:59px;
   height:28px;
   background-color:#ffc1cc;
   border-radius:5px;
   background-image:none;
   color:#ffffff;
   font:bold 14px Calibri;
   padding:6px 0px 0px 10px;
}

.text a {
  color:#ff758d!important;
}

/**** OBJETIVOS ****/

#current-episode h2 {
  font-size:15px;
}

#current-episode li.completed {
   background:transparent url(https://40.media.tumblr.com/cde5d381b9bd1b1ca40db7551f9c9247/tumblr_nn61lsHCdv1urksn7o4_75sq.png)left center no-repeat;
}

.episode-progression .progress-left {
   position:absolute;
   background:url(https://41.media.tumblr.com/fbdd733f5ae70b1e74e335e1a14c1e66/tumblr_nn61lsHCdv1urksn7o3_75sq.png)!important;
   width:5px;
   height:14px;
   left:-5px;
}

.episode-progression .progress-inner {
   position:absolute;
   background:url(https://40.media.tumblr.com/655a1880e8de69f7801eb6bdf5d9b3bc/tumblr_nn61lsHCdv1urksn7o1_75sq.png)!important;
   height:14px;
   left:0px;
   right:0px;
}

.episode-progression .progress-right {
   position:absolute;
   background:url(https://36.media.tumblr.com/7aff4f8c1dc70949922c55e48c9176b6/tumblr_nn61lsHCdv1urksn7o5_75sq.png)!important;
   width:5px;
   height:14px;
   right:-5;
   z-index:9999;
}

/**** JUEGO ****/

.scene .sentence {
  display:block;
  border:0;
  background-color:#fff4f6!important;
  opacity:0.8;
  filter: alpha(opacity=80);
  border-top:4px solid #ffa8b7!important;
  border-left:4px solid #ffa8b7!important;
  border-top-left-radius:25px;
  padding:5px 15px 0 37px;
  text-align:left;
}

.scene .responses {
  border-left:4px solid #ffa8b7!important;
}

.scene .responses .response {
  color:#ffa8b7!important;
}

.scene .responses .response:hover {
  background-color:#ffa8b7!important;
  color:#ffdbe1!important;
}

.scene .responses .response:hover .text {
  color:#ffffff!important;
}
            
.scene .player {
  background:transparent url(https://40.media.tumblr.com/3032cc627ba25ec0940c2b68c18cecb4/tumblr_nn7w5bvKrA1urksn7o7_r1_250.png) -6px top no-repeat;
  width:175px;
  height:130px;
  padding-left:13px;
}
                  
.text{
  color:#ff758d!important;
}

/*[[hello]]*/

/*[[hide]]*/

/*[[ver]]*/

/**** COPYRIGHT ****/

#copyright, #copy2 {
  text-align:center;
}

#copy2 .copy2d {
  float:center;
  text-align:center;
  color: #b1a59c;
  bottom:0;
  right:0;
  margin-bottom:5px;
  padding-left:12px;
}

/**** MINIJUEGOS ****/

#minigame {
  padding-left:36px;
}

#minigame h1 {
  text-align:center;
}

p.description a.auto-button-pink {
  margin:10px 10px 10px 36px;
}
                          
/**** HEADER ****/

#logged-episode {
   padding:2px 4px;
   color:#ff8ea2;
   text-shadow:none;
   font-weight:bold;
   text-transform:uppercase;
}

#logged-pseudo a {
   color:#ff758d;
}

#logged-pseudo span {
   float:left;
   color:#ff8ea2;
   text-shadow:none;
   font:bold 14px Calibri;
   letter-spacing:1px;
}

#astrological-sign a {
   text-decoration:none;
   font:oblique 11px Calibri;
   color:#ff8ea2!important;
   text-transform:uppercase;
   letter-spacing:1px;
}

#header a {
   font-size:88%;
}

#header a:hover {
   font-style:oblique;
}

#container.connected #header {
   background-image:url(https://40.media.tumblr.com/359e7b3036f07b0cd6114c3d262c10c1/tumblr_nn61lsHCdv1urksn7o2_r1_1280.png)!important;
}

#sous-menu {
   width:100%;
   height:40px;
   position:relative;
   margin-top:0px;
   background:url(https://41.media.tumblr.com/1494382fc957728e2dbea824a9417efe/tumblr_nn45e2sQ8H1te5zwko1_1280.png) no-repeat scroll top center transparent;
}

.sm0,.sm1, .sm2, .sm3, .sm4, .sm5 {
   opacity:0;
}

/**** CHICOS Y CHICAS ****/

.stamp {
   color:#ff8ea2!important;
   text-shadow:none;
   border-color:#ff8ea2!important;
}

.boyfriend .lom span {
   background-color:#ffc1cc;
   color:#ffffff;
   text-transform:uppercase;
   font:9px Calibri;
   letter-spacing:1px;
   padding:4px 6px;
   border-radius:10px;
   -moz-border-radius:10px;
   -webkit-border-radius:10px;
}

.friend .player-name a {
   color:#ff9fb0;
   font-weight:normal;
   font-style:oblique;
   margin-bottom:2px;
   display:block;
}

/**** MENSAJES ****/

#discussion table {
   background:#ffe5ec!important;
   border:1px solid #ff99b4!important;
}

#discussion table tr {
   border-bottom:1px solid #ff99b4!important;
}

#discussion table tr.read_0 {
   background:#ffe5ec!important;
   border:1px solid #ff99b4!important;
}

#discussion table tr.read_0 .author_avatar, #discussion table .author_avatar {
   background:#ffccd9!important;
}

#discussion .player-name, .backToMessageList {
   float:none!important;
   padding-left:55px;
}

#discussion span {
   color:#ff99b4;
}

#discussion .warning p:nth-of-type(1) {
   text-align:center;
}

.backToMessageList {
   float:right!important;
   padding:3px 1px;
}

#discussion .message_bloc .type-1 {
   background:#f8e5ff!important;
}

#discussion .message_bloc .content {
   border:1px solid #eab2ff!important;
}

#discussion .message_bloc .delete-option {
   opacity:0;
   position:absolute;
   bottom:1px;
   right:138px;
   cursor:pointer;
   transition:opacity .3s;
   -webkit-transition:opacity .3s;
}

#discussion .message_bloc .delete-option span, #discussion .message_bloc .report-option span {
   transition:color .3s;
   -webkit-transition:color .3s;
   color:#e399ff;
   font:88% Calibri;
   text-transform:uppercase;
   letter-spacing:1px;
   padding:1px 6px;
}

/**** TARJETAS ****/

.idcard-admin {
   background-image:url(https://40.media.tumblr.com/882d06f4d9f4a980874bea92c18b3925/tumblr_nn47ntbIbP1te5zwko1_400.png)!important;
   padding-left:4px;
}

.idcard-admin span {
   color:#ffe5ec;
}


.idcard-moderator {
   background-image:url(https://41.media.tumblr.com/e8d7218562ae5dea112226f7759d4115/tumblr_nn47ntbIbP1te5zwko3_400.png)!important;
   padding-left:4px;
}

.idcard-moderator span {
   color:#e399ff;
}

.idcard-member { 
   background-image:url(https://40.media.tumblr.com/13e76a32ea2b9510e798e602f56330d5/tumblr_nn47ntbIbP1te5zwko2_400.png)!important;
   padding-left:4px;
}

.idcard-member span {
   color:#ff8ea2;
}
}
