css:
@-moz-document domain(www.tumblr.com) {

/* bg color */  
body, .l-content, #left_column, #left_column:hover {
background: #ae00ff !important;
background-repeat: repeat !important;
background-size: cover !important;
}

/* content font */
body, .l-content {
font: 12pt Arial !important; 
}

/* removes curved borders */
div.post_footer.clearfix, .post.post_full {
border-radius: 0px !important;  
}

/* post bg color */   
.post_full .post_wrapper, div.post_footer.clearfix, .post_full .post_body, .post.post_full, .post_full .post_tags:after, .right_column, .controls_section li, .identity .controls_section.user_list li .follow_list_item_blog:before, .follow.plus-follow-button:hover, .post.post_full .post_content, .post_full.is_note .post_body .note_item {
background: #7000a8 !important;
border: 0px !important;    
}

/* remove gross blue spots */
.identity .controls_section.user_list li .follow_list_item_blog:before, .identity-refresh .radar .radar_footer .radar_avatar:before, .identity .right_column:after {
background: #7000a8 !important;
border-right: 0px;  
}  
    
.right_column {
margin-top: 5px !important;

}    

/* removes the radar */
#tumblr_radar, .identity .controls_section li.section_header, .sidebar_link.avatar_align, .identity .right_column:after, .identity .right_column .small_links {
display: none !important;
}  

/*
#logo .logo_anchor {

} 
*/  

/* removes certain follow buttons */  
a.reblog_follow_button, .identity .controls_section li.popover_button_blogs .open_blog_link, .identity .blog_menu .selected_blog #open_blog_link {
display: none !important;
}  

/* removes footer */  
.pinned_sidebar_footer .sidebar_nav {
display: none;
}  

/* text color */  
.plus-follow-button, body, .post .post_header .post_info_link, .note_link_current, .post_tag, .alternative_timestamp, .post_source_name_prefix, .post_full.is_note .asker .name, .post_full.is_note .asker {
color: #96848a !important;
}

/* header */    
.l-header-container--refresh .l-header, .l-header-container--refresh {
background: #7000a8 !important;
}

/* remove compose "pointless tumblr srsly why" button */        
.compose-button {
display: none;    
}  

/* search box */    
.ui_search .search_query {
border: 0px; 
color: #96848a !important;    
}  
  
/* update posts notification */  
.l-header-container--refresh .tab_notice, .new_post_notice_container {
background-color: #ae00ff !important;
border: 2px solid #ae00ff;
color: #ae00ff !important;
left: 38px;
}  
 
.icon_posts:before {
content: "";
color: #ae00ff !important;        
}    
.icon_followers:before {
content: "";
color: #ae00ff !important;    
}    
.icon_activity:before {
content: "";
color: #ae00ff !important;        
}    
.icon_drafts:before {
content: "";
color: #ae00ff !important;            
}    
.icon_queue:before {
content: "";
color: #ae00ff !important;            
}    
.icon_customize:before {
content: "";
color: #ae00ff !important;    
}    
.icon_like:before {
content: "";
color: #ae00ff !important;    
}    
.icon_following:before {
content: "";
color: #ae00ff !important;     
}   
.icon_explore_tumblr:before {
content: "";
color: #ae00ff !important;    
}        
  
.controls_section li .spotlight:before {
content: "" !important;
}    

.icon_arrow_carrot_right:before, .icon_arrow_carrot_down:before {
content: "";
}    
    
.post_controls .post_control:after, .post_controls .post_control.reblog:after, .xkit-one-click-reblog-done:after, .xkit-one-click-reblog-done {
opacity: 0.41;
}
    
/* post icons */    
.icon_post_text:before,
.icon_post_photo:before,
.icon_post_quote:before,
.icon_post_link:before,
.icon_post_chat:before,
.icon_post_audio:before,
.icon_post_video:before {
color: #f1e8fa !important;    
}


    
    
    
}
