css:
@-moz-document 
url-prefix(http://logout.hu/forum),
url-prefix(http://logout.hu/tema),
url-prefix(http://logout.hu/muvelet/hsz),
url-prefix(http://prohardver.hu/forum),
url-prefix(http://prohardver.hu/tema),
url-prefix(http://prohardver.hu/muvelet/hsz),
url-prefix(http://mobilarena.hu/forum),
url-prefix(http://mobilarena.hu/tema),
url-prefix(http://mobilarena.hu/muvelet/hsz),
url-prefix(http://itcafe.hu/forum),
url-prefix(http://itcafe.hu/tema),
url-prefix(http://itcafe.hu/muvelet/hsz),
url-prefix(http://gamepod.hu/forum),
url-prefix(http://gamepod.hu/tema),
url-prefix(http://gamepod.hu/muvelet/hsz)
{

/* Lapcsalád ajánlók */
.adbigtop {
display:none!important;
}

/* Lapcsaládok */
#network, #network span, #network li a {
  color:#DDD!important;
}

/* Nagy fejéc */
#head {
display:none! important;
}

/* Fórum gomb kiemelés */
#navi li.fo.act {
  background:#2D6E7D!important;
}
#navi li.fo.act a {
  color:#fff!important;
}

/* Kereső */
#ssearch {
  margin: 3px 0px 0px 0px !important;
}

/* Oldal háttér */
body {
  background: #000!important;
}
/* Tartalmi rész háttér+keret */
#main  {
  background: #0E0E0E!important;
  border: 1px solid #000!important;
  color:#fff!important;
}

/* Fórum fejléc */
.forum th {
  background:#2d2d2d!important;
  color: #fff!important;
} 

/* Fórum tábla / lista */
.forum td{
    background:#151515!important;
  color: #fff!important;
}
.temak tr.odd td {
  background:#151515!important;
}
.temak tr.evn td {
  background:#191919!important;
}

/* Fórum tábla / lista linkek */
.forum a {
  color:#34B0CD!important;
}
  
/* Oldalsáv fejléc stb. */
.boxs h4 {
  background:#2d2d2d!important;
  color: #fff!important;
  font-weight:bold!important;
  opacity:0.75!important;
}
/* Oldalsáv linkek */
.listdark li a {
  color:#fff!important;
  opacity:0.75!important;
}
/* Oldalsáv: Lefrissebb anyagok doboz */
.listcontent ul {
  background:#0E0E0E!important;
  color: #fff!important;
  opacity:0.75!important;
}
.listcontent ul li a {
  color:#fff!important;
  opacity:0.75!important;
}

/* Oldalsáv lista elem elválasztók */
.listcontent li, .listmenu li {
  background:none!important;
  border-bottom:1px dotted #2d2d2d!important;
}  
  
/* Lefrissebb anyagok cimke */
.listcontent li span {
  background:#2d2d2d!important;
  color:#fff!important;
  font:10px Arial, sans-serif!important;
}
 
/* beléptél, felhasználónév */
#right .usopts .access em {color: #34B0CD!important;}
  
/* Téma tudnivalók */
.thrinfo .msgblk {
  background:#151515!important;
}

/* Doboz és hsz linkek */
.msgblk a {
  color: #34B0CD!important;
}
  
/* Kedvencek lista: e-mail ikon */
span.mod img {
  display:none!important;
}

/* Kedvencek lista: törlés és lista elejére sorolás ikonok */  
span.mod a:nth-child(1), span.mod a:nth-child(2) {
  display: none!important;
}
  
/* Kedvencek lista: új hsz jelző */  
span.mod a.msgs {
 font-weight:bold!important;
 font-size:12px!important;
  background:#34B0CD!important;
  color:#000!important;
}
span.mod a.msgs img {
  display:none!important;
}
a.msgs::before {
  content:"|";
  background:#34B0CD!important;
  color:#34B0CD!important;
}  
a.msgs::after {
  content:"|";
  background:#34B0CD!important;
  color:#34B0CD!important;
}
  
/* Oldal elmentése */
.linkbox {
  background:#2d2d2d!important;
  color:#fff!important;
  border:none!important;
}
.linkbox:before, .linkbox:after {
  display:none!important;
}
.linkbox a {
  color:#fff!important;
}

/* ----- TOPIK NÉZET  ----- */  

/* Fórum navigáció */
#center .fpath a {
  color:#fff!important;
}
#center .patht, #path .patht {
  background:none!important;
  border-bottom: 2px solid #34B0CD;
}
 
/* Lapozó */
#center .fnavi .inr, #center .pnavi h5 {
  background: #2d2d2d!important;
  color:#fff!important;
}
/* Lapozó nyilak */
#center .pnavi .pages .arrow {
  -webkit-filter: hue-rotate(180deg) brightness(150%)!important;
  filter: hue-rotate(180deg) brightness(150%)!important;
}

/* Téma összefoglaló */
.msgblk .isnew .head {
  background:#2D6E7D!important;
  border:1px solid #000!important;
}
.msgblk .isnew .head a {
  color:#fff!important;
}
.msgblk .isnew {
  background: #151515!important;
  border-bottom:4px solid #2D6E7D!important;
}
/* Téma összefoglaló elválasztó border */
.thrcnt a.open {
  background:none!important;
}
/* Hozzászólások fejléc */
.msgblk .head {
  background:#000!important;
  border:1px solid #1a1a1a!important;
}
/* Új, Választ, Privát elválasztó */
.msgblk .head li {
  border-right:1px solid #2d2d2d!important;
}
/* Hozzászólás */
.msgblk .msg {
  background:#151515!important;
}
.face, .msgblk .text {
  color:#fff!important;
}
/* Arckép */
.face img {
  -webkit-filter: brightness(90%)!important;
  filter: brightness(90%)!important;
}
/* Szerkesztés gomb */
p.mod .floatl {
  color:#999!important;
}
/* Hsz aláírás, szerkesztve */
.msgblk .text p.sign, .msgblk .text p.modded {
  color:#999!important;
}
/* Offtopic */
.msgblk .off .head p:before {
  content: 'OFF'!important;
} 
.msgblk .off {
opacity:0.6!important;
color:#999!important;
}

/* Spoiler */
.msgblk .text si {
  -webkit-filter: invert(100%)!important;
  filter: invert(100%)!important;
}
.msgblk .text si:hover  {
  -webkit-filter:invert(0)!important;
  filter:invert(0)!important;
}
.msgblk .text si st {
  background:none!important;
  transition:none!important;
}
  
/* Programkód formázás */
.msgblk .text tt {
  background:#213941!important;
  color: #fff!important;
  padding: 1px 3px!important;
  border-radius:2px!important;

} 

/* ----- HSZ Írás ----- */

/* HSZ írás doboz */
.input h4 {
  background:#151515!important;
  color:#fff!important;
  border:none!important;
}
.input {
  background:#2d2d2d!important;
  border: 1px solid #333!important;
  color:#fff!important;
}
/* Textarea */
.input div.full textarea, .input div.big textarea, .input div.medium textarea, .input div.small textarea, .input div.tiny textarea, .input div.dummy textarea {
  background: #151515!important;
  color:#fff!important;
  border:2px solid #666!important;
}
/* Új kép feltöltése hozzászóláshoz - iframe */
iframe.embed {
  -webkit-filter: invert(100%) saturate(0)!important; 
  filter: invert(100%) saturate(0)!important;  
}
/* Hsz elküldése / OK gomb */
.submit {
  -webkit-filter: hue-rotate(200deg)!important;
  filter: hue-rotate(200deg)!important;
  border:2px solid #6E1212!important;
  
}
  
/* Fórum lista alja: regisztrált tagok, online tagok, legújabb tagunk */
#center .finfo, #center .finfo ul  {
  background:#2d2d2d!important;
  color:#fff!important;
}
#center .finfo ul a {
  color:#fff!important;
}
  
/* Lábléc */
#foot {
  display:none!important;
}
#thanks {
  display: none!important;
}  

  
/* end of style */ 
}
