css:
@-moz-document url-prefix(https://lms.neumont.edu) {

/*----Assignment List-----*/

   div.coursebox {
      background: transparent !important;
      border-color: #999999 !important;
      border-left-color: #666666 !important;
      border-width: 1px 1px 1px 5px !important;
      width: 97% !important;
   }

   div.coursebox div.overview {
      margin-left: 30px;
      font-size: small;
      color: red;
   }

   div.coursebox div.overview div.name {
      display: inline;
      color: black;
   }

   div.coursebox div.overview div.name a {
      font-size: small;
      font-weight: bold;
   }

   div.coursebox div.overview div.info {
      display: inline;
      margin-left: 10px !important;
      color: black;
      font-style: italic;
   }

   div.coursebox div.overview span.early {
      color: green;
   }

   /* Color for assignment types */
   div.coursebox div.assignment {
      
   }

   div.coursebox div.quiz {
      background-color: #CCFFFF;
   }

/*-----------------------*/

/*---------Global---------*/

   body {
      color: black !important;
      background-color: #555555 !important;
   }

   a {
      color: #003366 !important;
   }

   #page {
      border-style: none !important;
      margin-top: 10px;
   }

/*-----------------------*/


/*--------Sidebar--------*/

   div.sideblock {
      border-color: #888888 !important;
      border-style: solid;
      border-width: 0px 1px 1px 1px;
      margin-bottom: 10px !important;
      background-color: transparent !important;
   }

   div.sideblock .content {
      padding: 5px 5px 5px 5px !important;
      background-color: transparent !important;
   }

   div.sideblock .header{
      border-style: none !important;
   }

   div.sideblock h2 {
      color: #FBEB26 !important;
      border-style: none !important;
   }

   .event_course {
      background-color: #FBEB26 !important;
      border-style: none !important;
   }

   .today {
      background-color: #FBEB26 !important;
   }

/*-----------------------*/

/*--------Content--------*/

   td.left {
      display: none !important;
   }

   table.topics td.spacer {
      display: none;
   }

   table.topics td.content .summary {
      font-weight: bold;
      margin-bottom: 5px !important;
      margin-left: 40px !important;
      font-size: .9em;
   }
   
   #content {
      /* background-image: url('http://imagehippo.net/images/zpfrwapupx37yh1htj6.jpg') !important; */
      background-image:url(data:image/jpg;base64,) !important; 
      background-repeat: repeat-x;
      background-color: #FAFAFA !important;
      position: relative;
      top: -3px;
      margin: 0px 0px 0px 0px !important;
   }

   ul.section {
      background: transparent !important;
      border-style: none !important;
      padding-left: 15px !important;
   }

   ul.section li  {
      background: transparent !important;
      border-style: none !important;
      margin-left: 40px;
      font-size: x-small;
   }

   ul.section li img.spacer {
      display: none;
   }

   ul.section .forum  {
      margin-top: 10px;
   }

   ul.section .label{
      font-weight: bold !important;
      margin-left: 20px !important;
   }

/*-----------------------*/

/*--------Header---------*/

   #header_bg {
      height: 45px !important;
      padding-top: 5px;
      background-image: url(data:image/png;base64,) !important;
      background-color: #000000 !important;
   }

   #header {
      height: 40px !important;
      background-image: none !important;
      color: white;
   }

   #header h1 {
      margin-top: 4px !important;
      margin-left: 10px !important;
      height: 40px !important;
      background: url(data:image/png;base64,) no-repeat;
      background-position: 5px 0px;
      padding-top: 4px !important;
      padding-left: 53px !important;
   }

   #banner_bg {
      display: none;
   }

   #navbar_bg {     
      background-image: url(data:image/png;base64,) !important;
   }

   #navbar_bg div.navbar {
      background-image: url(data:image/png;base64,) !important;
   }

   #navbar_bg div.navbar div.breadcrumb {
      color: black !important;
   }

   #navbar_bg div.navbar div.breadcrumb a {
      color: black !important;
   }

/*-----------------------*/

/*--------Footer---------*/

   #footer {
      background-color: #000000 !important;
      background-position: top left !important;
      background-image: url(data:image/png;base64,) !important;
      height: 40px !important;
   }

   #footer .helplink {
      display: none;
   }   

   div.logininfo {
      
      padding-top: 10px;
      color: #CCCCCC;
   }

   div.logininfo a {
      color: #CCCCCC !important;
   }

   div.homelink {
      display: none;
   }

/*-----------------------*/

}
