css:
@-moz-document domain("eddb.io") {
/**
 * Style: eddb.io - Elite Dangerous Darker Orange Edition
 * Style Author: AWMoore (aka Commander Victor Skali)
 * Style URL: https://userstyles.org/styles/113093/eddb-io-elite-dangerous-darker-orange-edition
 * Version: 1.0.4
 * ---------------------------------------
 * Primary Orange:  rgb(255,115,0)
 * Primary Yellow:  rgb(246,175,61)
 * Primary Red:     rgb(91,5,0)
 */

/**
 * Uncomment this import to use Roboto.
 * @import url(http://fonts.googleapis.com/css?family=Roboto:400,700);
 */

 html.site-eddb, body.site-eddb, .wrap {
  background-color: rgb(0,0,0);
  /*background-image:url(http://i.imgur.com/tHET3U9.jpg);*/
  background-image:url(http://i.imgur.com/FDE7ejL.jpg);
  background-size: auto 100%;
  background-attachment: fixed;
}

h1 {
  border-bottom: none;
  margin-bottom: 10px;
  margin-top: 10px;
}

html, body {
  font: 14px/20px 'Roboto', 'Titillium Web', sans-serif;
  color: rgb(255,115,0);
}

hr {
  border-top: 1px solid rgb(255,115,0);
}

a, a:focus {
  color: rgb(236,149,61);
}

.breadcrumb a, .breadcrumb a:focus, .highlight {
  color: rgb(255,115,0);
}

a:hover {
  color: rgb(246,175,61);
  text-decoration: none;
}

button, .btn {
  text-transform: uppercase;
  border-radius: 0;
  font-weight: 600;
}

.btn-primary, .btn-action {
  background-color: rgb(255,115,0);
  border: none;
  color: rgb(255,255,255);
}

.btn-warning {
  border: none;
}

.btn-primary:hover, .btn-action:hover {
  color: rgb(91,5,0);
  background-color: rgb(246,175,61);
}

.control-label {
  text-transform: uppercase;
}

code {
  padding: 1px 4px;
  background-color: rgb(85,85,85);
  border: none;
  color: rgb(204,204,204);
  margin-top: 5px;
}

code.changeLog-diff {
  background-color: rgb(85,85,85);
}
code.changeLog-diff .changeLog-diff-name, code.changeLog-diff .fa {
  color: rgb(226, 161, 128)
}

li {
  margin: 0 0 5px;
}

/* === Page Header === */

.page-header {
  margin: 20px 0 20px;
  border: none;
  text-transform: uppercase;
  padding-bottom: 3px;
}

.page-header h1 i, h1 .prefix, .breadcrumb > .active {
  color: rgb(255,255,255);
}

.page-header h1, .page-header .h1 {
  border: none;
}

/* === Panels === */

.panel {
  background-color: transparent;
  border: none;
}

.panel-eddb>.panel-heading,
.panel-subheading {
  background-color: transparent;
  border-bottom: 2px solid rgb(255,115,0);
  border-top: 2px solid rgb(255,115,0);
  text-transform: uppercase;
  font-size: 15px;
  letter-spacing:1px;
}

.panel-eddb>.panel-heading {
  color: rgb(255,115,0);
  font-weight: bold;
  border-radius: 0px;
  box-shadow: inset 0 8px 8px -8px rgb(255,115,0),
  inset 0 -8px 8px -8px rgb(253,131,31);
  background:url(data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAEAAAAICAYAAAA4GpVBAAAAF0lEQVQIW2NkYGBgYGx3YzBGJ8AScAIAZ2AECeDijiwAAAAASUVORK5CYII=);
}

.panel-body {
  background:url(data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAEAAAAICAYAAAA4GpVBAAAAF0lEQVQIW2NkYGBgYGx3YzBGJ8AScAIAZ2AECeDijiwAAAAASUVORK5CYII=);
  border-bottom: 2px solid rgb(255,115,0);
  box-shadow: inset 0 8px 8px -8px rgb(255,115,0),
  inset 0 -8px 8px -8px rgb(253,131,31);
}

.panel table.panel-table .route-result-item:nth-child(even), .FixedHeader_Header table.panel-table .route-result-item:nth-child(even) {
  background-color: rgba(0,0,0,0.33);
}

.panel table.panel-table .route-result-item:nth-child(even):hover, .FixedHeader_Header table.panel-table .route-result-item:nth-child(even):hover {
  background-color: rgba(246,175,61,0.875);
}

.panel table.panel-table .route-result-item .result-total, .FixedHeader_Header table.panel-table .route-result-item .result-total {
  color: rgb(240,173,78);
}

.panel table.panel-table .route-result-item:hover .result-total, .FixedHeader_Header table.panel-table .route-result-item:hover .result-total {
  color: rgb(91,5,0);
}




/* === Quick Links === */

.quick-links {
  padding-top: 2px;
  border-top: 4px solid rgb(255,115,0);
  border-bottom: 4px solid rgb(255,115,0);
}

.quick-links .quick-box {
  background-color: transparent;
  border: none;
  border-radius: 0;
  margin-top: 3px;
  margin-bottom: 0;
  height: 20px;
  min-width: 160px;
  width: 16%;
}

.quick-links .quick-box > .headline {
  padding: 1px 5px 4px 5px;
  margin-bottom:5px;
  background-color: transparent;
  color: rgb(255,115,0);
  text-transform:uppercase;
  font-size: 16px;
}

.quick-links .quick-box > .headline:hover {
  background-color: rgb(246,175,61);
  color: rgb(91,5,0);
}

.quick-links .quick-box > .body {
  display: none;
}

#commodities-table tr.group {
  color: rgb(255,255,255);
}

/* Table */
.table-bordered {
  background-color: rgba(0,0,0,0.5);
  border: none;
  border-bottom: 2px solid rgb(255,115,0);
  border-top: 2px solid rgb(255,115,0);
  box-shadow: inset 0 8px 8px -8px rgb(255,115,0), inset 0 -8px 8px -8px rgb(253,131,31);
  background-image: url(data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAEAAAAICAYAAAA4GpVBAAAAF0lEQVQIW2NkYGBgYGx3YzBGJ8AScAIAZ2AECeDijiwAAAAASUVORK5CYII=);

}

.table-bordered > thead > tr > th, .table-bordered > tbody > tr > th, .table-bordered > tfoot > tr > th, .table-bordered > thead > tr > td, .table-bordered > tbody > tr > td, .table-bordered > tfoot > tr > td {
  border: none;
}

.table-wrap {
  overflow-x: hidden;
  background-color: rgba(0,0,0,0.5);
  background-image:url(data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAEAAAAICAYAAAA4GpVBAAAAF0lEQVQIW2NkYGBgYGx3YzBGJ8AScAIAZ2AECeDijiwAAAAASUVORK5CYII=);

}

.table .table {
  background-color: rgb(0,0,0);
}

table.dataTable thead th,
.panel table.panel-table .route-result-item td,
.FixedHeader_Header table.panel-table .route-result-item td {
  background-color: transparent;
}

div.FixedHeader_Cloned td {
  background-color: transparent !important;
}

div.FixedHeader_Cloned thead {
  background-color: rgb(0,0,0);
}

div.FixedHeader_Cloned th {
  background-color: transparent !important;
}

th.sorting,
table.dataTable thead th.sorting:after {
  color: rgb(255,115,0);
  text-transform: uppercase;
}

table.dataTable thead th.sorting_asc:after {
  color: rgb(255,255,255);
}
/* Highlight selected column */
table.dataTable thead .sorting_asc,
table.dataTable thead .sorting_desc {
  background-color: rgb(255,126,0) !important;
  color: rgb(255,255,255);
  text-transform: uppercase;
}

table thead th, table.dataTable thead th,
table thead td, table.dataTable thead td {
  border-left: none;
  border-bottom: 2px solid rgb(255,115,0) !important;
}

table.dataTable tbody tr {
  margin-top: 5px;
}

table.dataTable tbody tr,
.table-striped > tbody > tr:nth-of-type(odd) {
  background-color: transparent !important;
}

table.dataTable tbody tr:hover,
.table-striped > tbody > tr:nth-of-type(odd):hover {
  background-color: rgb(246,175,61) !important;
}

#closestListingTable {
  font-size: 13px;
}

.panel-eddb .table>tbody>tr>td {
  border: none;

}

table.dataTable tbody tr td {
  padding-left: 8px;
}

#commodities-table_wrapper table.dataTable tbody tr td:first-child a {
  margin-left: 10%;
}

table.dataTable tbody tr.group {
  text-transform: uppercase;
}

table.dataTable tbody tr.group strong {
  font-weight: normal;
}

.pagination > .disabled > span, .pagination > .disabled > span:hover, .pagination > .disabled > span:focus, .pagination > .disabled > a, .pagination > .disabled > a:hover, .pagination > .disabled > a:focus {
  color: rgb(255,126,0);
  background-color: rgb(0,0,0);
  border-color: rgb(255,126,0);
}

.pagination > .active > a, .pagination > .active > span, .pagination > .active > a:hover, .pagination > .active > span:hover, .pagination > .active > a:focus, .pagination > .active > span:focus {
  color: rgb(255,255,255);
  background-color: rgb(255,126,0);
  border-color: rgb(255,126,0);
}

.pagination > li > a, .pagination > li > span {
  padding: 6px 12px;
  margin-left: -1px;
  color: rgb(255,126,0);
  background-color: rgb(0,0,0);
  border: 1px solid rgb(255,126,0);
}

.pagination > li > a:hover, .pagination > li > span:hover, .pagination > li > a:focus, .pagination > li > span:focus {
  color: rgb(255,255,255);
  background-color: rgb(52,22,0);
  border-color: rgb(255,126,0);
}

.footer {
  background-color: rgb(0,0,0);
  border-top: 1px solid rgb(255,115,0);
}


/** Panel is Loading **/
.panel-loading-drop.panel-is-loading:before {
  opacity: 0.4;
  top: -3px;
  bottom: 0;
  right: 0;
  left: 0;
}

.panel-loading-drop:before {
  content: "";
  opacity: 0;
  z-index: 1000;
  -webkit-transition-delay: 0s;
  transition-delay: 0s;
  -webkit-transition-duration: 100ms;
  transition-duration: 100ms;
  -webkit-transition-property: opacity;
  transition-property: opacity;
  background-color: rgb(255,115,0);
  border-radius: 4px;
}

.filter-group .filter-label {
  background-color: rgb(0,0,0);
  text-transform: uppercase;
}

.filter-group td:first-child:before {
  background: rgb(255,115,0) linear-gradient(to right, rgb(255,255,255) 0%, rgb(255,115,0) 100%);
}

.filter-group .filter-value {
  background-color: rgb(255,115,0);
  color: rgb(91,5,0);
}

.filter-group .filter-value:hover {
  background-color: rgb(246,175,61);
  color: rgb(91,5,0);
}

.filter-group .filter-enabled .filter-value {
  color: rgb(255,255,255);
}

.table tbody tr:hover {
  background-color: rgba(246,175,61,0.875);
  color: rgb(91,5,0);
}

table tbody tr:hover a {
  color: rgb(91,5,0);
}

.table tbody tr.group:hover {
  background-color: transparent;
  color: rgb(255,255,255);
}

.result .location-what {
  padding: 2px 10px;
  border-left: 3px solid rgb(61,39,1);
  border-right: 3px solid rgb(61,39,1);
  border-bottom: 3px solid rgb(61,39,1);
}

.result .result-credits-unit-profit {
  font-weight: 400;
  font-size:14px;
}


.result .location-where, .result .result-total, .result .result-nothing {
  background-color: rgb(61,39,1);
}


.result .location .location-what {
  background-color: rgb(35,35,35);
}

.result .hop-space .hop-space-distance {
  color: rgb(44,15,14);
}

.result .hop-space {
  padding: 8px;
}

.result .result-total {
 font-size: 20px;
}

.form-control, .form-control[disabled], .form-control[readonly], fieldset[disabled] .form-control {
  color: rgb(204,204,204);
  background-color: rgb(35,35,35);
  border: 1px solid rgb(255,115,0);
}

/* -- Select2 Mods -- */

.select2-drop-active {
  border-color: rgb(255,115,0);
}
.select2-drop {
  background: rgb(35,35,35);
  background-color: rgb(35,35,35);
  border: 1px solid rgb(255,115,0);
  color: rgb(255,115,0);
}
.select2-container .select2-choices .select2-search-field input, .select2-container .select2-choice, .select2-container .select2-choices,
.select2-container-active .select2-choices, .select2-container-multi.select2-container-active .select2-choices,
.select2-container-multi.select2-container-active .select2-choices,
.form-control:focus {
  background-color: rgb(35,35,35);
  color: rgb(204,204,204);
  border-color: rgb(255,115,0);
}
.select2-search input, .select2-container.select2-container-disabled .select2-choice, .form-group input {
  border-color: rgb(255,115,0);
  background-color: rgb(35,35,35);
  color: rgb(204,204,204);
}

.select2-container-multi .select2-choices .select2-search-choice {
  color: rgb(0,0,0);
  background: rgb(240,173,78);
  border-color: rgb(240,173,78);
}

.select2-results .select2-highlighted {
  color: rgb(91,5,0);
  background-color: rgb(246,175,61);
}

.select2-results .select2-no-results, .select2-results .select2-searching, .select2-results .select2-selection-limit {
  background-color: transparent;
  color: rgb(255,115,0);
}

.has-error .help-block, .has-error .control-label, .has-error .radio, .has-error .checkbox, .has-error .radio-inline, .has-error .checkbox-inline, .has-error.radio label, .has-error.checkbox label, .has-error.radio-inline label, .has-error.checkbox-inline label {
  color: #FF0500;
}

.has-success .form-control, .has-success .help-block, .has-success .control-label, .has-success .radio, .has-success .checkbox, .has-success .radio-inline, .has-success .checkbox-inline, .has-success.radio label, .has-success.checkbox label, .has-success.radio-inline label, .has-success.checkbox-inline label {
  color: #43D034;
}

.modal-content {
  background-color: rgb(0,0,0);
  border: 1px solid rgb(255,115,0);
}

.modal-header {
  border-bottom: 1px solid rgb(255,115,0);
}

.modal-footer {
  border-top: 1px solid rgb(255,115,0);
}

/* === ROSS === */
.site-ross .table-changelog td, .site-ross .table-condensed td {
  border-top: none;
  border-bottom: none;
}

.site-ross .table-changelog, .site-ross .table-condensed {
  background-color: rgba(0,0,0,0.5);
  background:url(data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAEAAAAICAYAAAA4GpVBAAAAF0lEQVQIW2NkYGBgYGx3YzBGJ8AScAIAZ2AECeDijiwAAAAASUVORK5CYII=);
  border-bottom: 2px solid rgb(255,115,0);
}

.site-ross .table-changelog tbody > tr:nth-of-type(even),
.site-ross .table-condensed tbody > tr:nth-of-type(even) {
  background-color: rgba(0,0,0,0.25);
}

.site-ross .table-changelog tbody > tr:nth-of-type(even):hover,
.site-ross .table-condensed tbody > tr:nth-of-type(even):hover {
  background-color: rgb(246,175,61);
}

.site-ross .table-condensed thead {
  border-bottom: 2px solid rgb(255,115,0);
  border-top: 2px solid rgb(255,115,0);
  box-shadow: inset 0 8px 8px -8px rgb(255,115,0),
  inset 0 -8px 8px -8px rgb(253,131,31);
}

/* Trade Route Table */
.box, .box .box-heading, .box .box-heading-sub, .box .box-body {
    background: transparent;
    border: none;
}

.box .box-heading, .box .box-heading a {
    font-weight: 600;
    color: inherit;
}

.box {
  background:url(data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAEAAAAICAYAAAA4GpVBAAAAF0lEQVQIW2NkYGBgYGx3YzBGJ8AScAIAZ2AECeDijiwAAAAASUVORK5CYII=);
  border-bottom: 2px solid rgb(255,115,0);
  box-shadow: inset 0 8px 8px -8px rgb(255,115,0),
  inset 0 -8px 8px -8px rgb(253,131,31);
}

.box-heading {
  border-bottom: 2px solid rgb(255,115,0) !important;
  border-top: 2px solid rgb(255,115,0) !important;
  box-shadow: inset 0 8px 8px -8px rgb(255,115,0), inset 0 -8px 8px -8px rgb(253,131,31);
  text-transform: uppercase;
  font-size: 15px;
  letter-spacing:1px;
}

.loop-list .loop-details .loop-actions .loop-action-meta, .loops-result .loop-details .loop-actions .loop-action-meta, .single-result .loop-details .loop-actions .loop-action-meta,
.box .box-heading-sub, .box .box-heading-sub a,
.loop-list .loop-details .loop-result-wrapper .loop-result-profit small, .loops-result .loop-details .loop-result-wrapper .loop-result-profit small, .single-result .loop-details .loop-result-wrapper .loop-result-profit small{
    color: rgb(236,149,61);
}

.loop-list .loop-details .loop-result-wrapper .loop-result-profit, .loops-result .loop-details .loop-result-wrapper .loop-result-profit, .single-result .loop-details .loop-result-wrapper .loop-result-profit {
    background-color: #111;
    border-top: 2px solid rgb(255,115,0);
    border-left: 2px solid rgb(255,115,0);
}
.loop-list .loop-details .loop-result-wrapper .loop-result-profit, .loop-list .loop-details .loop-result-wrapper .loop-result-distance, .loops-result .loop-details .loop-result-wrapper .loop-result-profit, .loops-result .loop-details .loop-result-wrapper .loop-result-distance, .single-result .loop-details .loop-result-wrapper .loop-result-profit, .single-result .loop-details .loop-result-wrapper .loop-result-distance {
    padding: 2px 10px 0px 10px;
}
}

@-moz-document domain("ross.eddb.io") {
.eddb-link img, span.eddb-link i.fa-building-o { display: none; }
}
