css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("arbeitsagentur.de") {

/* Style for arbeitsagentur.de , the main German job agency. */
/* Last updated: 15-Oct-2008 */


#head #headBottom {
  background:#E2001A none repeat scroll 0 0!important;
}

body {
  background:transparent none repeat-y scroll -1px 0!important;
}

#naviLeft {
  background-color:#F8E2D2!important;
}

#naviLeft .level1 {
  background-color:#F09B5C!important;
}

#naviLeft .level2 {
  background-color:#F5B789!important;
}

#naviLeft .level3 {
  background-color:#F9CEAF!important;
}

#naviLeft .level4 {
  background-color:#FCE1CE!important;
}

#naviLeft .buerger:hover, #naviLeft .buerger:focus, #naviLeft .buerger:active, #naviLeft .buergerselected {
  background-color:#E2001A!important;
}

#naviLeft .unternehmen:hover, #naviLeft .unternehmen:focus, #naviLeft .unternehmen:active, #naviLeft .unternehmenselected {
  background-color:#006AB3!important;
}

#naviLeft .institutionen:hover, #naviLeft .institutionen:focus, #naviLeft .institutionen:active, #naviLeft .institutionenselected {
  background-color:#A74C21!important;
}

#naviLeft .neutral:hover, #naviLeft .neutral:focus, #naviLeft .neutral:active, #naviLeft .neutralselected {
  background-color:#404040!important;
}


#naviLeft .neu1, #naviLeft .neu1sel, #naviLeft .neu1open, #naviLeft .neu1selopen {
  background-image:none!important;
}

#naviLeft .bue1, #naviLeft .bue1sel, #naviLeft .bue1open, #naviLeft .bue1selopen {
  background-image:none!important;
}

#naviLeft .unt1, #naviLeft .unt1sel, #naviLeft .unt1open, #naviLeft .unt1selopen {
  background-image:none!important;
}

#naviLeft .ins1, #naviLeft .ins1sel, #naviLeft .ins1open, #naviLeft .ins1selopen {
  background-image:none!important;
}

#content h1 {
  background:#EBA979 none repeat scroll 0 0!important;
}

#content .box {
  background-color:#FAECE2!important;
}

#content h2.buerger {
  background-image:none!important;
}

#content h2.unternehmen {
  background-image:none!important;
}

#content h2.institutionen {
  background-image:none!important;
}


#teaser h2.neutral {
  background:#A74C21 none no-repeat scroll 0 0!important;
}

#teaser .box {
  background-color:#FAECE2!important;
}


div.sekundaer {
  border:1px solid #F9E2D1!important;
  background-color:#F9E2D1!important;
}

div.sekmenue {
  border:0px solid #F9E2D1!important;
  background-color:#F9E2D1!important;
}

table.ansicht, table.formular, table.formularHinweis, table.seitenfuss, table.error, table.ergebnisnav, td.ergebnisnavlinks, div.pagination_unten, div.pagination_oben {
  background-color:#FCECE0!important;
  border-color:#FCECE0!important;
}

table.daten, table.datenkopf, table.fmintplan, table.fmintplandruck {
  background-color:#FCECE0!important;
  border-color:#FCECE0!important;
}

.grauersubmit {
  background-color:#FCECE0!important;
}

.grauersubmitlink, a.grauersubmitlink, a.grauersubmitlink:link, a.grauersubmitlink:visited, a.grauersubmitlink:focus, a.grauersubmitlink:hover, a.grauersubmitlink:active {
  background-color:#FCECE0!important;
}

}
