css:
@-moz-document url-prefix('http://www.wattpad.com') {
a, .btn-link, .dropdown-menu, .btn-info, .fa-wp-greyteal, .fa-wp-darkorange, .fa-search, .fa-wp-orange, #profile-dropdown a.on-navigate, .user-event a.on-navigate, .fa-warning, ul.table-of-contents a, .fa-comment:hover {
color:#444!important;
}
#header {
background-image:none!important;
background-color:#333!important;
}
li#discover-dropdown > a, li#community-dropdown > a, a.on-navigate, .username, .caret {
color:#fff!important;
} 
a.tag, .toc-header a, .story-details a.on-navigate, #similar-stories .reads, #similar-stories .votes, #similar-stories .comments, .popover a.on-navigate, .alt-link.on-navigate, #profile-works a.on-navigate, .username.on-navigate, a.on-navigate .story-details, #results-people .username, .parts-list a.on-navigate {
color:#444!important;
}
nav li.active a {
border-bottom-color:#333!important;
}
.info_tabs_selected {
  border-bottom:3px solid #333!important;
}
#creation-works-details ul.tabs>li.active {
border-bottom:2px solid #333!important;
}
.user-event.self, #funbar-story .dropdown-menu>ul li.active a {
border-left:4px solid #333!important;
}
.btn-success, .btn-primary {
background-color:#333!important;
border-color:#333!important;
color:#fff!important;
}
.tag .category, #creation-works-details .tag.category, #creation-works-new .tag.category {
background-color:#444!important;
border-color:#444!important;
}
.btn-primary {
color:#fff!important;
}
.getmobile, .ad-unit-wrapper, .advertisement {
display:none!important;
}
a.bubble_selected, a.bubble:hover, #funbar-reading-progress #progressbar, .tooltip-inner, a.category {
background-color:#444!important;
color:#fff!important;
}
#funbar-reading-progress .tooltip .tooltip-arrow {
border-bottom-color:#444!important;
}
.container img:hover {
box-shadow:1px 1px 3px #999!important;
}
a.on-navigate:hover .title {
text-decoration:underline!important;
color:#555!important;
}
td.message span {
background-color:#eee!important;
}
.news_feed_header .ui-state-active, .ui-button:hover {
background:#444!important;
border:1px solid #444!important;
color:#fff!important;
}
#story-reading #comments>.comments {
margin-left:auto!important;
margin-right:auto!important;
}
#story-reading .page {
-webkit-touch-callout:text!important;
-webkit-user-select:text!important;
-khtml-user-select:text!important;
-moz-user-select:text!important;
-ms-user-select:text!important;
user-select:text!important;
}
}
