css:
/*
Styles for FA
*/
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.furaffinity.net") {
  /* Overall, smaller font */
  * {
    font-size:10pt !important;;
  }
  .site-banner {
    display: none !important;;
  }
  
  /* adblock */
  .ads {
    display:none;
  }

  #footer {
    min-height: 50px;
    max-height: 150px;
  }
 
  .fontsize20 {
    font-size: 16px;
  }
}
