css:
@-moz-document url-prefix(http://coding.pew.im/q/) {
* {
  background: none;
  border: none;
  font-family: 'Roboto', verdana, sans-serif;
}
  body {
  background: #eeeeee !important;
}
  
#name_input {
  margin-top: 20px;
  padding-top: 5px;
  padding-bottom: 5px;
  font-size: 14pt;
  background-color: #eeeeee;
  -webkit-box-shadow: 0px 1px 2px #a0a0a0 ;
  -moz-box-shadow: 0px 1px 2px #a0a0a0 ;
  box-shadow: 0px 1px 2px #a0a0a0 ;
  border-bottom: 1px solid #eeeeee;
  text-align: center;
  width: 231px;
}
  
#join {
  width: auto ;
  margin-left: auto ;
  margin-right: auto ;
  background-color: white;
  -webkit-box-shadow: 0px 1px 2px #a0a0a0 ;
  -moz-box-shadow: 0px 1px 2px #a0a0a0 ;
  box-shadow: 0px 1px 2px #a0a0a0 ;
  display: block;
  border: none;
}
 
#question {
  padding: 5px;
  background-color: white;
  -webkit-box-shadow: 0px 1px 2px #a0a0a0 ;
  -moz-box-shadow: 0px 1px 2px #a0a0a0 ;
  box-shadow: 0px 1px 2px #a0a0a0 ;
  display: block;
  border: none;
  font-weight: normal;
}

.client_name {
  margin-top: 5px;
  padding-top: 5px;
  padding-bottom: 5px;
  color: #eeeeee;
  font-size: 14pt;
  background-color: #008080;
  -webkit-box-shadow: 0px 1px 2px #a0a0a0 ;
  -moz-box-shadow: 0px 1px 2px #a0a0a0 ;
  box-shadow: 0px 1px 2px #a0a0a0 ;
}

.client_status {
  font-size: 11pt;
  padding-top: 5px;
  text-transform: uppercase;
}
.client {
  background-color: white;
  padding: 0px !important;
  -webkit-box-shadow: 0px 1px 2px #a0a0a0 ;
  -moz-box-shadow: 0px 1px 2px #a0a0a0 ;
  box-shadow: 0px 1px 2px #a0a0a0 ;
  display: block;
  border: none;
  margin: 5px;
}
  
.client_answer {
  padding-bottom: 5px;
}

#ready_button, #name_submit {
  position: relative;

  display: block;
  margin: 30px auto;
  padding-bottom: 6px;
  padding-top: 6px;
  padding-left: 20px;
  padding-right: 20px;

  overflow: hidden;

  border-width: 0;
  outline: none;
  border-radius: 2px;
  -webkit-box-shadow: 0px 1px 2px #a0a0a0 ;
  -moz-box-shadow: 0px 1px 2px #a0a0a0 ;
  box-shadow: 0px 1px 2px #a0a0a0 ;
  
  background-color: #008080;
  color: #ecf0f1;
  font-family: 'Roboto', verdana, sans-serif;
  font-size: 18px;
  font-weight: normal;
  text-transform: uppercase;
  
  transition: background-color .3s;
}

#ready_button:hover, #ready_button:focus, #name_submit:hover, #name_submit:focus {
  background-color: #006666;
}

#ready_button, #name_submit > * {
  position: relative;
}
  
#ask_col, #answer_col {
  background-color: white;
  padding: 5px;
  -webkit-box-shadow: 0px 1px 2px #a0a0a0 ;
  -moz-box-shadow: 0px 1px 2px #a0a0a0 ;
  box-shadow: 0px 1px 2px #a0a0a0 ;
  border: none;
  margin: 5px;
}
  
#ask_button, #answer_button {
  position: relative;
  font-size: 14pt;
  text-transform: uppercase;
  color: #006666;
  height: 25;
  margin-top: auto;
  margin-bottom: auto;
}
  
.ta_in {
  border-top: 1px solid #006666;
  border-bottom: 1px solid #006666;
}
}
