@-moz-document domain("soundcloud.com") {
body {
    background-color:#fff;
    transition: all .2s ease-in-out;
}





.profileMenu__list:not(:last-child), .moreMenu__list {
  border-bottom: 1px solid #ddd;
}
.profileMenu__list, .moreMenu__list {
  background-color: #f2f2f2;
}
.sc-list-nostyle {
  list-style: none;
  margin: 0;
}
.profileMenu.g-dark a, .moreMenu.g-dark a {
  color: #555;
}
.profileMenu__link:after {
  -webkit-filter: brightness( 0.4);
}
.header {
    background-color:#f2f2f2 ;
    opacity:0.9;
    transition: all .2s ease-in-out;
    outline-width: 1px;
    outline-color: rgba(0, 0, 0, 0.2);
    outline-style: solid;
}

.header:hover {
	background-color:#f7f7f7;
    opacity:1;
}

.header__navMenu>li>a {
    border-right:none;
    color:#555;
}

.header__navMenu>li>a.selectedÂ {
    color:#555 !important;
    background-color:#eee !important;
}

.header__navMenu>li>a:hover {
    border-right:none;
    color:#222;
}
.header.g-dark a, .g-dark .sc-type-light {
    color:#555;
}
.header.g-dark a, .g-dark .sc-type-light:hover {
    color:#222;
}

.userNav__username {
    max-width: 0px;
}


.g-type-shrinkwrap-block {
    box-shadow: none;
    background: none !important;
    transition: all .2s ease-in-out;
    text-shadow: 0px 2px 6px rgba(0, 0, 0, 1);
}
.g-type-shrinkwrap-inline {
    box-shadow: none;
    background: none !important;
    transition: all .2s ease-in-out;
    text-shadow: 0px 2px 6px rgba(0, 0, 0, 1);
}

inPlaylist  {
    box-shadow: none;
    background: none !important;
    transition: all .2s ease-in-out;
    text-shadow: 0px 2px 6px rgba(0, 0, 0, 1);
}

.g-image-filter-darken-light:after {
    background: linear-gradient(rgba(0,0,0,.00),rgba(0,0,0,.10))
}

class.visuals.streamContext {
    opacity: 0.8;
}

.l-listen-wrapper .l-about-rows {
  padding-right: 30px;
  border-right: none;
}

