.notificationIcon {
    -webkit-filter: brightness(0.4);
}

.header__moreButton {
    -webkit-filter: brightness(0.4);
}

.userNav__usernameButton:after {
    -webkit-filter: brightness(0.4);
}

/* fancy crap */
.featuredProfiles {
    transition: bottom .4s cubic-bezier(0.18, 0.89, 0.32, 1.28);
}

.g-z-index-modal-background {
    background-color: rgba(255,255,255,1);
    transition: opacity .6s ease-in-ease-out
}

.modal__modal, .modal__header {
  transition: -webkit-transform .4s cubic-bezier(.13,1.07,.5,1.01),visibility 0s;
  transition: transform .6s cubic-bezier(0, 1.1, 0, 1);
  visibility: visible;
}

.sc-status-icon {
    background-size: 9px 9px;
    background-repeat: no-repeat;
    transition: all .2s ease-in-out;
}

.sc-status-icon:hover {
    background-size: 12px 12px;
    background-repeat: no-repeat;
    transition: all .2s ease-in-out;
}

.sc-button-responsive, .sc-button-medium, .sc-button-icon, .sc-button-cta {
    border: none;
    background-color: #fff;
    transition: all .2s ease-in-out;
    color: #000;
}



.sc-button-responsive:hover, .sc-button-medium:hover, .sc-button-icon:hover, .sc-button-cta:hover {
    border: none !important;
    background-color: #eee !important;
    color: #000;
}

.sc-button-follow {
    background-color: #555;
    color: #fff;
}

.sc-button-follow:hover {
    color: #333;
}

.sc-button-follow sc-button-selected{
    background-color: #f50;
    color: #fff;
}

.heroPlayButton {
    background-color:rgba(0, 0, 0, 0.0) !important;
    border-color:rgba(0, 0, 0, 0.0) !important;
    transition: all .2s ease-in-out;
}

.heroPlayButton:hover {
    background-color: rgba(0, 0, 0, 1.0) !important;
    border-color: rgba(0, 0, 0, 1.0) !important;
}

.sc-button-play  {
    background-color:rgba(0, 0, 0, 0.4) !important;
    border-color:rgba(0, 0, 0, 0.0) !important;
    transition: all .2s ease-in-out;
}

.sc-button-play :hover {
    background-color: rgba(0, 0, 0, 1.0) !important;
    border-color: rgba(0, 0, 0, 1.0) !important;
}

.sc-border-light-bottom {
    border-bottom: hidden !important;
}

.sc-border-light-right {
	border-right: none !important;
}

/* new layout */

.fullListenHero__waveform {
    right: 30px;
}

.fullListenHero__info {
    right: 30px;
}

.header__navMenu>li>a.selected, .header>li>a:focus {
    background-color: #aaa;
    color: #555;
}

.infoBar__stats.isMine .sc-ministats:last-child {
  margin-right: 6px !important;
  border-right: none !important;
  padding-right: 12px !important;
}

.fullListenHero__artwork {
    width: 1240px;
	height: 400px;
    background-size: 170%;
    top: 0px;
    right: 0px;
    -webkit-filter: blur(40px);
    cursor: default !important;
    z-index: -2;
    transition: all 0.3s ease-in-out;
}

.fullListenHero__artwork:after {
	background-image: linear-gradient(rgba(0,0,0,.00),rgba(0,0,0,.20))
}

.sc-ministats-inverted:before {
    -webkit-filter: drop-shadow(0px 2px 3px rgba(0, 0, 0, 0.5));
    filter:         drop-shadow(0px 2px 3px rgba(0, 0, 0, 0.5)); 
    /*    text-shadow: 0px 2px 6px rgba(0, 0, 0, 1);*/
}

.trackItem {
    transition: all .1s ease-in-out;
}

.compactTrackListItem {
	transition: all .1s ease-in-out;
}

@keyframes badge {
    from {opacity: 0.0}
    to {opacity: 1.0}
}

.userRichBadge:hover {
    transition: badge .3s ease-in-out;
}

.header__userNav, notificationIcon {
    transition: all .1s ease-in-out;
}

.profileMenu.g-dark a, .moreMenu.g-dark a {
    transition: all .1s ease-in-out;
}

.l-sidebar-left a.sc-link-light {
    transition: all .3s cubic-bezier(0, 0, 0.20, 1.29) !important;
}
.l-sidebar-left a.sc-link-light:hover {
    margin-left: 4px !important;
}

.l-listen-wrapper .l-about-top>.listenEngagement {
    border: none;
}

.listenEngagement {
    padding: 0 0 0 0;
    box-shadow: 0 0 0 0;
}

.l-listen-wrapper .l-about-row {
    margin-bottom: 0;
}

h1.header__logo {
	background: rgba(0, 0, 0, 0) !important;
	background-image: none !important;
	-webkit-filter: brightness(0.4) !important;
}

h1.header__logo a:focus, h1.header__logo a:selected {
	background: rgba(0, 0, 0, 0) !important;
	background-image: none !important;
	-webkit-filter: brightness(1.0) !important;
}
}
