css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("kentucky.forums.rivals.com")
{
               


/*********KENTUCKY.FORUMS.RIVALS EDITS*******************/  
/*Blue Themed logo and buttons instead of purple*/
/*Square Avatars on Forum Home and Display*/
/*Removed Sidebar*/
/*Full page forum view*/
/*Larger Font and Font Type*/
/*White Background on quote box & like box */
/*Removed "Funbox"*/
/*Changed Username font and color */

/*********4-28-2015 UPDATE*******************/  
/*Changed the RGB value to match the official UK Blue rgb(0, 93, 170) */


/********************************************************KENTUCKY.FORUMS.RIVALS EDIT********************************************************/    
   
/*Changed Logo Background from purple to UK blue*/  

#logoBlock .pageContent {
    background-color: rgb(0, 93, 170) !important;}

/*Changed Forum Home dropdown category strips to light grey*/ 
.nodeList .categoryStrip {
    background-color: #eaecee!important;
    margin: 30px -0;}

/*Removed round avatar crop*/
.avatar img, .avatar .img, .avatarCropper {
    border-radius: 0 !important;}
 
/*Changed default avatar color from purple to blue*/  
  .avatar img, .avatar .img, .avatarCropper {
    background-color: rgb(0, 93, 170) !important;
    background-position: 0 0 !important;}

/*Resized post started by avatars on RR forum home*/
.discussionListItem .posterAvatar .avatar img {
    display: block !important;
    height: 28px !important;
    width: 28px !important;}
 
/*Resized last poster avatars on RR forum home*/
.discussionListItem .lastPostInfo .avatar img {
   max-height: 100% !important;}

/*Reduced padding on RR forum home thread list*/
.discussionListItem {
    padding: 0 !important;}

/*Reduced padding on RR forum home thread list*/
.discussionListItem .lastPostInfo .avatar img {
   max-height: 75% !important;}

/*Reduced font size of last poster on RR forum home*/
.discussionListItem .lastPostInfo .username {
    font-size: 10px !important;
    line-height: 2px !important;}
    
/*Reduced font size of last post date on RR forum home*/
.discussionListItem .lastPostInfo .dateTime {
    font-size: 10px !important;}

/*Changed font type and size on RR forum display*/
.messageText {
    color: #333333 !important;
    font-family: Tahoma, Geneva, sans-serif !important;
    font-size: 12pt !important;
    line-height: 1.4 !important;}

/*Changed font type and size Read thread message on RR forum display directory*/  
  .discussionListItem .title a {
    color: #333333 !important;
    font-family: Tahoma,Geneva,sans-serif !important;
    font-size: 12pt !important;}
    
/*Changed font type and size UNREAD thread message on RR forum display directory*/  
.discussionListItems .unread .title a {
    color: #333333 !important;
    font-family: Tahoma,Geneva,sans-serif !important;
    font-size: 12pt !important;
    line-height: 1.4;}

/*Make quote box background white on RR forum display*/ 
.quote {
    background-color: rgb(255, 255, 255) !important;}

/*Make quote box background white on RR forum display*/
.bbCodeQuote .quoteContainer {
    background-color: rgb(255, 255, 255) !important;}

/*Change color of users name on RR forum display*/
.messageUserBlock a.username {
   color: #0B4AB0 !important;}

/*Change sub nav menu to white*/ 
.navTabs .navTab.selected .tabLinks {
    background-color: rgb(0, 93, 170) !important;}

/*Remove funbox */ 
.funbox {
    visibility:hidden !important;
		display:none !important;}

/*Changed headers to UK blue */
.heading, .xenForm .formHeader {
    background-color: rgb(0, 93, 170) !important;}

/*Changed section headers to UK light grey */ 
 .sectionHeaders {
    background-color: #eaecee !important;}
    
/*Restyled like box*/ 
.message .likesSummary {
    background-color: #ffffff !important;
    border: 2px solid rgb(188, 195, 202) !important;
    border-radius: 8px !important;
    color: rgb(102, 106, 109) !important;
    margin-top: 2px !important;
    padding: 10px !important;}

/*Restyled like box text and size*/ 
.LikeText a {
    color: #333333 !important;
    font-family: Tahoma,Geneva,sans-serif !important;
    font-size: 10pt !important;
    line-height: 1.4;}
    
/*changed userbar color*/   
#userBar .navTabs {
    background-color:  #f5f8fb !important;}
    
/*changed userbar color*/ 
#userBar .navTabs .navTab.Popup.PopupContainerControl.PopupOpen, #userBar .navTabs .navTab.selected.PopupOpen .navLink {
     background-color:  #f0f0f0 !important;}
    
/*Changed New post status image from purple to blue on Forum Selection page*/
.uix_nodeTitle_status {
    background-color: rgb(0, 93, 170) !important;}
    
/*Changed default avatar image from purple to blue on Forum Selection page*/
.node .forumNodeInfo.unread .nodeIcon {
    background: none repeat scroll 0 0 rgb(0, 93, 170) !important;}
    
/*Changed default New Post status image from purple to blue on Forum display*/
.message .newIndicator {
    background-color: rgb(0, 93, 170) !important;}
    
/*Changed default POST NEW THREAD status image from purple to blue on Forum display*/  
a.callToAction span {
    background-color: rgb(0, 93, 170) !important;}

/*Changed default POST NEW THREAD hover status image from purple to grey on Forum display*/  
a.callToAction:hover span {
    background-color: rgb(75, 77, 79) !important;}
    
/*Changed default POST REPLY image from purple to blue on Forum display*/ 
.button.primary {
    background-color: rgb(0, 93, 170) !important;}
    
/*Changed default POST REPLY image hover on Forum display to light grey*/    
  .button:hover, .button[href]:hover, .buttonProxy:hover .button {
   background-color: rgb(75, 77, 79) !important;}
    
/*Changed Sign UP button from purple to blue*/  
#SignupButton .inner {
    background-color: rgb(0, 93, 170) !important;}
    
/*Changed active span color to blue*/    
a.callToAction:active span {
    background-color: rgb(0, 93, 170) !important;}

/*Changed to blue*/ 
.PageNav a.currentPage {
    background-color: rgb(0, 93, 170) !important;
    border-color: rgb(0, 93, 170) !important;} 

/*Changed top breadcrumb text to black*/  
.breadcrumb .crust:last-child a.crumb {
    color: #000000 !important;
    font-weight: bold;}
    
/*Changed top Last Post Info text to blue*/ 
.discussionListItems .unread .lastPostInfo .username {
    color: rgb(0, 93, 170) !important;
     font-family: Tahoma,Geneva,sans-serif !important;
    font-size: 9pt !important;
    font-weight: normal !important;}

/*Thread started by color*/ 
body .muted, body a.muted, body .muted a {
    color: rgb(0, 93, 170) !important;}
    
/*Thread time started color*/ 
body .faint, body a.faint, body .faint a {
    color: rgb(144, 144, 144)!important;}
    
/*Avatar width adjustment on Forum Home Thread displayr*/ 
.discussionList .posterAvatar {
    width: 30px !important;}
    
/*Make Page Nav Menu visible on Forum Home Thread Display*/ 
.discussionListItem .itemPageNav {
    visibility: visible !important;} 
    
/*Changed Last Post Time color to grey*/ 
.discussionListItem .lastPostInfo .dateTime {
    color: rgb(144, 144, 144)!important;
    font-family: Tahoma,Geneva,sans-serif !important;
    font-size: 9pt !important;}
    
/*Changed Last Post Username font size, style and color*/ 
.discussionListItem .lastPostInfo .username {
    color: rgb(0, 93, 170)!important;
    font-family: Tahoma,Geneva,sans-serif !important;
    font-size: 9pt !important;
    font-size: 10px !important;
    line-height: 2px !important;}
    
/*Changed Like, Report, Quote and Reply buttons from purple to blue*/
.message .messageMeta .control {
    color: rgb(0, 93, 170) !important;}
    
  /*Changed Like, Report, Quote and Reply buttons from purple to blue*/  
.discussionList .sectionHeaders a {
    color: rgb(0, 93, 170) !important;
    font-family: Tahoma,Geneva,sans-serif !important;
    font-size: 11pt !important;
    font-weight: normal !important;} 
    
/*Changed color of Jump to top box from purple to blue*/
#uix_jumpToFixed {
    background-color: rgb(0, 93, 170) !important;}  
    
/*Changed color pf links Forum Selection from purple to blue*/
.node .unread .nodeText .nodeTitle {
    color: rgb(0, 93, 170) !important;} 
    
/*Changed color pf links Forum Selection from purple to blue*/  
.nodeList .categoryStrip {
    color: rgb(0, 93, 170) !important;}  
    
/*Changed USER Submitted links from purple to blue*/ 
.ugc a:link, .ugc a:visited {
    border-radius: 0;
    margin: 0 -3px;
    padding: 0 3px;
    color: rgb(0, 93, 170)!important;}
    
/*Changed PContent from purple to blue*/ 
.primaryContent a {
    color: rgb(0, 93, 170) !important;}
    
/*Changed Top Navigation links from purple to black*/
.navTabs .navTab.PopupClosed .navLink {
    color: #000000 !important;}
    
/*Changed Selected Top Navigation links from purple to black*/
.navTabs .navTab.selected .navLink {
 color: #000000 !important;}
    
/*Change Hover background colors on Userbar*/
.ugc a:hover, .ugc a:focus {
    background: none repeat scroll 0 0 rgba(255, 255, 255, 0.2);
    box-shadow: 0 1px 0 rgb(0, 93, 170) !important;}   
    
.heading, .xenForm .formHeader {
    background-color: rgb(0, 93, 170) !important;}   
       
/*Change Hover background colors on Userbar*/
.Popup .PopupControl:hover, .Popup.PopupContainerControl:hover {
    background: none repeat scroll 0 0 rgb(240, 240, 240) !important;}
    
/*Changed Font color on Forum Selection Dropdown*/   
.nodeList .categoryStrip .nodeTitle a {
    color: #000000 !important;}

/*Hide Sidebar*/ 
.sidebar {
    visibility:hidden !important;
		display:none !important;}

/*Set container width to auto*/    
.mainContainer {
    width:auto !important;}

/*Hide minime to prevent overlapping with avatar*/ 
.discussionListItem .posterAvatar .miniMe img {
   visibility:hidden !important;
		display:none !important;}
    
/*Changed quota box hover color from purple to grey*/     
.message .publicControls .MultiQuoteControl.active {
    background-color: rgb(229, 229, 229)!important;}
    
 .hasFlexbox .messageList .uix_message {
    align-items: stretch !important;
    display: inherit !important;} 

}
